import React, { useState, useEffect, useCallback } from 'react';
import {
  Table,
  Button,
  Input,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from "reactstrap";
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official'
import exporting from 'highcharts/modules/exporting';
import exportData from 'highcharts/modules/export-data';
import accessibility from 'highcharts/modules/accessibility';
import highchartsGantt from "highcharts/modules/gantt";
import { useCookies } from 'react-cookie';
import Widget from '../../components/Widget/Widget';
import axios from 'axios';
import uuid from "uuid/v4";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import e from 'cors';

exporting(Highcharts);
exportData(Highcharts);
accessibility(Highcharts);
highchartsGantt(Highcharts);

const HighChartsPage = (props) => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [data, setData] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [modalMenu, setModalMenu] = useState("");

  // 구분 추가 시 사용
  const [newResource, setNewResource] = useState("");

  // 작업 추가 시 사용
  const [resourceYOfNewSub, setResourceYOfNewSub] = useState("");
  const [newSub, setNewSub] = useState("");
  const [newSubEname, setNewSubEname] = useState("");

  // 이벤트 추가 시 사용
  const [resourceYOfNewEvent, setResourceYOfNewEvent] = useState("");
  const [subYOfNewEvent, setSubYOfNewEvent] = useState("");
  let subName = ""; // 이벤트 추가 시, 구분에 따른 작업을 select 옵션으로 보여줄 건데, 이 때 작업이 중복되지 않기 위해 사용하는 변수
  const [newEventStartDate, setNewEventStartDate] = useState("");
  const [newEventEndDate, setNewEventEndDate] = useState("");


  // 토스트 옵션 (고정값이므로 state로 관리 X)
  const toastOptions =  {
    position: "top-center",
    autoClose: 2000,
    closeOnClick: false,
    pauseOnHover: false,
    draggable: true,
  };

  // 토스트 (알림창)
  const addNotification = (message, type) => {
    let id = uuid();
    if (type === "error") { // 빨간색
      toast.error(<div>{message}</div>, { ...toastOptions, toastId: id });
    } else if (type === "info") { // 초록색
      toast.info(<div>{message}</div>, { ...toastOptions, toastId: id });
    } else if (type === "success") { // 노란색
      toast.warn(<div>{message}</div>, { ...toastOptions, toastId: id,});
    }
  };

  // 전체 데이터 로드
  const parseData = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/wbs/resources?id=172&year=2021`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/wbs/sub`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/wbs/events`),
    ])
    .then(axios.spread((res1, res2, res3) => {
      const rawResources = res1.data;
      const rawSub = res2.data;
      const rawEvents = res3.data;
      const data = [];
      let y_count = 0;

      for (let i_resource = 0; i_resource < rawResources.length; i_resource++) {
        data.push({
          resource: rawResources[i_resource].name,
          pointWidth: 0,
          y: y_count++
        });

        var resourceId = y_count-1;
        const sub = rawSub.filter((item) => rawResources[i_resource].id === item.resource_id);

        for (let i_sub = 0; i_sub < sub.length; i_sub++) {
          const events = rawEvents.filter((item) => sub[i_sub].id === item.sub_id);
          if(events.length === 0) {
            data.push({
              resourceId: resourceId,
              name: sub[i_sub].name,
              담당자: sub[i_sub].담당자,
              color: rawResources[i_resource].color,
              y: y_count,
              // pointWidth: 0
              })
          } else {
            for (let i_event = 0; i_event < events.length; i_event++) {
              data.push({
                resourceId: resourceId,
                name: sub[i_sub].name,
                start: Date.UTC(events[i_event].start.split('-')[0], events[i_event].start.split('-')[1] - 1, events[i_event].start.split('-')[2]),
                end: Date.UTC(events[i_event].end.split('-')[0], events[i_event].end.split('-')[1] - 1, events[i_event].end.split('-')[2]),
                담당자: sub[i_sub].담당자,
                color: rawResources[i_resource].color,
                y: y_count})
            }
          }
          y_count++;
        }
      }
      console.log("데이터", data);
      setData(data);
    }))
    .catch(err => {
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
  }

  const onClickAdd = () => {
    if(modalMenu === "resource") {
      if(!newResource) {
        toast.error(<div>"값을 모두 입력해주세요"</div>, { ...toastOptions});
        // addNotification("값을 모두 입력해주세요", "error");
        return;
      }
      addResource(newResource);
    } else if(modalMenu === "sub"){
      if(!resourceYOfNewSub || !newSub || !newSubEname) {
        addNotification("값을 모두 입력해주세요", "error");
        return;
      }
      addSub(resourceYOfNewSub);
    } else {
      if(!subYOfNewEvent || !newEventStartDate || !newEventEndDate) {
        addNotification("값을 모두 입력해주세요", "error");
        return;
      }
      addEvent(subYOfNewEvent);
    }
    setIsOpen(!isOpen);
  }

  // 구분 추가
  const addResource = (newResource) => {
    data.push({
      resource: newResource,
      pointWidth: 0,
      y: data.length !== 0 ? data[data.length-1].y + 1 : 0
    })
    setData(data);
    setNewResource("");
  }

  // 작업 추가
  const addSub = (resourceYOfNewSub) => {
    let newSubData = {
      resourceId: Number(resourceYOfNewSub),
      name: newSub,
      담당자: newSubEname,
      color: "#000000",
      // pointWidth: 0,
      y: 0
    };

    var resourceIndex = data.findIndex(function(d) { return d.y === Number(resourceYOfNewSub) });
    var nextResourceIndex = data.slice(resourceIndex+1).findIndex(function(d) { return d.resource !== undefined });

    if(nextResourceIndex !== -1) { // 마지막 구분이 아닐 때
      var targetIndex = nextResourceIndex + resourceIndex + 1; //  추가할 곳
      
      newSubData.y = data[targetIndex].y;

      data.splice(targetIndex, 0, newSubData);
      data.slice(targetIndex+1).map(d => {
        d.y += 1;
        if(d.resource === undefined) {
          d.resourceId += 1;
        }
      });
      
    } else { // 마지막 구분일 때
      newSubData.y = data[data.length-1].y+1;
      data.push(newSubData);
    }

    setData(data);
    setResourceYOfNewSub("");
    setNewSub("");
    setNewSubEname("");
  }

  // 이벤트 추가
  const addEvent = (subYOfNewEvent) => {
    var subData = data[data.findIndex(function(d) { return d.y === Number(subYOfNewEvent) })];

    let newEventData = {
      resourceId: subData.resourceId,
      name: subData.name,
      start: Date.UTC(newEventStartDate.split('-')[0], newEventStartDate.split('-')[1] - 1, newEventStartDate.split('-')[2]),
      end: Date.UTC(newEventEndDate.split('-')[0], newEventEndDate.split('-')[1] - 1, newEventEndDate.split('-')[2]),
      담당자: subData.담당자,
      color: subData.color,
      y: subData.y
    };

    var targetIndex = (([...data].reverse().findIndex(function(d) { return d.y === Number(subYOfNewEvent) }) - (data.length-1)) * -1) + 1;
    console.log("타겟 인덱스: ",targetIndex);

    data.splice(targetIndex, 0, newEventData);

    setData(data);
    setResourceYOfNewEvent("");
    setSubYOfNewEvent("");
    setNewEventStartDate("");
    setNewEventEndDate("");

    console.log("데이터: ", data);
  }

  useEffect(() => parseData(), []);

  const options = {
    chart: {
      events: {
        click: function(e) {
          console.log(
              // Highcharts.dateFormat('%Y-%m-%d %H:%M:%S', e.xAxis[0].value), e.yAxis[0].value
              this
          )
        }
      },
      animation: false
    },
    title: {
      text: '프로젝트 명'
    },
    yAxis: {
      labels: {
        indentation: 100,
        align: "center",
        overflow: "allow",
        step: 1,
        style: {
          fontSize: "10px", // 왼쪽 표 글씨 사이즈
          whiteSpace: "break-spaces",
          textOverflow: "ellipsis"
        }
      },
      type: 'category',
      grid: {
        cellHeight: 300,
        columns: [{
          title: {
            text: '구분',
          },
          labels: {
            format: '{point.resource}'
          }
        }, {
          title: {
            text: '작업',
          },
          labels: {
            format: '{point.name}'
          }
        }, {
          title: {
            text: '담당자',
          },
          labels: {
            format: '{point.담당자}'
          }
        }]
      }
    },
    series: [{
      events: {
        click: function(e) {
          console.log(e);
        }
      },
      data: data,
    }],
    tooltip: {
      pointFormat: '<span><strong>{point.name}</strong></span><br/><span>{point.start:%y.%m.%d}~{point.end:%y.%m.%d}</span>'
    },
    navigator: {
      enabled: !isOpen,
      liveRedraw: true,
      series: {
        type: 'gantt',
        pointPlacement: 0.5,
        pointPadding: 0.25
      },
    },
    rangeSelector: { //1개월, 3개월, 6개월, 1년 바꿀수 있음
      enabled: !isOpen,
      inputDateFormat: '%Y-%m-%d',
      buttons: [{
        type: 'month',
        count: 1,
        text: '1m',
        title: '1개월 보기'
      }, {
        type: 'month',
        count: 3,
        text: '3m',
        title: '3개월 보기'
      }, {
        type: 'month',
        count: 6,
        text: '6m',
        title: '6개월 보기'
      }, {
        type: 'ytd',
        text: 'YTD',
        title: '연초부터 현재까지'
      }, {
        type: 'year',
        count: 1,
        text: '1y',
        title: '1년 보기'
      }, {
        type: 'all',
        text: 'All',
        title: '전체보기'
      }]
    },
    
  }

  return (
    <div>
      <Button 
        className="text-capitalize btn btn-warning" 
        onClick={() => {
          setModalMenu("resource");
          setIsOpen(!isOpen);
        }}
      >
        구분 추가
      </Button>
      <Button 
        className="text-capitalize btn btn-warning" 
        onClick={() => {
          setModalMenu("sub");
          setIsOpen(!isOpen);
        }}
      >
        작업 추가
      </Button>
      <Button 
        className="text-capitalize btn btn-warning" 
        onClick={() => {
          setModalMenu("event");
          setIsOpen(!isOpen);
        }}
      >
        이벤트 추가
      </Button>

      <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>WBS</p>}>
          <HighchartsReact
          highcharts={Highcharts}
          constructorType={"ganttChart"}
          options={options}/>
      </Widget>

      <Modal isOpen={isOpen} toggle={() => setIsOpen(!isOpen)} id="news-close-modal" className="modal-content">
        <ModalHeader toggle={() => setIsOpen(!isOpen)} id="news-close-modal-label" className="modal-header">
           추가
        </ModalHeader>
        
        <ModalBody className="bg-white modal-body">
          {modalMenu === "resource" ? // 구분 추가
            <>
              <Input type='text' maxLength='20' style={{width: 100+'%'}} value={newResource} onChange={(e) => setNewResource(e.target.value)} placeholder='추가할 구분을 입력해주세요.'/>
            </>
            : modalMenu === "sub" ? // 작업 추가
              <>
                <strong>구분</strong><br />
                <select className="form-control" id="resourceYOfNewSub" value={resourceYOfNewSub} onChange={(e) => setResourceYOfNewSub(e.target.value)}>
                  <option value="" disabled hidden></option>
                  {data.map((d, idx) =>{
                    if(d.resource !== undefined) {
                      return (<option key={idx} value={Number(d.y)}>{d.resource}</option>);
                    }
                  })}
                </select>
                <br />
                
                <strong>작업</strong><br />
                <Input type='text' maxLength='20' style={{width: 100+'%'}} value={newSub} onChange={(e) => setNewSub(e.target.value)} placeholder='추가할 작업을 입력해주세요.'/>
                <br />
                <strong>담당자</strong><br />
                <Input type='text' maxLength='20' style={{width: 100+'%'}} value={newSubEname} onChange={(e) => setNewSubEname(e.target.value)} placeholder='담당자를 입력해주세요.'/>
              </>
              : // 이벤트 추가
                <>
                  <strong>구분</strong><br />
                  <select className="form-control" id="resourceYOfNewEvent" value={resourceYOfNewEvent} onChange={(e) => setResourceYOfNewEvent(e.target.value)}>
                    <option value="" disabled hidden></option>
                    {data.map((d, idx) =>{
                      if(d.resource !== undefined) {
                        return (<option key={idx} value={Number(d.y)}>{d.resource}</option>);
                      }
                    })}
                  </select>
                  <br />
                  
                  <strong>작업</strong><br />
                  <select className="form-control" id="subYOfNewEvent" value={subYOfNewEvent} onChange={(e) => setSubYOfNewEvent(e.target.value)}>
                    <option value="" disabled hidden></option>
                    {data.map((d, idx) => {
                      if(d.resourceId === Number(resourceYOfNewEvent) && d.name !== subName) {
                        subName = d.name;
                        return (<option key={idx} value={d.y}>{d.name}</option>);
                      }
                    })}
                  </select>
                  <br />

                  <strong>이벤트</strong><br />
                  
                  시작 날짜<Input type='date' maxLength='20' value={newEventStartDate} onChange={(e) => setNewEventStartDate(e.target.value)}/>
                  종료 날짜<Input type='date' maxLength='20' value={newEventEndDate} onChange={(e) => setNewEventEndDate(e.target.value)}/>

                </>
          }
        </ModalBody>

        <ModalFooter className="modal-footer">
          <Button className="text-capitalize btn btn-warning"
            onClick={onClickAdd}
          >
            추가
          </Button>{" "}
          <Button className="text-capitalize btn btn-secondary"
            onClick={() => setIsOpen(!isOpen)}
          >
            취소
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default HighChartsPage;
