import React from "react";
import EquipMenu from "../../components/Equip/EquipMenu";
import {
  Row,
  Col,
  Table,
  Head,
  Body,
  Progress,
  Button,
  UncontrolledButtonDropdown,
  DropdownMenu,
  DropdownToggle,
  DropdownItem,
  Input,
  Label,
  Badge
} from "reactstrap";
import { Sparklines, SparklinesBars } from "react-sparklines";

import Widget from "../../components/Widget/Widget";
import s from "./Equips.modules.scss";
import Axios from 'axios'
import axios from "axios";



class myPage extends React.Component {
  
    constructor(props) {
        super(props);
        
        this.state={
        members: "",
        exPassword: "",
        newPassword: "",
        checkPassword: "",
        };
    }


    findMembers() {
        axios.get(`http://${process.env.REACT_APP_API_URL}/Bteam1`)
        .then(res=>res.data)
         .then((data)=>{
           this.setState({members:data});
     });
   }

   pswdload = (password, num) => {
    axios.post(`http://${process.env.REACT_APP_API_URL}/members/reset`, {password, num})
    .then((response) => {
      console.log(response);
    });
    window.location.reload();
  }

    changePassword = () => {
        if(this.state.exPassword === this.state.members.password){
            if(this.state.newPassword === this.state.checkPassword){
            this.pswdload(this.state.newPassword, this.state.members.num);
        }
        else(alert("새 비밀번호와 비밀번호 확인값이 일치하지 않습니다."));
    }
    else(alert("기존 비밀번호가 일치하지 않습니다."));
}


     render() {
    return (
        <div className={s.root}>

          <Widget title={<p style={{ fontWeight: 700 }}>비밀번호 변경</p>} customDropDown>
            
            <div style={{paddingBottom: 30}}>
                <p>기존 비밀번호</p>
                <input value ={this.setState.expassword}></input>
            </div>

            <div style={{paddingBottom: 30}}>
                <p>새 비밀번호</p>
                <input value = {this.setState.newPassword}></input>
            </div>

            <div style={{paddingBottom: 30}}>
                <p>새 비밀번호 확인</p>
                <input value = {this.setState.checkPassword}></input>
            </div>

            <button onClick={() => this.changePassword()}>적용</button>
            
          </Widget>
        </div>
    );
}
}
export default myPage;
