import React, { useState, useContext } from "react";
import { withRouter, Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Alert, Button, Label } from "reactstrap";
import Widget from "../../components/Widget";
import signinImg from "../../images/signinImg.svg";
import axios from "axios";
import bcrypt from 'bcryptjs';
import { UserContext } from '../../context/user';

const Login = (props) => {
  const [loginInfo, setLoginInfo] = useState({
    id: "",
    password: ""
  });
  const {setUserInfo} = useContext(UserContext);

  const changeId = (event) => {
    setLoginInfo({
      ...loginInfo,
      id: event.target.value
    });
  }

  const changePassword = (event) => {
    setLoginInfo({
      ...loginInfo,
      password: event.target.value
    });
  }

  const doLogin = (e) => {
    e.preventDefault();// 새로고침 안됨

    axios.post(`http://${process.env.REACT_APP_API_URL}/login`, //DB연동
    {loginInfo}).then((response)=>{
      if(response.data.result === 'success'){
        const {id, name} = response.data;
        console.log(response)
        sessionStorage.setItem('Token', response.data.token);
        axios.defaults.headers.common['Authorization'] = response.data.token;
        setUserInfo({id, name});
        localStorage.setItem('authenticated', true);
        props.history.push("/");
      } else {
        alert(response.data.message);
      }
      // if(response.data.length>0){
      //   bcrypt.compare(loginInfo.password, response.data[0].password, (err, res) => {
      //     if(res){
      //       if (response.data[0].authority != 1 && response.data[0].authority != 2) {
      //         alert("접근 권한이 없습니다.");
      //         return;
      //       }
      //       alert('reload user info login');
      //       localStorage.setItem("id", loginInfo.id);
      //       localStorage.setItem('authenticated', true);
      //       setUserInfo({
      //         num: response.data[0].num,
      //         id: response.data[0].id,
      //         division: response.data[0].division,
      //         team: response.data[0].team,
      //         name: response.data[0].name,
      //         ranks: response.data[0].ranks,
      //         position: response.data[0].position,
      //         authority: response.data[0].authority,
      //       });
      //       if(response.data[0].authority == 1){
      //         alert("관리자 로그인 성공!! 오늘 하루도 파이팅 하세요!");
      //       }else{
      //         alert("로그인 성공!! 오늘 하루도 파이팅 하세요!");
      //       }
      //     }else{
      //       alert("비밀번호가 틀렸습니다.");
      //     }
      //   });
      // }else{
      //   alert("아이디가 없습니다.");
      // }
    });
  }
  const { from } = props.location.state || { from: { pathname: '/app' } }; // eslint-disable-line

  // cant access login page while logged in
  if (JSON.parse(localStorage.getItem('authenticated'))) {
    return (
        <Redirect to={from} />
    );
  }

  return (
    
    <div className="auth-page">
      <Widget
        className="widget-auth my-auto"
        title={
          <h3 className="mt-0 mb-2" style={{ fontSize: 40 }}>
            로그인
          </h3>
        }
      >
        <p className="widget-auth-info">
          Welcome Back!
        </p>
        <form className="mt" onSubmit={doLogin}>
          {props.errorMessage && (
            <Alert className="alert-sm" color="danger">
              {props.errorMessage}
            </Alert>
          )}
          <div className="form-group">
            <Label for="search-input1">ID</Label>
            <input
              className="form-control"
              onChange={changeId}
              required
              name="id"
              placeholder="ID를 입력하세요"
            />
          </div>
          <div className="form-group mb-2">
            <Label for="search-input1">비밀번호</Label>
            <input
              className="form-control"
              onChange={changePassword}
              type="password"
              required
              name="password"
              placeholder="비밀번호를 입력하세요"
            />
          </div>
          <Button
            type="submit"
            color="warning"
            className="auth-btn mb-3"
            size="sm"
            // onChange={changeAuthority}
            onClick={(e) => doLogin(e)}
          >
            {props.isFetching ? "Loading..." : "Login"}
          </Button>
          <div className={"d-flex align-items-center"}>
            계정이 없으신가요?{" "}
            <Link to="register" className={"ml-1"}>
              회원가입
            </Link>
          </div>
        </form>
      </Widget>
      <img src={signinImg} alt="signin" className={"backImg"} />
    </div>
  );
}

function mapStateToProps(state) {
  return {
    isFetching: state.auth.isFetching,
    isAuthenticated: state.auth.isAuthenticated,
    errorMessage: state.auth.errorMessage,
  };
}

export default withRouter(connect(mapStateToProps)(Login));
