import moment from "moment";
import React, { useState, useEffect } from "react";
import { Col, Row, Table } from "reactstrap";
import Button from "reactstrap/lib/Button";
import Widget from "../../components/Widget/Widget";
import axios from 'axios';
import { Link } from "react-router-dom";
import './ManagerSchedule.scss';
import { place } from '../newScheduleWeek/NewScheduleWeek';
import { useCookies } from 'react-cookie';

const managers = [
  'ymyou', 'wrsong', 'twinrain', 'ysyoun', 'digitallcs', 'kyu698'
]

const Schedule = () => {
  const [schedule, setSchedule] = useState([]);
  const [today, setToday] = useState(new Date());
  const [days, setDays] = useState([]);
  const [members, setMembers] = useState([]);
  const [cookies, removeCookie] = useCookies(['token']);

  useEffect(() => {
    let days = [];
    const _today = today.getDay();
    for (let i = 1; i < _today; i++) {
      days.push(moment(new Date(new Date(today).setDate(new Date(today).getDate() - _today + i))).format("YYYY-MM-DD"));
    }
    for (let i = 0; i < 6 - _today; i++) {
      days.push(moment(new Date(new Date(today).setDate(new Date(today).getDate() + i))).format("YYYY-MM-DD"));
    }
    setDays(days);
  }, [today]);

  const onClickBefore = () => {
    setToday(new Date(new Date(today).setDate(today.getDate() - 7)));
  };

  const onClickNext = () => {
    setToday(new Date(new Date(today).setDate(today.getDate() + 7)));
  };

  const onClickToday = () => {
    setToday(new Date());
  };

  useEffect(()=> findAllInfos(), []);

  const findAllInfos = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/schedule`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/members`)])
    .then(axios.spread((res1,res2)=>{
      setSchedule(res1.data);
      setMembers(res2.data);
    }))
    .catch(err => {
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
  }

  const selectClassName = (_place) => {
    const colors = ['#EBC680', '#FFCFDA', '#E2F3F4', '#FFDBC1', '#D9E3F0', '#EAC6FA', '#D8F0E5', '#F0E2D8', '#E6D8F0', '#E9E2C5', '#E9C5C5', '#ABC6DE', '#E5C3CA', '#DFDFDF', '#FFFFFF']
    return (colors[place.indexOf(_place)]);
  }

  return (
    <>
      <Widget title={
        <div style={{width: 100+'%', display: 'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
          <p style={{ fontSize:30, fontWeight: 700 }}>관리자 스케줄</p>
          <div>
            <Link to="/app/schedule/add/week">
              <Button color="success">일주일추가</Button>
            </Link>
            <Button color="success" onClick={() => window.location.reload()}>새로고침</Button>
          </div>
        </div>}>
        <div style={{width: 100+'%', textAlign: 'center', fontWeight: 700, fontSize: 20}}>
          {days[0]} ~ {days[4]}
          <div>
            <Button color="info" onClick={onClickBefore}>{'<'}</Button>
            <Button color="primary" onClick={onClickToday}>Today</Button>
            <Button color="info" onClick={onClickNext}>{'>'}</Button>
          </div>
        </div>
        <Table>
          <thead>
            <tr className="manager-schedule__tr" style={{textAlign:'center'}}>
              {days.map((day, index) => (
                <th className="manager-schedule__th" key={index}>{moment(new Date(day)).format("ddd MM/DD")}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            <tr>
              {days.map((day, index) => (
                <td className="manager-schedule__td" key={index} style={{height: 30+'rem', width:20+ '%'}}>
                  <Link to={{pathname: `/app/schedule/add`, state: {date: day}}}>
                    {/* <div style={{height: 100 + '%'}}> */}
                  {schedule
                  .filter(schedule => schedule.날짜 === day)
                  .sort((a, b) => managers.indexOf(a.이름) - managers.indexOf(b.이름))
                  .map((d, i) => {
                    if (d.날짜 === day) {
                      const member = members.find(({id}) => id === d.이름);
                      return (
                        <Link to={{pathname: `schedule/${d.id}`}} key={i}>
                          <div className={"manager-schedule__div "+ d.이름}>
                            <div>{member ? member.name : ""}</div>
                            <div>오전 : <span style={{backgroundColor: selectClassName(d.오전)}}>{d.오전}</span></div>
                            <div>오후 : <span style={{backgroundColor: selectClassName(d.오후)}}>{d.오후}</span></div>
                          </div>
                        </Link>
                      );
                    }
                  }
                  )}
                  {/* </div> */}
                </Link>
                </td>
              ))}
            </tr>
          </tbody>
        </Table>
      </Widget>
    </>
  );
}

export default Schedule;
