import React, { useState, useEffect } from 'react';
import {
    Table,
    Button,
    Input,
} from "reactstrap";
import moment from 'moment';
import axios from "axios";
import { useCookies } from 'react-cookie';

const Team = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [teams, setTeams] = useState([]);
  const [team, setTeam] = useState({
    teamname: '',
    firstorder: '',
    firstsale: '',
    lastorder: '',
    lastsale: '',
    num: '',
    year: moment(new Date()).format('YYYY'),
  });
  const [year, setYear] = useState(moment(new Date()).format('YYYY'));

  const onChangeInput = (e) => {
    setTeam({
      ...team,
      [e.target.id]: e.target.value,
    });
  };

  const onSave = () => {
    if (team.teamname && team.firstorder && team.firstsale && team.lastorder && team.lastsale && team.year) {
      if (team.num) {
        axios
          .post(`http://${process.env.REACT_APP_API_URL}/teams/update`, {
            team: team
          })
          .then((response) => {
            console.log(response);
          })
          .catch(err => {
            if (cookies.token) {
              removeCookie('token');
            }
            alert("세션이 만료되었습니다. 다시 로그인 해주세요");
            window.location.href = "/";
          });
      } else {
        const newTeam = {
          teamname: team.teamname,
          firstorder: team.firstorder,
          firstsale: team.firstsale,
          lastorder: team.lastorder,
          lastsale: team.lastsale,
          num: teams.filter(t => t.year == year).length + 1,
          year: team.year,
        };
        axios
          .post(`http://${process.env.REACT_APP_API_URL}/teams/add`, {
            team: newTeam
          })
          .then((response) => {
            console.log(response);
          })
          .catch(err => {
            if (cookies.token) {
              removeCookie('token');
            }
            alert("세션이 만료되었습니다. 다시 로그인 해주세요");
            window.location.href = "/";
          });
        }
      window.location.reload();
    }
    else {
      alert("모든 항목을 입력해주세요.");
    }
  };

  useEffect(() => findAllInfo(), []);

  const findAllInfo = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios.get(`http://${process.env.REACT_APP_API_URL}/teams`)
    .then((res) => {
      const _teams = res.data.sort((a, b) => a.num - b.num);
      setTeams(res.data);
    })
    .catch(err => {
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
  };

  return (
    <>
      <div className="team">
        <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between', marginBottom: 0.5+'rem'}}>
          <p style={{ fontWeight: 700, fontSize: 1.5+'rem' }}>팀관리</p>
          <select className="form-control" style={{width: 8+'rem'}} id="year" value={year} onChange={(e) => {setYear(e.target.value); setTeam({...team, year: e.target.value})}}>
            {[...Array(moment(new Date()).format('YYYY') - 2018).keys()].map((i) => (
              <option key={i} value={moment(new Date()).format('YYYY') - i + 1}>{moment(new Date()).format('YYYY') - i + 1}</option>
            ))}
          </select>
        </div>
        <Table className="manage__table table-sm">
          <thead>
            <tr>
              <th className="col-md-1">우선순위</th>
              <th className="col-md-3">팀명<br /><br /><Input type="text" id="teamname" value={team.teamname} onChange={onChangeInput}/></th>
              <th className="col-md-2">상반기 목표수주<br /><br /><Input type="text" id="firstorder" value={team.firstorder} onChange={onChangeInput}/></th>
              <th className="col-md-2">상반기 목표매출<br /><br /><Input type="text" id="firstsale" value={team.firstsale} onChange={onChangeInput}/></th>
              <th className="col-md-2">하반기 목표수주<br /><br /><Input type="text" id="lastorder" value={team.lastorder} onChange={onChangeInput}/></th>
              <th className="col-md-2">하반기 목표매출<br /><br /><Input type="text" id="lastsale" value={team.lastsale} onChange={onChangeInput}/></th>
              <th className="col-md-2">Action<br /><br /><Button size="sm" color="info" onClick={onSave}>{team.num ? "변경" : "추가"}</Button></th>
            </tr>
          </thead>
          <tbody>  
            {teams ? teams
            .filter((team) => team.year == year)
            .map((c, index) => {
              return( 
              <tr key={index}>
                <td style={{ textAlign: "center" }}>{c.num}</td> 
                <td style={{ textAlign: "center" }}>{c.teamname}</td> 
                <td style={{ textAlign: "center" }}>{c.firstorder}</td> 
                <td style={{ textAlign: "center" }}>{c.firstsale}</td>
                <td style={{ textAlign: "center" }}>{c.lastorder}</td>
                <td style={{ textAlign: "center" }}>{c.lastsale}</td>
                <td>
                  <Button color={"warning"} className="UpdateBtn" size="sm" variant="primary" onClick={()=>setTeam(c)}>수정</Button>
                  &nbsp;
                  <Button color={"default"} className="UpdateBtn" size="sm" variant="danger"onClick={() => console.log("HI")}>삭제</Button></td>
              </tr> 
              );
            })  :<tr><td colSpan="7">데이터가 없습니다.</td></tr>}
          </tbody>
        </Table>
      </div>
    </>
  );
}


