import React from 'react';
import {
    Row,
    Col,
    Table,
    Progress,
    Button,
    UncontrolledButtonDropdown,
    DropdownMenu,
    DropdownToggle,
    DropdownItem,
    Input,
    Label,
    Badge
  } from "reactstrap";
import { Sparklines, SparklinesBars } from "react-sparklines";

import moment from 'moment';
import Widget from "../../components/Widget";
import axios from "axios";
import { name } from 'file-loader';

class Team extends React.Component{
     
    constructor(props){
      super(props);

      this.state={
        teams:"", 
        num:"",
        teamname:"",
        saveDate:"",
        updateEdit :[],
        onEdit : this.onEdit,
        onDelete : this.onDelete,
        updateValue : this.updateValue,
        onSave : this.onSave,
        year: moment(new Date()).format("YYYY"),
      };
    }

    getRecord = (num) => {
      const product = this.state.teams.find(item => item.num === num);
      return product;
    }

    onEdit = (num) =>{
      const tempProduct = this.state.teams;
      const index  = tempProduct.indexOf(this.getRecord(num));
      const selectedRecord = tempProduct[index];
      this.setState({
        num : selectedRecord['num'],
        teamname : selectedRecord['teamname'],
        firstorder : selectedRecord['firstorder'],
        firstsale : selectedRecord['firstsale'],
        lastorder : selectedRecord['lastorder'],
        lastsale : selectedRecord['lastsale'],
        year : selectedRecord['year'],
      })
    }

    updateValue= (e,test) =>{
      if(test === "teamname"){
        this.state.teamname = e.target.value;
      }
      if(test === "firstorder"){
        this.state.firstorder = e.target.value;
      }
      if(test === "firstsale"){
        this.state.firstsale = e.target.value;
      }
      if(test === "lastorder"){
        this.state.lastorder = e.target.value;
      }
      if(test === "lastsale"){
        this.state.lastsale = e.target.value;
      }
      if(test === "year"){
        this.state.year = e.target.value;
      }
      const tempArr = [this.state.teamname, this.state.firstorder, this.state.firstsale, this.state.lastorder, this.state.lastsale, this.state.year];
      this.setState({
        updateEdit : tempArr
      })
    }

    onSave = (num) =>{
      if(num!==''){ // Update 조건문
        const SaveRecord = this.state.teams;
        const index = SaveRecord.indexOf(this.getRecord(num));
        const Record = SaveRecord[index];
        Record['num'] = this.state.updateEdit[0];
        Record['teamname'] = this.state.updateEdit[1];
        Record['firstorder'] = this.state.updateEdit[2];
        Record['firstsale'] = this.state.updateEdit[3];
        Record['lastorder'] = this.state.updateEdit[4];
        Record['lastsale'] = this.state.updateEdit[5];
        Record['year'] = this.state.updateEdit[6];
        this.setState({
          teams : [...this.state.teams],
          num :"" , teamname:"", firstorder:"", firstsale:"", lastorder:"", lastsale:"", year: "",
        })

        axios.post(`http://${process.env.REACT_APP_API_URL}/teams/update`, //DB연동
        {num: this.state.num, teamname: this.state.teamname, firstorder: this.state.firstorder, firstsale: this.state.firstsale, lastorder: this.state.lastorder, lastsale: this.state.lastsale, year: this.state.year})
        
        .then((response)=>{
        console.log(response);
         });
         window.location.reload();
      }
      // Insert 조건문
      else{ 
        const MaxNum = Math.max(...this.state.teams.map(item=>item.num))
        const num = MaxNum + 1;
        const newArr = [];
        newArr['teamname'] = this.state.updateEdit[0];
        newArr['firstorder'] = this.state.updateEdit[1];
        newArr['firstsale'] = this.state.updateEdit[2];
        newArr['lastorder'] = this.state.updateEdit[3];
        newArr['lastsale'] = this.state.updateEdit[4];
        newArr['year'] = this.state.updateEdit[5];
        this.setState({
          teams : [...this.state.teams, newArr],
          num :"" , teamname:"", firstorder:"", firstsale:"", lastorder:"", lastsale:"", year:"",
        })

        axios.post(`http://${process.env.REACT_APP_API_URL}/teams/add`, //DB연동
        {num: MaxNum+1, teamname: this.state.teamname, firstorder: this.state.firstorder, firstsale: this.state.firstsale, lastorder: this.state.lastorder, lastsale: this.state.lastsale, year: this.state.year})
      
        .then((response)=>{
        console.log(response);
        });

        window.location.reload();
      }
    }

     //DELETE 하기 
     onDelete = (num) => {
      if (window.confirm("정말 삭제하시겠습니까??") == true){    //확인
        const tempProduct = this.state.teams.filter(item=> item.num !== num);
      this.setState({
        members : tempProduct,
        num :"" , id:"", password:"", name:"",
      })
      axios.post(`http://${process.env.REACT_APP_API_URL}/teams/delete`, //DB연동
        {num: num}).then((response)=>{
        console.log(response);
        });
        window.location.reload();
      }else{   //취소
        return;
      }
    }
    componentDidMount(){
      this.findAllTeams();
      
    }

    findAllTeams(){
        axios.get(`http://${process.env.REACT_APP_API_URL}/teams`)
        .then(res=>res.data)
        .then((data)=>{
          this.setState({teams:data});
        });
    }

    render(){
        return(
        <>
          <div className="team">
            <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between', marginBottom: 0.5+'rem'}}>
              <p style={{ fontWeight: 700, fontSize: 1.5+'rem' }}>팀관리</p>
              <select className="form-control" style={{width: 8+'rem'}} id="Year" value={this.state.year} onChange={(e) => {this.state.updateValue(e,"year")}}>
                <option key={2020} value="2020">2020</option>
                <option key={2021} value="2021">2021</option>
                <option key={2022} value="2022">2022</option>
                <option key={2023} value="2023">2023</option>
              </select>
            </div>
            <Table className="manage__table table-sm">
                <thead>
                  <tr>
                    <th>팀명<br /><br /><Input type="text" value={this.state.teamname} onChange={(e) => {this.state.updateValue(e,"teamname")}}/></th>
                    <th>상반기 목표수주<br /><br /><Input type="text" value={this.state.firstorder} onChange={(e) => {this.state.updateValue(e,"firstorder")}}/></th>
                    <th>상반기 목표매출<br /><br /><Input type="text" value={this.state.firstsale} onChange={(e) => {this.state.updateValue(e,"firstsale")}}/></th>
                    <th>하반기 목표수주<br /><br /><Input type="text" value={this.state.lastorder} onChange={(e) => {this.state.updateValue(e,"lastorder")}}/></th>
                    <th>하반기 목표매출<br /><br /><Input type="text" value={this.state.lastsale} onChange={(e) => {this.state.updateValue(e,"lastsale")}}/></th>
                    <th>Action<br /><br /><Button size="sm" onClick={()=>{this.state.onSave(this.state.num)}}>{this.state.num ? "변경" : "추가"}</Button></th>
                  </tr>
                </thead>
                <tbody>  
                  {this.state.teams ? this.state.teams.map(c => {
                    if(c.year == this.state.year){
                      return( 
                      <tr key={c.num}>
                        <td style={{ textAlign: "center" }}>{c.teamname}</td> 
                        <td style={{ textAlign: "center" }}>{c.firstorder}</td> 
                        <td style={{ textAlign: "center" }}>{c.firstsale}</td>
                        <td style={{ textAlign: "center" }}>{c.lastorder}</td>
                        <td style={{ textAlign: "center" }}>{c.lastsale}</td>
                        <td><Button color={"warning"} className="UpdateBtn" size="sm" variant="primary" onClick={()=>{this.state.onEdit(c.num)}}>수정</Button>&nbsp;&nbsp;
                          <Button color={"default"} className="UpdateBtn" size="sm" variant="danger"onClick={()=>{this.state.onDelete(c.num)}}>삭제</Button></td>
                      </tr> 
                      );
                  }})  :<tr><td colSpan="7">데이터가 없습니다.</td></tr>}
                </tbody>
              </Table>
            </div>
          </>
        )
    }
}

export default Team;
