import React, {Component} from 'react';
import {DayPilot, DayPilotScheduler} from "daypilot-pro-react";
import Zoom from "./Zoom";
import axios from 'axios';
import moment from 'moment';

import Flatpickr from "react-flatpickr";
import "flatpickr/dist/themes/confetti.css";

class Scheduler extends Component {

  constructor(props) {
    super(props);

    this.state = {

      start_date: new Date(),
      end_date: new Date(),

      autoScroll: "Always",
      startDate: new DayPilot.Date.today().firstDayOfMonth(),
      days: new DayPilot.Date.today().daysInMonth(),
      scale: "Day",

      timeHeaders: [
        {groupBy: "Month"},
        {groupBy: "Day", format: "d"}
      ],
      cellWidthSpec: "Auto",
      cellWidth: 30,

      durationBarVisible: false, // 이벤트 셀의 윗부분
      treeEnabled: true,

      rowHeaderColumns: [
        {name: "요소", width: 200},
        {name: "담당자", display: "Ename", width: 80},
        {name: "투입 인원", display: "Ecnt", width: 70},
      ],

      updateEdit :[],
      updateValue : this.updateValue,

      resources: [],
      events: [],

      // rowMoveHandling: "Update",
      timeRangeSelectedHandling: "Enabled",
      
      // 메뉴 펼치기 아이콘
      // 현재 안됨
      
      // 그룹 생성
      rowCreateHandling: "Enabled",
      rowCreateText: "  New",

      onRowCreate: args => {
        const row = {
          id: "",
          name: args.text,
          expanded: 1
        }
        
        
        axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/group/add`, //DB연동
        {name: row.name, expanded: row.expanded , project_id:this.props.id}).then((response)=>{
          row.id = "G" + response.data.insertId;

          this.scheduler.rows.add(row);
          this.scheduler.update();
        });
      },

      // 그룹, 리소스 메뉴
      contextMenuResource: new DayPilot.Menu({
        onShow: args => {
          const row = args.source;
          const isParent = row.level === 0;
          args.menu.items[3].disabled = !isParent;
        },

        items: [
          {text: "수정하기", onClick: args => this.edit(args)},
          {text: "삭제하기", onClick: args => this.delete(args)},
          {text: "-"},
          {text: "세부 요소 추가하기", onClick: args => this.add(args)}
        ]
      }),

      // 이벤트 메뉴
      contextMenu: new DayPilot.Menu({
        items: [
          {text: "수정하기", onClick: args => this.editEvent(args)},
          {text: "삭제하기", onClick: args => this.deleteEvent(args)
            // {
            //   const e = args.source;
            //   const params = {
            //     id: e.data.id,
            //   };
            //   // await axios.post("/api/deleteEvent", params);
            //   // this.scheduler.events.remove(e.data.id);
            //   this.scheduler.message("Event deleted: " + e.text());
            // }
          }
        ]
      }),      

    };

  }

  zoomChange(args) {
    switch (args.level) {
      case "year":
        this.setState({
          startDate: new DayPilot.Date.today().firstDayOfYear(),
          days: 365,
          scale: "Day"
        });
        break;
      case "month":
        this.setState({
          startDate: new DayPilot.Date.today().firstDayOfMonth(),
          days: new DayPilot.Date.today().daysInMonth(),
          scale: "Day"
        });
        break;
      case "week":
        this.setState({
          startDate: new DayPilot.Date.today().firstDayOfWeek(),
          days: 7,
          scale: "Day"
        });
        break;
      case "select":
        this.setState({
        startDate: this.state.start_date, 
        days: ( new Date(this.state.end_date).getTime()/( 24*60*60*1000) )- ( new Date(this.state.start_date)).getTime()/( 24*60*60*1000)+1,
        scale: "Day"
        });
        break;
      default:
        throw new Error("Invalid zoom level");
    }
  }

  handleChangeStart(date) {
    this.setState({
      start_date: date[0]
    });
  };

  handleChangeEnd(date) {
    this.setState({
      end_date: date[0]
    });
  };

  cellWidthChange(ev) {
    const checked = ev.target.checked;
    this.setState({
      cellWidthSpec: checked ? "Auto" : "Fixed"
    });
  }

  componentDidMount() {
    this.findAllWBS();
  }

  findAllWBS() {
  
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/wbs/resources/group?id=${this.props.id}`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/wbs/resources/resource`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/wbs/resources/events`)
    ])
    .then(axios.spread((res1,res2,res3)=>{
      const get_resources = [];
      const get_evnets = [];
     
      res1.data.map(function(d) {
        get_resources.push({id: "G"+d.id, name: d.name, expanded: d.expanded});
      });

      get_resources.forEach(r => {
        const get_children = res2.data.filter(c => "G"+c.group_id === r.id);
        get_children.map(function(c) {
          c.parent_id = "G"+c.group_id;
        });
        r.children = get_children;
      });
      
      this.setState({ resources: get_resources });
      
      res3.data.map(function(d) {
        get_evnets.push({id: d.id, text: d.text, start: d.start, end: d.end, resource: d.resource, backColor: d.backColor});
      });
      
      this.setState({ events: get_evnets });
      
      // 각 갯수 할당
      this.setState({ cntChild: res2.data.length });
      this.setState({ cntGroup: res1.data.length });

      }
    ))
    
  }

  async add(args) {

    const parent = args.source;
    const form = [
      {name: "세부 요소", id: "name"},
      {name: "담당자", id: "Ename"},
      {name: "투입 인원", id: "Ecnt"}
    ];
    
    const data = {
      id: 0,
      name: "",
      Ecnt: "",
      Ename: "",
      group_id: Number(parent.data.id.substring(1)),
      parent_id: parent.data.id
    }
  
    const modal = await DayPilot.Modal.form(form, data);
    if (modal.canceled) {
      return;
    }
  
    const row = modal.result;

    row.Ecnt = Number(row.Ecnt);
    // row.group_id = Number(parent.data.id.substring(1));
    // row.parent_id = parent.data.id;
    
    axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/resource/add`, //DB연동
    {name: row.name , Ecnt: row.Ecnt , Ename: row.Ename, group_id: row.group_id}).then((response)=>{
      row.id = response.data.insertId;

      this.scheduler.rows.addChild(parent, row);
      this.scheduler.update();
    });
  
  }
  
  async edit(args) {
    
    const isGroup = !!!args.source.data.group_id;
    
    const form_group = [
      {name: "요소", id: "name"},
    ];
    
    const form_child = [
      {name: "세부 요소", id: "name"},
      {name: "담당자", id: "Ename"},
      {name: "투입 인원", id: "Ecnt"},
    ];
    
    const row = args.source;
    const data = row.data;
    
    const modal = isGroup ? await DayPilot.Modal.form(form_group, data) : await DayPilot.Modal.form(form_child, data) ;
    
    if (modal.canceled) {
      return;
    }
    
    if(isGroup==1){ // parents
      axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/group/update`, //DB연동
      {name: modal.result.name , id:Number(row.id.substring(1))});
      const col = this.scheduler.rows.find(data.id);
      col.data.name = modal.result.name;
      this.scheduler.update();
    }
    else if(isGroup==0){ //children
      axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/resource/update`, //DB연동
      {name: modal.result.name , Ecnt: modal.result.Ecnt, Ename: modal.result.Ename, group_id: modal.result.group_id, id:row.id});
      const col = this.scheduler.rows.find(data.id);
      col.data.name = modal.result.name;
      col.data.Ename = modal.result.Ename;
      col.data.Ecnt = Number(modal.result.Ecnt);
      this.scheduler.update();
    }

  }
  
  async delete(args) {
    const isGroup = !!!args.source.data.group_id;
    const row = args.source;
    const modal = await DayPilot.Modal.confirm(`정말로 '${row.name}' 를 삭제하시겠습니까?`);
    if (modal.canceled) {
      return;
    }

  
    if (isGroup==1) { // parents
      axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/group/delete`, {id: Number(row.data.id.substring(1))});
    }
    else if (isGroup==0) { //children
      axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/resource/delete`, {id: row.data.id});
    }

    this.scheduler.rows.remove(row);
    this.scheduler.update();

  }

  async editEvent(args) {

    const color = [
      {name: "빨강색", id: "#ff0000"},
      {name: "주황색", id: "#ff7f00"},
      {name: "노랑색", id: "#ffd400"},
      {name: "초록색", id: "#009900"},
      {name: "파랑색", id: "#0067a3"},
      {name: "남색색", id: "#000080"},
      {name: "보라색", id: "#8b00ff"}
    ]

    const form = [
      { name: "내용", id: "text"},
      { name: "색상", id: "backColor", options: color},
    ];
    
    const row = args.source;
    const data = row.data;
    
    const modal = await DayPilot.Modal.form(form, data);

    if (modal.canceled) {
      return;
    }

    axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/event/update`,
    {id: modal.result.id, text: modal.result.text, backColor: modal.result.backColor});
    const col = this.scheduler.events.find(data.id);
    col.data.text = modal.result.text;
    col.data.backColor = modal.result.backColor;
    this.scheduler.update();
  }

  async deleteEvent(args) {
    const row = args.source;
    const modal = await DayPilot.Modal.confirm(`정말로 '${row.data.text}' 를 삭제하시겠습니까?`);
    if (modal.canceled) {
      return;
    }

    axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/event/delete`, {id: row.data.id});

    this.scheduler.events.remove(row);
    this.scheduler.update();

  }

  render() {
    const { ...config } = this.state;
    const { start_date } = this.state;
    const { end_date } = this.state;

    return (

      <div>

        <div className="toolbar">

          <Zoom onChange={args => this.zoomChange(args)}/>

          <span className="toolbar-item">

            <Flatpickr
              value={start_date}
              options={
                {dateFormat: "Y-m-d"}
              }
              onChange={start_date => {
                this.handleChangeStart(start_date);
                this.state.start_date = moment(this.state.start_date).format("YYYY-MM-DD");
              }}
            />

            <Flatpickr
              value={end_date}
              options={
                {dateFormat: "Y-m-d"}
              }
              onChange={end_date => {
                this.handleChangeEnd(end_date);
                this.state.end_date = moment(this.state.end_date).format("YYYY-MM-DD");
              }}
            />

            <label>
              <input type="checkbox" checked={this.state.cellWidthSpec === "Auto"} 
                onChange={ev => this.cellWidthChange(ev)}/> Auto width
            </label>

          </span>

        </div>

        <DayPilotScheduler
          {...config}

          onEventMoved = {args => {

            if (Number.isInteger(args.newResource)==0) {
              alert("부모 값으로 Event 를 옮길 수 없습니다");

              args.e.data.start = args.e.cache.start;
              args.e.data.end = args.e.cache.end;
              args.e.data.resource = args.e.cache.resource;
              
              this.scheduler.update();

              return;
            }
          
            this.scheduler.message("Event moved: " + args.e.data.text);
            
            axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/event/move`, //DB연동
            {start: moment(args.newStart.value).format("YYYY-MM-DD"), end: moment(args.newEnd.value).format("YYYY-MM-DD"), resource: args.newResource, id: args.e.data.id})
          }}

          onEventResized = {args => {
            this.scheduler.message("Event resized: " + args.e.data.text);

            axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/event/resize`, //DB연동
              {start: moment(args.newStart.value).format("YYYY-MM-DD"), end: moment(args.newEnd.value).format("YYYY-MM-DD"), id: args.e.data.id})
          }}

          onTimeRangeSelected = {args => {
            if (Number.isInteger(args.resource) == 0) {
              alert("부모값으로 Event를 생성할 수 없습니다");
              this.scheduler.clearSelection();
              return;
            }

            DayPilot.Modal.prompt("새로운 일정 등록", "").then(modal => {
              this.scheduler.clearSelection();
              if (modal.canceled) {
                return;
              }

              const row = {
                id: 0,
                start: moment(args.start.value).format("YYYY-MM-DD"),
                end: moment(args.end.value).format("YYYY-MM-DD"),
                text: modal.result,
                resource: args.resource,
                backColor: "#ff7f00"
              };


              axios.post(`http://${process.env.REACT_APP_API_URL}/wbs/resources/event/add`, //DB연동
              {start: row.start, end: row.end, text: row.text, resource: row.resource, backColor: row.backColor}).then((response)=>{
                row.id = response.data.insertId;
              });

              this.scheduler.events.add(row);
              this.scheduler.update();

            });
            
          }}
          
          onBeforeEventRender = {args => {
            if (!args.data.backColor) {
              args.data.backColor = "#ff7f00"; // 이벤트 셀 디폴트 컬러
            }
            args.data.borderColor = "darker";
            args.data.fontColor = "white";
          }}

          ref={component => {
            this.scheduler = component && component.control;
          }}

        />
      </div>
    );
  }
}

export default Scheduler;
