import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
// import { formatDate } from "@fullcalendar/common";
import Widget from "../../components/Widget/Widget";
import interactionPlugin from '@fullcalendar/interaction';
import timeGridPlugin from '@fullcalendar/timegrid';
import {
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap";
import moment from 'moment';

const Reservation = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [modalMenu, setModalMenu] = useState("");
  const [modalData, setModalData] = useState({
    id: "",
    title: "",
    start: "",
    end: "",
  });

  return (
    <>
      <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>프로젝트룸</p>}>
        <FullCalendar plugins={[ dayGridPlugin, timeGridPlugin, interactionPlugin ]} initialView="timeGridWeek"
          timeZone="Asia/Seoul"
          locale="ko"
          nowIndicator={true}
          weekends={false}
          headerToolbar= {{
            left: 'prev,next today',
            center: 'title',
            right: 'dayGridMonth,timeGridWeek,timeGridDay'
          }}
          slotMinTime="06:00:00"
          slotMaxTime="22:00:00"
          events={[
            { title: '프로젝트1', start: '2021-12-01T15:30', end: '2021-12-01T18:30', id: '19' },
            { title: '프로젝트2', start: '2021-12-02T15:30', end: '2021-12-02T18:30', id: '20' },
            { title: '프로젝트3', start: '2021-12-03T15:30', end: '2021-12-03T18:30', id: '30' },
          ]}
          allDaySlot={false}
          eventClick={(info) => {
            console.log(info.event._instance.range.start.toISOString().slice(0,-1));
            setModalMenu("updateEvent");
            setModalData({
              id: info.event._def.publicId,
              title:info.event._def.title,
              start: info.event._instance.range.start.toISOString().slice(0,-1),
              end: info.event._instance.range.end.toISOString().slice(0,-1),
            });
            setIsOpen(true);
          }}
          selectable={true}
          selectMirror={true}
          selectOverlap={false}
          select={(selectInfo) => {
            console.log(selectInfo);
            setModalMenu("createEvent");
            setModalData({
              title: "",
              start: selectInfo.startStr,
              end: selectInfo.endStr,
            });
            setIsOpen(true);
          }}
          scrollTime="08:00:00"
        />
      </Widget>
      <Modal isOpen={isOpen} toggle={() => setIsOpen(!isOpen)}>
        <ModalHeader toggle={() => setIsOpen(!isOpen)}>예약정보</ModalHeader>
        <ModalBody>
          {modalMenu === "updateEvent" && (
            <>
              <p>예약수정</p>
              <p>예약자 : {modalData.title}</p>
              <p>시작시간 : {moment(new Date(modalData.start)).format("YYYY-MM-DD HH:mm")}</p>
              <p>종료시간 : {moment(new Date(modalData.end)).format("YYYY-MM-DD HH:mm")}</p>
              <p>ID : {modalData.id}</p>
            </>
          )}
          {modalMenu === "createEvent" && (
            <>
              <h3>예약하기</h3>
              <p>예약자 : {modalData.title}</p>
              <p>시작시간 : {moment(new Date(modalData.start)).format("YYYY-MM-DD HH:mm")}</p>
              <p>종료시간 : {moment(new Date(modalData.end)).format("YYYY-MM-DD HH:mm")}</p>
            </>
          )}
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary" onClick={() => setIsOpen(!isOpen)}>확인</button>
        </ModalFooter>
      </Modal>
    </>
  );
};

export default Reservation;
