import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Row, Table } from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import { Link } from 'react-router-dom';
import './ProjectList.scss';
import Button from 'reactstrap/lib/Button';
import moment from 'moment';

// 구글 시트 동기화
const {GoogleSpreadsheet} = require("google-spreadsheet");
const gs_creds = require("./vom-shchoi-1f999190023a.json"); // 키 생성 후 다운된 json파일을 지정합니다.
const doc = new GoogleSpreadsheet("12Y0F34Kt6MXPIjy_phTwgXO1_uD5ZqDU_UzucNBdSNI");

async function authGoogleSheet(){
	try{
		await doc.useServiceAccountAuth(gs_creds);
		await doc.loadInfo()
	}catch(err){
		console.log( "AUTH ERROR ", err)
	}
}

// id, 팀수주, 팀매출, 프로젝트코드, 프로젝트명, 상태, 실, 고객사, 고객부서, ManMonth, 프로젝트계약금액_백만, 상반기예상수주, 상반기수주, 상반기예상매출, 상반기매출, 하반기예상수주, 하반기수주, 하반기예상매출, 하반기매출, 장비비, 컨설팅비, 도구비, 착수, 종료, 고객담당자, 근무지, 업무, 도구, 과제성격, 사업지역, PM, 투입명단, year, 주간보고서, 실적보고
async function readFirstSheetRow(data){
  authGoogleSheet(); // 처음 시작할 때 문서 접속에 대한 인증을 처리하고 해당 문서를 로드합니다.
  try{
    const date = moment(new Date()).format('YYYY-MM-DD');
    await doc.loadInfo();
    const sheet = doc.sheetsByTitle[date];
    if (sheet){
      await sheet.delete();
    }
    const new_sheet = await doc.addSheet({title: date});
    await new_sheet.resize({ rowCount: 50, columnCount: 35 });
    await new_sheet.setHeaderRow(['id', '팀수주', '팀매출', '프로젝트코드', '프로젝트명', '상태', '실', '고객사', '고객부서', 'ManMonth', '프로젝트계약금액_백만', '상반기예상수주', '상반기수주', '상반기예상매출', '상반기매출', '하반기예상수주', '하반기수주', '하반기예상매출', '하반기매출', '장비비', '컨설팅비', '도구비', '착수', '종료', '고객담당자', '근무지', '업무', '도구', '과제성격', '사업지역', 'PM', '투입명단', 'year', '주간보고서', '실적보고']);
    await new_sheet.addRows(data);
  }catch(err){
    console.log( "READ ERROR ", err)
  }
}

const ProjectList = () => {
  const [projects , setProjects] = useState([]);
  const [members , setMembers] = useState([]);
  const [year, setYear] = useState(moment(new Date()).format('YYYY'));
  const [term, setTerm] = useState(true); //상반기 == true, 하반기 == false

  const sortList = (e) => {
    let arrayCopy = [...projects];
    switch (e.target.name) {
      case "ascSu":
        arrayCopy.sort((a, b) => a.팀수주.localeCompare(b.팀수주));
        break;
      case "descSu":
        arrayCopy.sort((a, b) => b.팀수주.localeCompare(a.팀수주));
        break;
      case "ascMa":
        arrayCopy.sort((a, b) => a.팀매출.localeCompare(b.팀매출));
        break;
      case "descMa":
        arrayCopy.sort((a, b) => b.팀매출.localeCompare(a.팀매출));
        break;
      case "ascName":
        arrayCopy.sort((a, b) => a.프로젝트명.localeCompare(b.프로젝트명));
        break;
      case "descName":
        arrayCopy.sort((a, b) => b.프로젝트명.localeCompare(a.프로젝트명));
        break;
      case "ascStatus":
        arrayCopy.sort((a, b) => a.상태.localeCompare(b.상태));
        break;
      case "descStatus":
        arrayCopy.sort((a, b) => b.상태.localeCompare(a.상태));
        break;
      default:
        return;
    }
    setProjects(arrayCopy);
  };

  useEffect(()=> findAllProjects(), []);

  const findAllProjects = () => {
    axios
    .all([
    axios.get(`http://${process.env.REACT_APP_API_URL}/projects`),
    axios.get(`http://${process.env.REACT_APP_API_URL}/members`)])
    .then(axios.spread((res1,res2)=>{
      setProjects(res1.data);
      setMembers(res2.data);
    }))
 }

  return (
    <>
      <Row>
        <Col>
          <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>프로젝트목록</p>}>
            <div style={{display: 'flex', flexDirection:'row', justifyContent: 'space-between', marginBottom: 1+'rem'}}>
              <div style={{display: 'flex', flexDirection:'row'}}>
                <select className="form-control" style={{width: 10+'rem'}} name="year" value={year} onChange={(e)=>setYear(e.target.value)}>
                  {[...Array(moment(new Date()).format('YYYY') - 2019).keys()]
                  .map((i)=>{
                    return <option key={i} value={moment(new Date()).format('YYYY') - i}>{moment(new Date()).format('YYYY') - i}</option>
                  })}
                </select>
                <Button color="primary" style={{marginLeft: 1+'rem'}} onClick={()=>{
                  setTerm(!term);
                }}>{term ? '상반기' : '하반기'}</Button>
              </div>
              <div>
                <Button color="warning" onClick={() => readFirstSheetRow(projects)}>스프레드시트 동기화</Button>
                <Link to="/app/projects/new"><button className="btn btn-primary">새로운 프로젝트</button></Link>
              </div>
            </div>
            <Table className="project-list__table table-sm">
              <thead>
                <tr>
                  <th className="col-md-1 project-list__th">
                    팀(수주)
                    <button className="orderBtn glyphicon glyphicon-collapse" name="ascSu" onClick={sortList}/>
                    <button className="orderBtn glyphicon glyphicon-collapse-top" name="descSu" onClick={sortList}/>
                  </th>
                  <th className="col-md-1 project-list__th">
                    팀(매출)
                    <button className="orderBtn glyphicon glyphicon-collapse" name="ascMa" onClick={sortList}/>
                    <button className="orderBtn glyphicon glyphicon-collapse-top" name="descMa" onClick={sortList}/>
                  </th>
                  <th className="col-md-3 project-list__th">
                    프로젝트 명
                    <button className="orderBtn glyphicon glyphicon-collapse" name="ascName" onClick={sortList}/>
                    <button className="orderBtn glyphicon glyphicon-collapse-top" name="descName" onClick={sortList}/>
                  </th>
                  <th className="col-md-1 project-list__th">
                    상태
                    <button className="orderBtn glyphicon glyphicon-collapse" name="ascStatus" onClick={sortList}/>
                    <button className="orderBtn glyphicon glyphicon-collapse-top" name="descStatus" onClick={sortList}/>
                  </th>
                  {term ?
                  <>
                    <th className="col-md-1 project-list__th">상반기 예상수주</th>
                    <th className="col-md-1 project-list__th">상반기 수주</th>
                    <th className="col-md-1 project-list__th">상반기 예상매출</th>
                    <th className="col-md-1 project-list__th">상반기 매출</th>
                  </>
                  :
                  <>
                    <th className="col-md-1 project-list__th">하반기 예상수주</th>
                    <th className="col-md-1 project-list__th">하반기 수주</th>
                    <th className="col-md-1 project-list__th">하반기 예상매출</th>
                    <th className="col-md-1 project-list__th">하반기 매출</th>
                  </>
                  }
                  <th className="col-md-1 project-list__th">PM</th>
                </tr>
              </thead>
              <tbody>
                {projects
                .filter((project)=> project.year == year)
                .map((project, index) => {
                  const member = members.find(({id}) => id === project.PM);
                  return(
                  <tr key={index}>
                    <td className="col-md-1 project-list__td">{project.팀수주}</td>
                    <td className="col-md-1 project-list__td">{project.팀매출}</td>
                    <td className="col-md-3 project-list__td" style={{whiteSpace: 'break-spaces'}}>
                      <Link to={{pathname: `/app/projects/detail/${project.id}/${year}`}}>
                        {project.프로젝트명}
                      </Link>
                    </td>
                    <td className="col-md-1 project-list__td">{project.상태}</td>
                    {term ?
                    <>
                      <td className="col-md-1 project-list__td project-list__td_cost">{project.상반기예상수주}</td>
                      <td className="col-md-1 project-list__td project-list__td_cost">{project.상반기수주}</td>
                      <td className="col-md-1 project-list__td project-list__td_cost">{project.상반기예상매출}</td>
                      <td className="col-md-1 project-list__td project-list__td_cost">{project.상반기매출}</td>
                    </>
                    :
                    <>
                      <td className="col-md-1 project-list__td project-list__td_cost">{project.하반기예상수주}</td>
                      <td className="col-md-1 project-list__td project-list__td_cost">{project.하반기수주}</td>
                      <td className="col-md-1 project-list__td project-list__td_cost">{project.하반기예상매출}</td>
                      <td className="col-md-1 project-list__td project-list__td_cost">{project.하반기매출}</td>
                    </>
                    }
                    <td className="col-md-1 project-list__td">{member ? member.name : ""}</td>
                  </tr>
                )})}
              </tbody>
            </Table>
          </Widget>
        </Col>
      </Row>
    </>
  );
}

export default ProjectList;
