import React from "react";
import { Row, Col } from "reactstrap";
import Form from "reactstrap/lib/Form";
import Widget from "../../components/Widget/Widget";
import styles from "./organization.module.scss";
import { useState, useEffect } from 'react';
import axios from "axios";
import PropTypes from "prop-types";


class organizationCar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Cteam1: "", //select where 샤시힐스검증팀
      Cteam2: "", //select where 제어로직검증팀 
      Cteam3: "", //select where 기능안전검증팀
      Cteam4: "", //select where 자율주행검증팀
      Cteam5: "", //select where UAM로보틱스개발팀
      Cteam6: "",
      visible: false,
      checkedItems: new Set(),
     };
   }
  checkedItemHandler = (id, isChecked) => {
    if (isChecked) {
      const newChecked = this.state.checkedItems.add(id);
      this.setState({checkedItems: newChecked});
    } else if (!isChecked && this.state.checkedItems.has(id)) {
      const newChecked = this.state.checkedItems.delete(id);
      this.setState({checkedItems: newChecked});
    }
    console.log(this.state.checkedItems);
  };
 
  onClickButton = () => {
    if (this.state.visible) {
      const newCteam6 = this.state.Cteam6.filter((item, index) => !this.state.checkedItems.has(index));
      this.setState({Cteam6: newCteam6});
      this.onDelete();
    }
    this.setState({visible: !this.state.visible});
  }

  componentDidMount(){
    this.setState({ status: true });

    this.findHills();
    this.findLogic();
    this.findSafe();
    this.findCar();
    this.findUAM();
    this.findIntern();
  }

  componentWillUnmount() {
    this.setState({ status: false });
  }

  findHills() {
    axios.get(`http://${process.env.REACT_APP_API_URL}/Cteam1`)
    .then(res=>res.data)
    .then((data)=>{
      this.setState({Cteam1:data});
    });
  }

  findLogic() {
    axios.get(`http://${process.env.REACT_APP_API_URL}/Cteam2`)
    .then(res=>res.data)
    .then((data)=>{
      this.setState({Cteam2:data});
   });
  }

  findSafe() {
    axios.get(`http://${process.env.REACT_APP_API_URL}/Cteam3`)
    .then(res=>res.data)
    .then((data)=>{
      this.setState({Cteam3:data});
    });
  }

  findCar() {
    axios.get(`http://${process.env.REACT_APP_API_URL}/Cteam4`)
    .then(res=>res.data)
    .then((data)=>{
      this.setState({Cteam4:data});
    });
  }

  findUAM() {
    axios.get(`http://${process.env.REACT_APP_API_URL}/Cteam5`)
    .then(res=>res.data)
    .then((data)=>{
      this.setState({Cteam5:data});
    });
  }

findIntern() {
    axios.get(`http://${process.env.REACT_APP_API_URL}/Cteam6`)
    .then(res=>res.data)
    .then((data)=>{
      this.setState({Cteam6:data});
    });
  }

  onDelete = () => {
    if (window.confirm("정말 삭제하시겠습니까??") == true){    //확인
      // const postDelete = this.state.Cteam6.filter((item, index) => this.state.checkedItems.has(index));
      this.state.checkedItems.map((item) => {
        // let 이메일 = item.이메일;
        console.log(item);
        axios.post(`http://${process.env.REACT_APP_API_URL}/posts/delete`, //DB연동
          {이메일 : item}).then((response)=>{
          console.log(response);
        });
      })
      window.location.reload();
    }else{   //취소
      return;
    }
  }

   
    
    render() {
      const { isOpen, close } = this.props;
      return (
        <div className="pricingTable">
        <h2 className="pricingTable-title">미래차검증전략실</h2>
        <h3 className="pricingTable-subtitle">실장 유영민</h3>
        
        <ul className="pricingTable-firstTable">
          <li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">샤시힐스검증팀</h1>
            <p className="pricingTable-firstTable_table__pricing"><span>송우람</span><span>팀장</span></p>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Cteam1 ? this.state.Cteam1.map(c => {
              if(c.직위 === '책임' || c.직위 === '수석')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam1 ? this.state.Cteam1.map(c => {
              if(c.직위 === '선임')
                return(
                <li className="li_second">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam1 ? this.state.Cteam1.map(c => {
              if(c.직위 === '전임')
                return(
                <li className="li_third">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              
               </ul>
          </li><li className="pricingTable-firstTable_table">
          <h1 className="pricingTable-firstTable_table__header">제어로직검증팀</h1>
            <p className="pricingTable-firstTable_table__pricing"><span>이창우</span><span>팀장</span></p>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Cteam2 ? this.state.Cteam2.map(c => {
              if(c.직위 === '책임' || c.직위 === '수석')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam2 ? this.state.Cteam2.map(c => {
              if(c.직위 === '선임')
                return(
                <li className="li_second">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam2 ? this.state.Cteam2.map(c => {
              if(c.직위 === '전임')
                return(
                <li className="li_third">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
               </ul>
       
          </li><li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">기능안전검증팀</h1>
            <p className="pricingTable-firstTable_table__pricing"><span>윤영산</span><span>팀장</span></p>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Cteam3 ? this.state.Cteam3.map(c => {
              if(c.직위  === '책임' || c.직위 === '수석')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam3 ? this.state.Cteam3.map(c => {
              if(c.직위 === '선임')
                return(
                <li className="li_second">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam3 ? this.state.Cteam3.map(c => {
              if(c.직위 === '전임')
                return(
                <li className="li_third">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
               </ul>
    
          </li> <li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">자율주행검증팀</h1>
            <p className="pricingTable-firstTable_table__pricing"><span>이창수</span><span>팀장</span></p>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Cteam4 ? this.state.Cteam4.map(c => {
              if(c.직위 === '책임' || c.직위 === '수석')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam4 ? this.state.Cteam4.map(c => {
              if(c.직위 === '선임')
                return(
                <li className="li_second">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam4 ? this.state.Cteam4.map(c => {
              if(c.직위 === '전임')
                return(
                <li className="li_third">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
               </ul>
  
          </li><li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">UAM로보틱스개발팀</h1>
            <p className="pricingTable-firstTable_table__pricing"><span>이규열</span><span>팀장</span></p>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Cteam5 ? this.state.Cteam5.map(c => {
              if(c.직위 === '책임' || c.직위 === '수석')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam5 ? this.state.Cteam5.map(c => {
              if(c.직위 === '선임')
                return(
                <li className="li_second">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Cteam5 ? this.state.Cteam5.map(c => {
              if(c.직위 === '전임')
                return(
                <li className="li_third">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
               </ul>
               
    
          </li>
          <li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">인턴</h1>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Cteam6 ? this.state.Cteam6.map((c, index) => {
              if(c.직위 === '인턴')
                return(
                <li className="li_last">{this.state.visible ? 
                  <input type="checkbox"onChange={(e) => this.checkedItemHandler(c.이메일, e.target.checked)} id={index}/> : null}
                  {c.이름}({c.그룹})
                </li>
