import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Row, Table } from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import { Link } from 'react-router-dom';
import './meeting.scss';
import { useCookies } from 'react-cookie';

const ProjectList = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [meetings , setmeetings] = useState([]);
  const columns = [ "회의명", "회의장소", "회의일시", "작성일시", "작성자"];
  
  const sortList = (e) => {
    let arrayCopy = [...meetings];
    switch (e.target.name) {
      case "ascMname":
        arrayCopy.sort((a, b) => a.회의명.localeCompare(b.회의명));
        break;
      case "descMname":
        arrayCopy.sort((a, b) => b.회의명.localeCompare(a.회의명));
        break;
      case "ascMPlace":
        arrayCopy.sort((a, b) => a.회의장소.localeCompare(b.회의장소));
        break;
      case "descMPlace":
        arrayCopy.sort((a, b) => b.회의장소.localeCompare(a.회의장소));
        break;
      case "ascMDate":
        arrayCopy.sort((a, b) => a.회의일시.localeCompare(b.회의일시));
        break;
      case "descMDate":
        arrayCopy.sort((a, b) => b.회의일시.localeCompare(a.회의일시));
        break;
      case "ascWDate":
        arrayCopy.sort((a, b) => a.작성일시.localeCompare(b.작성일시));
        break;
      case "descWDate":
        arrayCopy.sort((a, b) => b.작성일시.localeCompare(a.작성일시));
        break;
      case "ascWriter":
        arrayCopy.sort((a, b) => a.작성자.localeCompare(b.작성자));
        break;
      case "descWriter":
        arrayCopy.sort((a, b) => b.작성자.localeCompare(a.작성자));
        break;
      default:
        return;
    }
    setmeetings(arrayCopy);
  };

  useEffect(()=> findAllmeetings(), []);

  const findAllmeetings = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios.get(`http://${process.env.REACT_APP_API_URL}/meetings`)
    .then((res)=>{
      setmeetings(res.data);
      console.log(res.data);
    })
    .catch(err => {
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
 }

  return (
    <>
      <Row>
        <Col>
          <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>회의록</p>}>
            <Link to="/app/projects/meetings/new"><button className="btn btn-primary">새로운 회의록</button></Link>
            <Table className="project-list__table table-sm">
              <thead>
                <tr>
                  <th className="col-md-3 project-list__th">회의명<button className="orderBtn glyphicon glyphicon-collapse" name="ascMname" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descMname" onClick={sortList}/></th>
                  <th className="col-md-2 project-list__th">회의장소<button className="orderBtn glyphicon glyphicon-collapse" name="ascMPlace" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descMPlace" onClick={sortList}/></th>
                  <th className="col-md-2 project-list__th">회의일시<button className="orderBtn glyphicon glyphicon-collapse" name="ascMDate" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descMDate" onClick={sortList}/></th>
                  <th className="col-md-2 project-list__th">작성일시<button className="orderBtn glyphicon glyphicon-collapse" name="ascWDate" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descWDate" onClick={sortList}/></th>
                  <th className="col-md-1 project-list__th">작성자<button className="orderBtn glyphicon glyphicon-collapse" name="ascWriter" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descWriter" onClick={sortList}/></th>
                </tr>
              </thead>
              <tbody>
                {meetings.map((meeting) => {
                  return(
                  <tr key={meeting.id}>
                    
                    <td>
                        <Link to={{pathname: `/app/projects/meetings/detail/${meeting.id}`}}>
                        {meeting.회의명}
                        </Link>
                        </td>
                    <td>{meeting.회의장소}</td>
                    <td>
                        {meeting.회의일시}
                    </td>
                    <td>{meeting.작성일시}</td>
                    <td>{meeting.작성자}</td>
                  </tr>
                )})}
              </tbody>
            </Table>
          </Widget>
        </Col>
      </Row>
    </>
  );
}

export default ProjectList;
