import React from "react";
import { Row, Col } from "reactstrap";
import Form from "reactstrap/lib/Form";
import Widget from "../../components/Widget/Widget";
import styles from "./organization.module.scss";
import { useState, useEffect } from 'react';
import axios from "axios";
import PropTypes from "prop-types";


class organizationA extends React.Component {


  constructor(props) {
    super(props);

    this.state = {
     Ateam1: "", 
     Ateam2: "",
    };
  }


    componentDidMount(){
      this.setState({ status: true });

      this.findManagement();
      this.findFinance();
      this.findIntern();
    }

    componentWillUnmount() {
      this.setState({ status: false });
    }

    findManagement() {
       axios.get(`http://${process.env.REACT_APP_API_URL}/Ateam1`)
       .then(res=>res.data)
        .then((data)=>{
          this.setState({Ateam1:data});
    });
  }

  findFinance() {
    axios.get(`http://${process.env.REACT_APP_API_URL}/Ateam2`)
    .then(res=>res.data)
     .then((data)=>{
       this.setState({Ateam2:data});
 });
}

findIntern() {
  axios.get(`http://${process.env.REACT_APP_API_URL}/Ateam3`)
  .then(res=>res.data)
   .then((data)=>{
     this.setState({Ateam3:data});
});
}

    render() {
      const { isOpen, close } = this.props;
      return (
        <div className="pricingTable">
        <h2 className="pricingTable-title">경영기획실</h2>
        <h3 className="pricingTable-subtitle">실장 장정희</h3>
        
        <ul className="pricingTable-firstTable">
          <li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">경영지원팀</h1>
            <p className="pricingTable-firstTable_table__pricing"><span>최영익</span><span>부장</span></p>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Ateam1 ? this.state.Ateam1.map(c => {
              if(c.직위 === '부장')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Ateam1 ? this.state.Ateam1.map(c => {
              if(c.직위 === '과장')
                return(
                <li className="li_second">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Ateam1 ? this.state.Ateam1.map(c => {
              if(c.직위 === '대리' || c.직위 === '사원')
                return(
                <li className="li_third">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              
               </ul>
          </li><li className="pricingTable-firstTable_table">
          <h1 className="pricingTable-firstTable_table__header">재무기획팀</h1>
            <p className="pricingTable-firstTable_table__pricing"><span>전성환</span><span>부장</span></p>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Ateam2 ? this.state.Ateam2.map(c => {
              if(c.직위 === '부장')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Ateam2 ? this.state.Ateam2.map(c => {
              if(c.직위 === '과장')
                return(
                <li className="li_second">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Ateam2 ? this.state.Ateam2.map(c => {
               if(c.직위 === '대리' || c.직위 === '사원')
                return(
                <li className="li_third">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
               </ul>
               
    
          </li>
          <li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">인턴</h1>
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Ateam3 ? this.state.Ateam3.map(c => {
              if(c.직위 === '인턴')
                return(
                <li className="li_last">{c.이름}({c.그룹})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
               </ul>
               <button class="pricingTable-firstTable_table__getstart">구성원 삭제</button>
               
           
          </li>
        </ul>
      </div>
      );
    }
    
  }

  function mapStateToProps(state) {
    return {
      isFetching: state.register.isFetching,
      errorMessage: state.register.errorMessage,
    };
  }
  
  export default organizationA;
