import React, { useState, useEffect } from "react";
import {
  Row,
  Col,
  Table,
  Head,
  Body,
  Progress,
  Button,
  UncontrolledButtonDropdown,
  DropdownMenu,
  DropdownToggle,
  DropdownItem,
  Input,
  Label,
  Badge
} from "reactstrap";
import "./MyPage.scss"
import Widget from "../../components/Widget/Widget";
import axios from "axios";
import { useCookies } from 'react-cookie';

const MyPage = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [password, setPassword] = useState({
    ex: "",
    change: "",
    confirm: ""
  });
  const [user, setUser] = useState({
    id: "",
    name: "",
    division: "",
    team: "",
    ranks: "",
    position: "",
  });

  const onChangeInput = (e) => {
    setPassword({
      ...password,
      [e.target.id]: e.target.value
    });
  };

  useEffect(() => findAllInfo(), []);

  const findAllInfo = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios.get(`http://${process.env.REACT_APP_API_URL}/members/search?id=${id}`)
    .then((res) => {
      setUser(res.data[0]);
    })
    .catch(err => {
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
  };

  return (
    <>
      <Row>
        <Col>
          <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>My Page</p>}>
            <p style={{ fontWeight: 700, fontSize: 1.5+'rem'}}>내 정보</p>
            <Table>
              <thead>
                <tr>
                  <th>아이디</th>
                  <th>이름</th>
                  <th>소속</th>
                  <th>팀</th>
                  <th>직급</th>
                  <th>직책</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.division}</td>
                  <td>{user.team}</td>
                  <td>{user.ranks}</td>
                  <td>{user.position}</td>
                </tr>
              </tbody>
            </Table>
            <p style={{ fontWeight: 700, fontSize: 1.5+'rem'}}>비밀번호 변경</p>
            <div>
              <div className="mypage__sub_wrap">
                <div className="mypage__sub_title">기존 비밀번호</div>
                <input className="form-control" type="password" value={password.ex} id="ex" onChange={onChangeInput}/>
              </div>
              <div className="mypage__sub_wrap">
                <div className="mypage__sub_title">새 비밀번호</div>
                <input className="form-control" type="password" value={password.change} id="change" onChange={onChangeInput}/>
              </div>
              <div className="mypage__sub_wrap">
                <div className="mypage__sub_title">새 비밀번호 확인</div>
                <input className="form-control" type="password" value={password.confirm} id="confirm" onChange={onChangeInput}/>
              </div>
            </div>
            <Button color="primary" style={{marginTop: 0.5+'rem'}}>변경</Button>
          </Widget>
        </Col>
      </Row>
    </>
  );
};

export default MyPage;
