import React, { useEffect, useState } from 'react';
import {
    Table,
    Button,
    Input,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
} from "reactstrap";
import axios from "axios";
import moment from 'moment';
import { useCookies } from 'react-cookie';

const Member = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [name, setName] = useState();
  const [visible, setVisible] = useState(false)
  const [teams, setTeams] = useState([]);
  const [members, setMembers] = useState([]);
  const [member, setMember] = useState({
    num: "",
    id: "",
    password: "",
    name: "",
    team: "",
    division: "",
    ranks: "",
    position: "",
    authority: "",
  });

  const _openModal= () => {
    setVisible(true);
  }

  const _closeModal = () => {
    setVisible(false);
  }

  const toggleModal = () => {
    setVisible(!visible);
  }

  const onChangeInput = (e) => {
    console.log(e.target)
    setMember({
      ...member,
      [e.target.id]: e.target.value,
    });
  };

  useEffect(() => findAllInfo(), []);

  const findAllInfo = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/members`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/teams`),
    ])
    .then(axios.spread((res1, res2) => {
      setMembers(res1.data);
      setTeams(res2.data);
    }))
    .catch(err => {
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
  };

  const onSave = () => {
    if (member.id && member.password && member.name && member.team && member.division && member.ranks && member.authority) {
      if (member.num) {
        axios
          .post(`http://${process.env.REACT_APP_API_URL}/members/update`, {
            member: member
          })
          .then((response) => {
            console.log(response);
          })
          .catch(err => {
            if (cookies.token) {
              removeCookie('token');
            }
            alert("세션이 만료되었습니다. 다시 로그인 해주세요");
            window.location.href = "/";
          });
      } else {
        axios
          .post(`http://${process.env.REACT_APP_API_URL}/members/add`, {
            member: member
          })
          .then((response) => {
            console.log(response);
          })
          .catch(err => {
            if (cookies.token) {
              removeCookie('token');
            }
            alert("세션이 만료되었습니다. 다시 로그인 해주세요");
            window.location.href = "/";
          });
        }
      window.location.reload();
    }
    else {
      alert("모든 항목을 입력해주세요.");
    }
  };

  const onDelete = (num) => {
    axios
      .post(`http://${process.env.REACT_APP_API_URL}/members/delete`, {num})
      .then((response) => {
        console.log(response);
      })
      .catch(err => {
        if (cookies.token) {
          removeCookie('token');
        }
        alert("세션이 만료되었습니다. 다시 로그인 해주세요");
        window.location.href = "/";
      });
    window.location.reload();
  };

  const pswdReset = (num) => {
    axios.post(`http://${process.env.REACT_APP_API_URL}/members/reset`, {password:'1234', num})
    .then((response) => {
      console.log(response);
    });
    window.location.reload();
  }

  const _getSearch = () => {
    const search = members.find(member => member.name === name);
    console.log(search);
    if(search){
    pswdReset(search.num);
    }
   }

  const onEdit = (data) => {
    setMember(data);
  };

  return (
    <>
      <div className="member">
        <div style={{display: 'flex', justifyContent: 'space-between'}}>
          <p style={{ fontWeight: 700, fontSize: 1.5+'rem'}}>미래차검증전략실 직원 목록</p>
          <Button color="danger" size="sm" onClick={toggleModal}>
            비밀번호 초기화
          </Button>
        </div>
        <Modal isOpen={visible} toggle={toggleModal} id="news-close-modal" className="modal-content">
          <ModalHeader toggle={toggleModal} id="news-close-modal-label" className="modal-header">
            비밀번호 초기화
          </ModalHeader>
          <ModalBody className="bg-white modal-body">
            비밀번호 초기화
            <div style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}>
              <Input type='text' maxLength='20' value={name} onChange={(e) => setName(e.target.value)} style={{width: 70+'%'}} placeholder='이름을 입력해주세요.'/>
              <Button color="info" size="sm" onClick={() => {_getSearch()}} style={{width: 12+'%', fontSize: 0.8+'rem'}}>검색</Button>
            </div>
          </ModalBody>
        </Modal>
        <div style={{width:100+'%'}}>
          <Table className="manage__table table-sm">
            <thead>
              <tr>
                <th>ID<br /><br /><Input type="text" id="id" value={member.id} onChange={onChangeInput}/></th>
                <th>이름<br /><br /><Input type="text" id="name" value={member.name} onChange={onChangeInput}/></th>
                <th>소속<br /><br /><Input type="text" id="division" value={member.division} onChange={onChangeInput}/></th>
                <th>팀<br /><br />
                  <Input type="select" id="team" value={member.team} onChange={onChangeInput}>
                    <option value=""> </option>
                    {teams ? 
                    teams
                    .filter(({year}) => year == moment(new Date()).format("YYYY"))
                    .map((team, index) => {
                    return( 
                      <option key={index} value={team.teamname}>{team.teamname}</option>     
                        );
                    })  :""}
                  </Input>
                </th>
                <th>직급<br /><br /><Input type="text" id="ranks" value={member.ranks} onChange={onChangeInput}/></th>
                <th>직책<br /><br /><Input type="text" id="position" value={member.position} onChange={onChangeInput}/></th>
                <th>권한<br /><br /><Input type="text" id="authority" value={member.authority} onChange={onChangeInput}/></th>
                <th>Action<br /><br /><Button size="sm" color="info" onClick={onSave}>{member.num ? "변경" : "추가"}</Button></th>
              </tr>
            </thead>
            <tbody>
              {members ? members.map(c => {
                  return( <tr key={c.num}>
                    <td style={{ textAlign: "center" }}>{c.id}</td> 
                    <td style={{ textAlign: "center" }}>{c.name}</td>
                    <td style={{ textAlign: "center" }}>{c.division}</td>
                    <td style={{ textAlign: "center" }}>{c.team}</td>
                    <td style={{ textAlign: "center" }}>{c.ranks}</td>
                    <td style={{ textAlign: "center" }}>{c.position}</td>
                    <td style={{ textAlign: "center" }}>{c.authority}</td>
                    <td>
                    <Button color={"warning"} className="UpdateBtn" size="sm" variant="primary" onClick={()=>onEdit(c)}>수정</Button>
                    &nbsp;
                    <Button color={"default"} className="UpdateBtn" size="sm" variant="danger"onClick={() => onDelete(c.num)}>삭제</Button></td>
                  </tr>
                    );
                })  :""}
            </tbody>
          </Table>
        </div>
      </div>
    </>
  );
};

export default Member;
