import React from "react";
import Member from "../../components/Manage/Member";
import Team from "../../components/Manage/Team";
import Tool from "../../components/Manage/Tool";
import {
  Row,
  Col,
  Table,
  Head,
  Body,
  Progress,
  Button,
  UncontrolledButtonDropdown,
  DropdownMenu,
  DropdownToggle,
  DropdownItem,
  Input,
  Label,
  Badge
} from "reactstrap";
import s from "./Manages.modules.scss";
import Widget from "../../components/Widget/Widget";

const Manages = () => {
  const [menu, setMenu] = React.useState("member");
  return (
    <>
      <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>관리자페이지</p>}>
        <Row>
          {/* <Col md="12"> */}
            <div className={s.tab}>
              <Button
                color="primary"
                size="sm"
                className={menu === "member" ? s.active : ""}
                onClick={() => setMenu("member")}
              >
                회원관리
              </Button>
              <Button
                color="primary"
                size="sm"
                className={menu === "team" ? s.active : ""}
                onClick={() => setMenu("team")}
              >
                팀관리
              </Button>
              <Button
                color="primary"
                size="sm"
                className={menu === "tool" ? s.active : ""}
                onClick={() => setMenu("tool")}
              >
                도구관리
              </Button>
            </div>
          {/* </Col> */}
        </Row>
        <Row>
          <Col md="12">
            {menu === "member" && <Member />}
            {menu === "team" && <Team />}
            {menu === "tool" && <Tool />}
          </Col>
        </Row>
      </Widget>
        {/* 첫번째 테이블 */}
        {/* <Member/> */}
        {/* 두번째 테이블 */}
        {/* <Team/> */}
        {/* 세번째 테이블 */}
        {/* <Tool/> */}
      {/* </Widget> */}
    </>
  );
}

export default Manages;
