import React from 'react';
import {
    Row,
    Col,
    Table,
    Progress,
    Button,
    UncontrolledButtonDropdown,
    DropdownMenu,
    DropdownToggle,
    DropdownItem,
    Alert,
    Input,
    Label,
    Badge
  } from "reactstrap";
import Widget from "../../components/Widget";
import axios from "axios";
import "./Member.modules.scss";


class Member extends React.Component{
     
    constructor(props){
      super(props);

      this.state={
        teams:"",
        members:"", 
        num:"",
        id:"",
        password:"",
        name:"",
        team:"",
        division:"",
        ranks:"",
        authority:"",
        updateEdit :[],
        onEdit : this.onEdit,
        onDelete : this.onDelete,
        updateValue : this.updateValue,
        onSave : this.onSave,
        goAlert : this.goAlert,
      };
    }

    getRecord = (num) => {
      const product = this.state.members.find(item => item.num === num);
      return product;
    }

    onEdit = (num) =>{
      const tempProduct = this.state.members;
      const index  = tempProduct.indexOf(this.getRecord(num));
      const selectedRecord = tempProduct[index];
      this.setState({
        num : selectedRecord['num'],
        id : selectedRecord['id'],
        password : selectedRecord['password'],
        name : selectedRecord['name'],
        division : selectedRecord['division'],
        team : selectedRecord['team'],
        ranks : selectedRecord['ranks'],
        authority : selectedRecord['authority'],
      })
    }

    updateValue= (e,test) =>{
      if(test === "id"){
        this.state.id = e.target.value;
      }
      if(test === "password"){
        this.state.password = e.target.value;
      }
      if(test === "name"){
        this.state.name = e.target.value;
      }
      if(test === "team"){
        this.state.team = e.target.value;
      }
      if(test === "division"){
        this.state.division = e.target.value;
      }
      if(test === "ranks"){
        this.state.ranks = e.target.value;
      }
      if(test === "authority"){
        this.state.authority = e.target.value;
      }
      const tempArr = [this.state.id, this.state.password, this.state.name, this.state.division, this.state.team, this.state.ranks, this.state.authority];
      this.setState({
        updateEdit : tempArr
      })
    }

    onSave = (num) =>{
      if(num!==''){ // Update 조건문
        const SaveRecord = this.state.members;
        const index = SaveRecord.indexOf(this.getRecord(num));
        const Record = SaveRecord[index];
        const deleteConfirm = () => console.log("삭제했습니다.")
        Record['id'] = this.state.updateEdit[0];
        Record['password'] = this.state.updateEdit[1];
        Record['name'] = this.state.updateEdit[2];
        Record['division'] = this.state.updateEdit[3];
        Record['team'] = this.state.updateEdit[4];
        Record['ranks'] = this.state.updateEdit[5];
        Record['authority'] = this.state.updateEdit[6];
        this.setState({
          members : [...this.state.members],
          num :"" , id:"", password:"", name:"", team:"", division:"", ranks:"", authority:"",
        })

        axios.post(`http://${process.env.REACT_APP_API_URL}/members/update`, //DB연동
        {num: this.state.num, id: this.state.id, password: this.state.password, name: this.state.name, team: this.state.team,
         division: this.state.division, ranks: this.state.ranks , authority: this.state.authority }).then((response)=>{
        console.log(response);
         });
         window.location.reload();
      }
      // Insert 조건문
      else{ 
        const MaxNum = Math.max(...this.state.members.map(item=>item.num))
        const num = MaxNum + 1;
        const newArr = [];
        newArr['id'] = this.state.updateEdit[0];
        newArr['password'] = this.state.updateEdit[1];
        newArr['name'] = this.state.updateEdit[2];
        newArr['division'] = this.state.updateEdit[3];
        newArr['team'] = this.state.updateEdit[4];
        newArr['ranks'] = this.state.updateEdit[5];
        newArr['authority'] = this.state.updateEdit[6];

        this.setState({
          members : [...this.state.members, newArr],
          num :"" , id:"", password:"", name:"",  team:"", division:"", ranks:"", authority:"",
        })

        axios.post(`http://${process.env.REACT_APP_API_URL}/members/add`, //DB연동
        {num: MaxNum+1, id: this.state.id, password: this.state.password, name: this.state.name, team: this.state.team,
          division: this.state.division, ranks: this.state.ranks , authority: this.state.authority}).then((response)=>{
        console.log(response);
        });

        window.location.reload();
      }
    }

    //DELETE 하기 
    onDelete = (num) => {
      if (window.confirm("정말 삭제하시겠습니까??") == true){    //확인
        const tempProduct = this.state.members.filter(item=> item.num !== num);
      this.setState({
        members : tempProduct,
        num :"" , id:"", password:"", name:"",  team:"", division:"", ranks:"", authority:"",
      })
      axios.post(`http://${process.env.REACT_APP_API_URL}/members/delete`, //DB연동
        {num: num}).then((response)=>{
        console.log(response);
        });
        window.location.reload();
      }else{   //취소
        return;
      }
    }

    componentDidMount(){
      this.findAllMembers();
    } 

    findAllMembers(){
      axios
      .all([axios.get(`http://${process.env.REACT_APP_API_URL}/members`),axios.get(`http://${process.env.REACT_APP_API_URL}/teams`) ])
      .then(axios.spread((res1,res2)=>{
             this.setState({members:res1.data});
             this.setState({teams:res2.data});
          })
      )
    }

    


    render() {
        return(
        <div>
          <div>
            <Widget
              title={<p style={{ fontWeight: 700 }}>미래차검증전략실 직원 목록</p>}
            >
            <div>
              <Table size="sm" responsive>
                  <thead>
                    <tr >
                      <th rowSpan="2">ID<br /><br /><Input type="text" value={this.state.id} onChange={(e) => {this.state.updateValue(e,"id")}}/></th>
                      <th rowSpan="2">Password<br /><br /><Input type="text" value={this.state.password} onChange={(e) => {this.state.updateValue(e,"password")}}/></th>
                      <th rowSpan="2">이름<br /><br /><Input type="text" value={this.state.name} onChange={(e) => {this.state.updateValue(e,"name")}}/></th>
                      <th rowSpan="2">실<br /><br /><Input type="text" value={this.state.division} onChange={(e) => {this.state.updateValue(e,"division")}}/></th>
                      <th rowSpan="2">팀<br /><br />
                        <Input type="select" value={this.state.team} onChange={(e) => {this.state.updateValue(e,"team")}}>
                          <option value=""> </option>
                          {this.state.teams ? this.state.teams.map(c => {
                          return( 
                            <option value={c.teamname}>{c.teamname}</option>     
                              );
                          })  :""}
                        </Input>
                      </th>
                      <th rowSpan="2">직급<br /><br /><Input type="text" value={this.state.ranks} onChange={(e) => {this.state.updateValue(e,"ranks")}}/></th>
                      <th rowSpan="2">권한<br /><br /><Input type="text" value={this.state.authority} onChange={(e) => {this.state.updateValue(e,"authority")}}/></th>
                      <th rowSpan="2">Action<br /><br /><Button size="sm" onClick={()=>{this.state.onSave(this.state.num)}}>{this.state.num ? "변경" : "추가"}</Button></th>
                    </tr>
                  </thead>
                  
                  <tbody>  
                  {this.state.members ? this.state.members.map(c => {
                      return( <tr key={c.num}>
                        <td style={{ textAlign: "center" }}>{c.id}</td> 
                        <td style={{ textAlign: "center" }}>{c.password}</td> 
                        <td style={{ textAlign: "center" }}>{c.name}</td>
                        <td style={{ textAlign: "center" }}>{c.division}</td>
                        <td style={{ textAlign: "center" }}>{c.team}</td>
                        <td style={{ textAlign: "center" }}>{c.ranks}</td>
                        <td style={{ textAlign: "center" }}>{c.authority}</td>
                        <td><Button color={"warning"} className="UpdateBtn" size="sm" variant="primary" onClick={()=>{this.state.onEdit(c.num)}}>수정</Button>
                        &nbsp;
                        <Button color={"default"} className="UpdateBtn" size="sm" variant="danger"onClick={()=>{this.state.onDelete(c.num)}}>삭제</Button></td>
                      </tr>
                        );
                    })  :""}
              </tbody>
              </Table>
            </div>

            </Widget>
          </div>
        </div>
        )
    }
}

export default Member;
