import React, { PureComponent } from 'react';
import {
  Row,
  Col,
} from 'reactstrap';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official'
import variablePie from 'highcharts/modules/variable-pie';
import exporting from 'highcharts/modules/exporting';
import exportData from 'highcharts/modules/export-data';
import accessibility from 'highcharts/modules/accessibility';
import highcharts3d from 'highcharts/highcharts-3d';
import sunburst from 'highcharts/modules/sunburst';
import vector from 'highcharts/modules/vector';
import wordcloud from 'highcharts/modules/wordcloud';
// import gantt from 'highcharts/modules/gantt';
import highchartsGantt from "highcharts/modules/gantt";
import Widget from '../../../components/Widget';
import chartsData from './mock';

variablePie(Highcharts);
exporting(Highcharts);
exportData(Highcharts);
accessibility(Highcharts);
highcharts3d(Highcharts);
sunburst(Highcharts);
vector(Highcharts);
wordcloud(Highcharts);
// gantt(Highcharts);
highchartsGantt(Highcharts);

var today = new Date(),
  day = 1000 * 60 * 60 * 24,
  map = Highcharts.map,
  dateFormat = Highcharts.dateFormat,
  series,
  cars;

// Set to 00:00:00:000 today
today.setUTCHours(0);
today.setUTCMinutes(0);
today.setUTCSeconds(0);
today.setUTCMilliseconds(0);
today = today.getTime();

cars = [{
  model: 'Nissan Leaf',
  current: 0,
  deals: [{
    rentedTo: 'Lisa Star',
    from: today - 1 * day,
    to: today + 2 * day
  }, {
    rentedTo: 'Shane Long',
    from: today - 3 * day,
    to: today - 2 * day
  }, {
    rentedTo: 'Jack Coleman',
    from: today + 5 * day,
    to: today + 6 * day
  }]
}, {
  model: 'Jaguar E-type',
  current: 0,
  deals: [{
    rentedTo: 'Martin Hammond',
    from: today - 2 * day,
    to: today + 1 * day
  }, {
    rentedTo: 'Linda Jackson',
    from: today - 2 * day,
    to: today + 1 * day
  }, {
    rentedTo: 'Robert Sailor',
    from: today + 2 * day,
    to: today + 6 * day
  }]
}, {
  model: 'Volvo V60',
  current: 0,
  deals: [{
    rentedTo: 'Mona Ricci',
    from: today + 0 * day,
    to: today + 3 * day
  }, {
    rentedTo: 'Jane Dockerman',
    from: today + 3 * day,
    to: today + 4 * day
  }, {
    rentedTo: 'Bob Shurro',
    from: today + 6 * day,
    to: today + 8 * day
  }]
}, {
  model: 'Volkswagen Golf',
  current: 0,
  deals: [{
    rentedTo: 'Hailie Marshall',
    from: today - 1 * day,
    to: today + 1 * day
  }, {
    rentedTo: 'Morgan Nicholson',
    from: today - 3 * day,
    to: today - 2 * day
  }, {
    rentedTo: 'William Harriet',
    from: today + 2 * day,
    to: today + 3 * day
  }]
}, {
  model: 'Peugeot 208',
  current: 0,
  deals: [{
    rentedTo: 'Harry Peterson',
    from: today - 1 * day,
    to: today + 2 * day
  }, {
    rentedTo: 'Emma Wilson',
    from: today + 3 * day,
    to: today + 4 * day
  }, {
    rentedTo: 'Ron Donald',
    from: today + 5 * day,
    to: today + 6 * day
  }]
}];

// Parse car data into series.
series = cars.map(function (car, i) {
  var data = car.deals.map(function (deal) {
    return {
      id: 'deal-' + i,
      rentedTo: deal.rentedTo,
      start: deal.from,
      end: deal.to,
      // color: '#' + Highcharts.getOptions().colors[i],
      parent: 'car-' + 0,
      y: i
    };
  });
  return {
    name: car.model,
    data: data,
    current: car.deals[car.current]
  };
});

class HighChartsPage extends PureComponent {

  state = {
    cd: chartsData,
  }

  render() {

    const { cd } = this.state;

    return (
      <div>
        <h1 className="page-title">Visual - <span className="fw-semi-bold">Highcharts</span></h1>
        <p>For more information please read full <a href="https://github.com/highcharts/highcharts-vue">documentation</a></p>
        <Row>
          <Col lg={12} xs={12}>
            <Widget
              title={<h5>Highcharts <span className="fw-semi-bold">Line Chart</span></h5>}
              close collapse
            >
              <HighchartsReact
              highcharts={Highcharts}
              constructorType={"ganttChart"}
              options={{
                title: {
                  text: '프로젝트 명'
                },
                navigator: {
                  enabled: true,
                  liveRedraw: true,
                  series: {
                    type: 'gantt',
                    pointPlacement: 0.5,
                    pointPadding: 0.25
                  },
                },
                rangeSelector: { //1개월, 3개월, 6개월, 1년 바꿀수 있음
                  enabled: true,
                  selected: 0,
                  inputDateFormat: '%Y-%m-%d',
                  buttons: [{
                    type: 'month',
                    count: 1,
                    text: '1m',
                    title: 'View 1 month'
                }, {
                    type: 'month',
                    count: 3,
                    text: '3m',
                    title: 'View 3 months'
                }, {
                    type: 'month',
                    count: 6,
                    text: '6m',
                    title: 'View 6 months'
                }, {
                    type: 'ytd',
                    text: 'YTD',
                    title: '올해 초부터 지금까지'
                }, {
                    type: 'year',
                    count: 1,
                    text: '1년',
                    title: 'View 1 year'
                }, {
                    type: 'all',
                    text: '전체보기',
                    // title: 'View all'
                }],
                buttonSpacing: 10,
                },
                series: [{
                  name: 'Project 1',
                  data: [{
                    id: 's',
                    name: 'Start prototype',
                    start: Date.UTC(2021, 10, 18),
                    end: Date.UTC(2021, 10, 20)
                  }, {
                    id: 'b',
                    name: 'Develop',
                    start: Date.UTC(2021, 10, 20),
                    end: Date.UTC(2021, 10, 25),
                    dependency: 's'
                  }, {
                    id: 'a',
                    name: 'Run acceptance tests',
                    start: Date.UTC(2021, 10, 23),
                    end: Date.UTC(2021, 10, 26)
                  }, {
                    name: 'Test prototype',
                    start: Date.UTC(2021, 10, 27),
                    end: Date.UTC(2021, 10, 29),
                    dependency: ['a', 'b']
                  }]
                  }, {
                    name: 'Project 2',
                    data: [{
                    id: 'ss',
                    name: '대분류',
                    pointWidth: 0,
                  }, {
                    name: 'Requirements',
                    id: 'requirements',
                    parent: 'ss',
                    start: new Date(2021, 10, 19).getTime(),
                    end: new Date(2021, 10, 30).getTime(),
                  }, {
                    name: 'playing',
                    id: 'playing',
                    parent: 'ss',
                    start: new Date(2021, 11, 1).getTime(),
                    end: new Date(2021, 11, 10).getTime()
                  }, {
                    name: '레이어',
                    start: new Date(2021, 10, 20),
                    end: new Date(2021, 10, 25),
                  }, {
                    name: '아이콘',
                    start: new Date(2021, 10, 23),
                    end: new Date(2021, 10, 26)
                  }, {
                    name: '애니메이션',
                    start: new Date(2021, 10, 27),
                    end: new Date(2021, 10, 29),
                  }]
                }]
              }} />
            </Widget>
          </Col>
          {/* <Col lg={6} xs={12}>
            <Widget
              title={<h5>Highcharts <span className="fw-semi-bold">Pie Chart</span></h5>}
              close collapse
            >
              <HighchartsReact highcharts={Highcharts} options={cd.pie} />
            </Widget>
          </Col>
          <Col lg={6} xs={12}>
            <Widget
              title={<h5>Highcharts <span className="fw-semi-bold">Column 3D Chart</span></h5>}
              close collapse
            >
              <HighchartsReact highcharts={Highcharts} options={cd.column3D} />
            </Widget>
          </Col> */}
          {/* <Col lg={5} xs={12}>
            <Row>
              <Col lg={12} xs={12}>
                <Widget
                  title={<h5>Highcharts <span className="fw-semi-bold">Vector Chart</span></h5>}
                  close collapse
                  >
                    <HighchartsReact highcharts={Highcharts} options={cd.vector} />
                  </Widget>
              </Col>
              <Col lg={12} xs={12}>
                <Widget
                  title={<h5>Highcharts <span className="fw-semi-bold">Sunburst Chart</span></h5>}
                  close collapse
                  >
                    <HighchartsReact highcharts={Highcharts} options={cd.wordCloud} />
                </Widget>
              </Col>
            </Row>
          </Col>
          <Col lg={7} xs={12}>
            <Widget
              title={<h5>Highcharts <span className="fw-semi-bold">Sunburst Chart</span></h5>}
              close collapse
              >
                <HighchartsReact highcharts={Highcharts} options={cd.sunburst} />
            </Widget>
          </Col> */}
        </Row>
      </div>
    );
  }
}

export default HighChartsPage;
