import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Row, Table } from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import { Link } from 'react-router-dom';
import './meeting.scss';

const ProjectList = () => {
  const [meetings , setmeetings] = useState([]);
  const columns = [ "회의명", "회의장소", "회의일시", "작성일시", "작성자"];
  
  const compareAscMname = (a, b) => a.회의명 < b.회의명 ? -1 : a.회의명 > b.회의명 ? 1 : 0;
  const compareDescMname = (a, b) => b.회의명 < a.회의명 ? -1 : b.회의명 > a.회의명 ? 1 : 0;
  const compareAscMPlace = (a, b) => a.회의장소 < b.회의장소 ? -1 : a.회의장소 > b.회의장소 ? 1 : 0;
  const compareDescMPlace = (a, b) => b.회의장소 < a.회의장소 ? -1 : b.회의장소 > a.회의장소 ? 1 : 0;
  const compareAscMDate = (a, b) => a.회의일시 < b.회의일시 ? -1 : a.회의일시 > b.회의일시 ? 1 : 0;
  const compareDescMDate = (a, b) => b.회의일시 < a.회의일시 ? -1 : b.회의일시 > a.회의일시 ? 1 : 0;
  const compareAscWDate = (a, b) => a.작성일시 < b.작성일시 ? -1 : a.작성일시 > b.작성일시 ? 1 : 0;
  const compareDescWDate = (a, b) => b.작성일시 < a.작성일시 ? -1 : b.작성일시 > a.작성일시 ? 1 : 0;
  const compareAscWriter = (a, b) => a.작성자 < b.작성자 ? -1 : a.작성자 > b.작성자 ? 1 : 0;
  const compareDescWriter = (a, b) => b.작성자 < a.작성자 ? -1 : b.작성자 > a.작성자 ? 1 : 0;
  const sortList = (e) => {
    let arrayCopy = [...meetings];
    switch (e.target.name) {
      case "ascMname":
        arrayCopy.sort(compareAscMname);
        break;
      case "descMname":
        arrayCopy.sort(compareDescMname);
        break;
      case "ascMPlace":
        arrayCopy.sort(compareAscMPlace);
        break;
      case "descMPlace":
        arrayCopy.sort(compareDescMPlace);
        break;
      case "ascMDate":
        arrayCopy.sort(compareAscMDate);
        break;
      case "descMDate":
            arrayCopy.sort(compareDescMDate);
      case "ascWDate":
        arrayCopy.sort(compareAscWDate);
        break;
      case "descWDate":
            arrayCopy.sort(compareDescWDate);
        break;
      case "ascWriter":
            arrayCopy.sort(compareAscWriter);
            break;
      case "descWriter":
                arrayCopy.sort(compareDescWriter);
            break;
      default:
        return;
    }
    setmeetings(arrayCopy);
  };

  useEffect(()=> findAllmeetings(), []);

  const findAllmeetings = () => {
    axios.get(`http://${process.env.REACT_APP_API_URL}/meetings`)
    .then((res)=>{
           setmeetings(res.data);
           console.log(res.data);
        })
 }

  return (
    <>
      <Row>
        <Col>
          <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>회의록</p>}>
            <Link to="/app/meetings/new"><button className="btn btn-primary">새로운 회의록</button></Link>
            <Table striped hover responsive>
              <thead>
                <tr>
                  <th>{columns[0]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascMname" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descMname" onClick={sortList}/></th>
                  <th>{columns[1]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascMPlace" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descMPlace" onClick={sortList}/></th>
                  <th>{columns[2]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascMDate" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descMDate" onClick={sortList}/></th>
                  <th>{columns[3]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascWDate" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descWDate" onClick={sortList}/></th>
                  <th>{columns[4]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascWriter" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descWriter" onClick={sortList}/></th>
                </tr>
              </thead>
              <tbody>
                {meetings.map((meeting) => {
                  return(
                  <tr key={meeting.id}>
                    
                    <td>
                        <Link to={{pathname: `/app/meetings/detail/${meeting.id}`}}>
                        {meeting.회의명}
                        </Link>
                        </td>
                    <td>{meeting.회의장소}</td>
                    <td>
                        {meeting.회의일시}
                    </td>
                    <td>{meeting.작성일시}</td>
                    <td>{meeting.작성자}</td>
                  </tr>
                )})}
              </tbody>
            </Table>
          </Widget>
        </Col>
      </Row>
    </>
  );
}

export default ProjectList;
