import axios from 'axios';
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Row, Col } from "reactstrap";
import Table from "reactstrap/lib/Table";
import Widget from "../../components/Widget/Widget";
import "./DetailReport.scss";

const DetailWeekly = (props) => {
  const id = Number(props.match.params.id);
  const project_id = props.match.params.pid;
  const [project, setProject] = useState({
    프로젝트명: "",
    PM: "",
    상태: "",
    착수: "",
    종료: "",
  });
  const [report, setReport] = useState({
    작성자: "",
    금주계획: "",
    금주진행: "",
    차주계획: "",
    특이사항: "",
    비고: "",
    최종확인: "",
  });
  const [inModifying, setInModifying] = useState(false);
  const [lastReport, setLastReport] = useState({
    금주계획: '',
    금주진행: '',
    차주계획: '',
    특이사항: '',
    비고: ''
  });
  const cssStart = "<span style='background-color: #ffff00'>"
  const cssEnd = "</span>";

  const onChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "작성자":
        return(setReport({ ...report, 작성자: value }));
      case "금주계획":
        return(setReport({ ...report, 금주계획: value }));
      case "금주진행":
        return(setReport({ ...report, 금주진행: value }));
      case "차주계획":
        return(setReport({ ...report, 차주계획: value }));
      case "특이사항":
        return(setReport({ ...report, 특이사항: value }));
      case "비고":
        return(setReport({ ...report, 비고: value }));
      default:
        return;
    }
  }

  const onClickConfirm = () => {
    const result = window.confirm("최종확인 하시겠습니까?");
    if (result) {
      axios.post(`http://${process.env.REACT_APP_API_URL}/reports/confirm`, {id})
      .then(res => {
        console.log(res);
      });
      window.location.reload();
    }
  }

  const onClickUpdate = () =>
  {
    const result = window.confirm("수정하시겠습니까?");
    if(result){
      axios.post(`http://${process.env.REACT_APP_API_URL}/reports/update`, {report, id, 최종수정시간: new Date()})
      .then((response)=>{
        console.log(response);
      });
      window.location.reload();
    }
  }

  const onClickDelete = () =>
  {
    const result = window.confirm("삭제하시겠습니까?");
    if (result) {
      axios.post(`http://${process.env.REACT_APP_API_URL}/reports/delete`, {id: id})
      .then((response)=>{
        console.log(response);
      }
      );
      window.location.href = "#/app/projects/reports";
    }
  }

  useEffect(()=> findAllInfos(), []);

  const findAllInfos = () => {
    axios
    .all([axios.get(`http://${process.env.REACT_APP_API_URL}/projects/search?id=${props.match.params.pid}&year=${props.match.params.year}`), axios.get(`http://${process.env.REACT_APP_API_URL}/reports/search?id=${props.match.params.id}`),
    axios.get(`http://${process.env.REACT_APP_API_URL}/reports/search/last?pid=${props.match.params.pid}`)])
    .then(axios.spread((res1,res2,res3)=>{
      setProject(res1.data[0]);
      setReport(res2.data[0]);
      const _lastReport = res3.data.find(({최종수정시간}) => new Date(최종수정시간).getWeek(3) === new Date(res2.data[0].최종수정시간).getWeek(3) - 1);
      _lastReport ? setLastReport({..._lastReport}) : setLastReport({금주계획: '', 금주진행: '', 차주계획: '', 특이사항: '', 비고: ''});
    })
    )
  }

  function highlightText() { //강조버튼 클릭시 해당 내용을 강조하는 함수
    let selection = "";
    if (document.getSelection) { //크롬
      selection = document.getSelection();
    } else if (document.selection) { //익스
      selection = document.selection.createRange().text;
    }
    try {
      let selectionTextArea = selection.anchorNode.lastElementChild; //선택된 textArea
      if (selectionTextArea.name === "프로젝트" || selectionTextArea.name === "작성자")
        return;
      //getCssIndex(selectionTextArea.value); //선택된 textArea에 css 태그 index 가져오기
      let selectStartIndex = selectionTextArea.selectionStart; // 드래그 영역 selectStartIndex~selectEndIndex-1
      let selectEndIndex = selectionTextArea.selectionEnd;
      let content = selectionTextArea.value;

      // aaAAbb => aa AA bb
      let dragBefore = selectionTextArea.value.substring(0, selectStartIndex);
      let drag = selectionTextArea.value.substring(selectStartIndex, selectEndIndex); //강조할 부분
      let dragAfter = selectionTextArea.value.substring(selectEndIndex, content.length);

      // aa<span>AA</span>bb -> AA만 드래그 했을 경우 고려해서 => aa <span>AA</span> bb
      let includeDragBefore = selectionTextArea.value.substring(0, selectStartIndex-cssStart.length)
      let includeDrag = selectionTextArea.value.substring(selectStartIndex-cssStart.length, selectEndIndex+cssEnd.length) 
      let includeDragAfter = selectionTextArea.value.substring(selectEndIndex+cssEnd.length, content.length);
      
      // <span></span> +- 과정에서 인덱스 벗어나는 경우 고려
      if((selectStartIndex-cssStart.length < 0)&&(selectEndIndex+cssEnd.length>=content.length)){
        includeDragBefore = dragBefore;
        includeDrag = drag;
        includeDragAfter = dragAfter;
      }
      else if(selectStartIndex-cssStart.length < 0){
        includeDragBefore = "";
        includeDrag = selectionTextArea.value.substring(0, selectEndIndex+cssEnd.length)
      }
      else if(selectEndIndex+cssEnd.length>=content.length){
        selectionTextArea.value.substring(selectStartIndex-cssStart.length, content.length)
        includeDragAfter = "";
      }
      
      if(hasHighlight(includeDrag)){ // 강조 해제
        includeDrag = includeDrag.replaceAll(cssStart,"");
        includeDrag = includeDrag.replaceAll(cssEnd, "");
        dragBefore = includeDragBefore;
        drag = includeDrag;
        dragAfter = includeDragAfter;
      }
      else{ // 강조
        drag = cssStart + drag + cssEnd;
      }
      selectionTextArea.value = dragBefore + drag + dragAfter;
      const { name } = selectionTextArea;
      switch (name) {
        case "금주계획":
          return(setReport({ ...report, 금주계획: selectionTextArea.value }));
        case "금주진행":
          return(setReport({ ...report, 금주진행: selectionTextArea.value }));
        case "차주계획":
          return(setReport({ ...report, 차주계획: selectionTextArea.value }));
        case "특이사항":
          return(setReport({ ...report, 특이사항: selectionTextArea.value }));
        case "비고":
          return(setReport({ ...report, 비고: selectionTextArea.value }));
        default:
          return;
      }
    } catch (err) {
    }
  }

  function hasHighlight(content){ //강조되어있는지 확인하는 함수
    if(content.indexOf(cssStart,0)!=-1 || content.indexOf(cssEnd,0) != -1)
      return true; //cssStart와 cssEnd 둘 중 하나라도 가지고 있으면 true
    return false;
  }

  return (
    <>
      <Row>
        <Col>
          <Widget style={{paddingTop: 2+'rem'}}>
            <Table>
              <tbody>
                <tr style={{fontWeight: 'bold'}}>
                  <td>프로젝트</td>
                  <td>
                    <div className="form-control">
                      <Link to={{pathname: `/app/projects/detail/${project_id}/${props.match.params.year}`}}>{project.프로젝트명}</Link>
                    </div>
                  </td>
                </tr>
                <tr style={{fontWeight: 'bold'}}>
                  <td>작성자</td>
                  <td><input className="form-control" type="text" name="작성자" value={report.작성자} onChange={onChange}/></td>
                </tr>
              </tbody>
            </Table>
          </Widget>
          <Widget style={{paddingTop: 2+'rem'}}>
            {inModifying ?
              <>
                <button className="btn btn-warning" onClick={highlightText} style={{zIndex: 10, position: 'fixed', right:5+'rem' }}>강조</button>
                <Table>
                  <tr style={{fontWeight: 'bold'}}>
                    <td className="col-md-1">(전)금주계획</td>
                    <td className="col-md-1">금주계획</td>
                  </tr>
                  <tr>
                    <td>
                      <p dangerouslySetInnerHTML={ {__html: lastReport.금주계획} }></p>
                    </td>
                    <td>
                      <textarea 
                        type="text" 
                        name="금주계획"
                        maxLength="1500" 
                        value={report.금주계획}
                        onChange={onChange}/>
                    </td>
                  </tr>
                  <tr style={{fontWeight: 'bold'}}>
                    <td>(전)금주진행</td>
                    <td>금주진행</td>
                  </tr>
                  <tr>
                    <td>
                      <p dangerouslySetInnerHTML={ {__html: lastReport.금주진행} }></p>
                    </td>
                    <td>
                      <textarea
                        type="text" 
                        name="금주진행" 
                        maxLength="3500" 
                        value={report.금주진행} 
                        onChange={onChange}/>
                      </td>
                  </tr>
                  <tr style={{fontWeight: 'bold'}}>
                    <td>(전)차주계획</td>
                    <td>차주계획</td>
                  </tr>
                  <tr>
                    <td>
                      <p dangerouslySetInnerHTML={ {__html: lastReport.차주계획} }></p>
                    </td>
                    <td>
                      <textarea
                        type="text" 
                        name="차주계획" 
                        maxLength="1500" 
                        value={report.차주계획} 
                        onChange={onChange}/>
                      </td>
                  </tr>
                  <tr style={{fontWeight: 'bold'}}>
                    <td>(전)특이사항</td>
                    <td>특이사항</td>
                  </tr>
                  <tr>
                    <td>
                      <p dangerouslySetInnerHTML={ {__html: lastReport.특이사항} }></p>
                    </td>
                    <td>
                      <textarea
                        type="text" 
                        name="특이사항" 
                        maxLength="1500" 
                        value={report.특이사항} 
                        onChange={onChange}/>
                      </td>
                  </tr>
                  <tr style={{fontWeight: 'bold'}}>
                    <td>(전)비고</td>
                    <td>비고</td>
                  </tr>
                  <tr>
                    <td>
                      <p dangerouslySetInnerHTML={ {__html: lastReport.비고} }></p>
                    </td>
                    <td>
                      <textarea
                        type="text" 
                        name="비고" 
                        maxLength="1500" 
                        value={report.비고} 
                        onChange={onChange}/>
                      </td>
                  </tr>
                </Table>
              </>
              :
              <>
                <Table className={report.최종확인 ? "confirmed" : ""}>
                  <tr style={{fontWeight: 'bold'}}>
                    <td className="col-md-1">(전)금주계획</td>
                    <td className="col-md-1">금주계획</td>
                  </tr>
                  <tr>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: lastReport.금주계획} }></p>
                    </td>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: report.금주계획} }></p>
                    </td>
                  </tr>
                  <tr style={{fontWeight: 'bold'}}>
                    <td className="col-md-1">(전)금주진행</td>
                    <td className="col-md-1">금주진행</td>
                  </tr>
                  <tr>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: lastReport.금주진행} }></p>
                    </td>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: report.금주진행} }></p>
                    </td>
                  </tr>
                  <tr style={{fontWeight: 'bold'}}>
                    <td className="col-md-1">(전)차주계획</td>
                    <td className="col-md-1">차주계획</td>
                  </tr>
                  <tr>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: lastReport.차주계획} }></p>
                    </td>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: report.차주계획} }></p>
                    </td>
                  </tr>
                  <tr style={{fontWeight: 'bold'}}>
                    <td className="col-md-1">(전)특이사항</td>
                    <td className="col-md-1">특이사항</td>
                  </tr>
                  <tr>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: lastReport.특이사항} }></p>
                    </td>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: report.특이사항} }></p>
                    </td>
                  </tr>
                  <tr style={{fontWeight: 'bold'}}>
                    <td className="col-md-1">(전)비고</td>
                    <td className="col-md-1">비고</td>
                  </tr>
                  <tr>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: lastReport.비고} }></p>
                    </td>
                    <td className="col-md-1">
                      <p style={{width: 100+'%', height: 'auto', whiteSpace: 'breakSpaces', wordWrap: 'break-word'}} dangerouslySetInnerHTML={ {__html: report.비고} }></p>
                    </td>
                  </tr>
                </Table>
              </>
            }
            {inModifying ?
              <>
                <button className="btn btn-primary" onClick={onClickUpdate}>등록</button>
                <button className="btn btn-primary" onClick={() => setInModifying(false)}>취소</button>
              </>
              :
              <>
                {!report.최종확인 ? 
                  <>
                    <button className="btn btn-primary" onClick={onClickConfirm}>최종확인</button>
                    <button className="btn btn-primary" onClick={() => setInModifying(true)}>수정</button>
                    <button className="btn btn-primary" onClick={onClickDelete}>삭제</button>
                  </> : null}
                {/* <button className="btn btn-primary" onClick={onClickConfirm}>최종확인</button> */}
              </>
            }
          </Widget>
        </Col>
      </Row>
    </>
  );
};

export default DetailWeekly;
