import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Row, Table } from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import { Link } from 'react-router-dom';
import './wbsP.scss';

const WBSP = () => {
  const [projects , setProjects] = useState([]);
  const columns = [ "팀(수주)", "팀(매출)", "프로젝트 명", "고객사", "PM"];
  
  const compareAscSu = (a, b) => a.팀수주 < b.팀수주 ? -1 : a.팀수주 > b.팀수주 ? 1 : 0;
  const compareDescSu = (a, b) => b.팀수주 < a.팀수주 ? -1 : b.팀수주 > a.팀수주 ? 1 : 0;
  const compareAscMa = (a, b) => a.팀매출 < b.팀매출 ? -1 : a.팀매출 > b.팀매출 ? 1 : 0;
  const compareDescMa = (a, b) => b.팀매출 < a.팀매출 ? -1 : b.팀매출 > a.팀매출 ? 1 : 0;

  const sortList = (e) => {
    let arrayCopy = [...projects];
    switch (e.target.name) {
      case "ascSu":
        arrayCopy.sort(compareAscSu);
        break;
      case "descSu":
        arrayCopy.sort(compareDescSu);
        break;
      case "ascMa":
        arrayCopy.sort(compareAscMa);
        break;
      case "descMa":
        arrayCopy.sort(compareDescMa);
        break;
      default:
        return;
    }
    setProjects(arrayCopy);
  };

  useEffect(()=> findAllProjects(), []);

  const findAllProjects = () => {
    axios.get(`http://${process.env.REACT_APP_API_URL}/projects`)
    .then((res)=>{
           setProjects(res.data);
           console.log(res.data);
        })
 }

  return (
    <>
      <Row>
        <Col>
          <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>WBS</p>}>
            <Table striped hover responsive>
              <thead>
                <tr>
                  <th>{columns[0]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascSu" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descSu" onClick={sortList}/></th>
                  <th>{columns[1]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascMa" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descMa" onClick={sortList}/></th>
                  <th>{columns[2]}</th>
                  <th>{columns[3]}</th>
                  <th>{columns[4]}</th>
                  {/* <th>{columns[5]}</th> */}
                </tr>
              </thead>
              <tbody>
                {projects.map((project) => {
                  return(
                  <tr key={project.id}>
                    <td>{project.팀수주}</td>
                    <td>{project.팀매출}</td>
                    <td>
                      <Link to={{pathname: `/app/projects/wbs/${project.id}`}}>
                        {project.프로젝트명}
                      </Link>
                    </td>
                    <td>{project.고객사}</td>
                    <td>{project.PM}</td>
                  </tr>
                )})}
              </tbody>
            </Table>
          </Widget>
        </Col>
      </Row>
    </>
  );
}

export default WBSP;
