import React from 'react';
import {
    Row,
    Col,
    Table,
    Progress,
    Button,
    UncontrolledButtonDropdown,
    DropdownMenu,
    DropdownToggle,
    DropdownItem,
    Alert,
    Input,
    Label,
    Badge
  } from "reactstrap";
import Widget from "../../components/Widget";
import axios from "axios";
import "./Member.modules.scss";


/* class Member extends React.Component{
     
    constructor(props){
      super(props);

      this.state={
        teams:"",
        members:"", 
        num:"",
        id:"",
        password:"",
        name:"",
        team:"",
        division:"",
        ranks:"",
        authority:"",
        updateEdit :[],
        onEdit : this.onEdit,
        onDelete : this.onDelete,
        updateValue : this.updateValue,
        onSave : this.onSave,
        goAlert : this.goAlert,
      };
    }

    getRecord = (num) => {
      const product = this.state.members.find(item => item.num === num);
      return product;
    }

    onEdit = (num) =>{
      const tempProduct = this.state.members;
      const index  = tempProduct.indexOf(this.getRecord(num));
      const selectedRecord = tempProduct[index];
      this.setState({
        num : selectedRecord['num'],
        id : selectedRecord['id'],
        password : selectedRecord['password'],
        name : selectedRecord['name'],
        division : selectedRecord['division'],
        team : selectedRecord['team'],
        ranks : selectedRecord['ranks'],
        authority : selectedRecord['authority'],
      })
    }

    updateValue= (e,test) =>{
      if(test === "id"){
        this.state.id = e.target.value;
      }
      if(test === "password"){
        this.state.password = e.target.value;
      }
