import React, {useState, useEffect} from "react";
import axios from "axios";
import { useCookies } from "react-cookie";
import { checkPropTypes } from "prop-types";

const Organization = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [teams, setTeams] = useState([]);
  const [members, setMembers] = useState([]);
  let chief = "";

  useEffect(() => findAllInfo(), []);

  const findAllInfo = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/members`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/teams`),
    ])
    .then(axios.spread((res1, res2) => {
      setMembers(res1.data);
      chief = res1.data.find(member => member.직책 == '실장')
      setTeams(res2.data.filter(team => team.year === new Date().getFullYear()));
    }))
    .catch(err => {
      console.log(err);
      // if (cookies.token) {
      //   removeCookie('token');
      // }
      // alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      // window.location.href = "/";
    });
  };

  return (
    <div>
      <h1>미래차검증전략실</h1>
      <div>
        <h2>{cheif ? chief.name : null}</h2>
        <ul>
          {teams.map(team => (
            <li key={team.num}>
              <h3>{team.teamname}</h3>
              {members
              .filter(member => member.team === team.teamname)
              .map(member => (
                <div key={member.num}>
                  <p>{member.name}</p>
                  <p>{member.position}</p>
                </div>
              ))}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Organization;
