import React from "react";
import { Row, Col } from "reactstrap";
import Form from "reactstrap/lib/Form";
import Widget from "../../components/Widget/Widget";
import styles from "./organization.module.scss";
import { useState, useEffect } from 'react';
import axios from "axios";
import PropTypes from "prop-types";


class organizationF extends React.Component {


  constructor(props) {
    super(props);

    this.state = {
     Fteam1: "", 
     Fteam2: "",
     Fteam3: "", 
     Fteam4: "", 
     Fteam5: "", 
     Fteam6: "", 
    };
  }


    componentDidMount(){
      this.setState({ status: true });

      this.findBody();
      this.findEvSys();
      this.findSimulation();
      this.findHEV();
      this.findHEVTest();
      this.findIntern();
    }

    componentWillUnmount() {
      this.setState({ status: false });
    }

    findBody() {
       axios.get(`http://${process.env.REACT_APP_API_URL}/Fteam1`)
       .then(res=>res.data)
        .then((data)=>{
          this.setState({Fteam1:data});
    });
  }

findEvSys() {
    axios.get(`http://${process.env.REACT_APP_API_URL}/Fteam2`)
    .then(res=>res.data)
     .then((data)=>{
       this.setState({Fteam2:data});
 });
}

findSimulation() {
  axios.get(`http://${process.env.REACT_APP_API_URL}/Fteam3`)
  .then(res=>res.data)
   .then((data)=>{
     this.setState({Fteam3:data});
});
}

findHEV() {
  axios.get(`http://${process.env.REACT_APP_API_URL}/Fteam4`)
  .then(res=>res.data)
   .then((data)=>{
     this.setState({Fteam4:data});
});
}

findHEVTest() {
  axios.get(`http://${process.env.REACT_APP_API_URL}/Fteam5`)
  .then(res=>res.data)
   .then((data)=>{
     this.setState({Fteam5:data});
});
}

findIntern() {
  axios.get(`http://${process.env.REACT_APP_API_URL}/Fteam6`)
  .then(res=>res.data)
   .then((data)=>{
     this.setState({Fteam6:data});
});
}




   
    
    render() {
      const { isOpen, close } = this.props;
      return (
        <div className="pricingTable">
        <h2 className="pricingTable-title">Automotive시스템검증실</h2>
        <h3 className="pricingTable-subtitle">정은선 실장</h3>
        
        <ul className="pricingTable-firstTable">
          <li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">바디샤시시스템검증팀</h1>
            {this.state.Fteam1 ? this.state.Fteam1.map(c => {
              if(c.직위 === '책임' && c.이름 === '최인석')
                return(
                  <p className="pricingTable-firstTable_table__pricing"><span>{c.이름}</span><span>팀장</span></p>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Fteam1 ? this.state.Fteam1.map(c => {
              if(c.직위 === '수석' || c.직위 === '책임')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Fteam1 ? this.state.Fteam1.map(c => {
              if(c.직위 === '선임')
                return(
                <li className="li_second">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
              {this.state.Fteam1 ? this.state.Fteam1.map(c => {
              if(c.직위 === '전임')
                return(
                <li className="li_third">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
               </ul>

               
          </li>

          <li className="pricingTable-firstTable_table">
            <h1 className="pricingTable-firstTable_table__header">EV시스템검증팀</h1>
            {this.state.Fteam2 ? this.state.Fteam2.map(c => {
              if(c.직위 === '선임' && c.이름 === '조규현')
                return(
                  <p className="pricingTable-firstTable_table__pricing"><span>{c.이름}</span><span>팀장</span></p>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
                  );
              })  :""}
            {/* <p className="pricingTable-firstTable_table__pricing"><span>권민혁</span><span>팀장</span></p> */}
            <ul className="pricingTable-firstTable_table__options">
            {this.state.Fteam2 ? this.state.Fteam2.map(c => {
              if(c.직위 === '책임' || c.직위 === '수석')
                return(
                <li className="li_first">{c.이름}({c.직위})</li>
                 // 위에 선언한 state equips 의 모든 값을 반복문으로 리턴해옴 if문을 해준 이유는 data 를 get 하는 속도보다 코드 읽는 속도가 더 빠르기 때문에 debug를 위해
