import React, { PureComponent } from 'react';
import {
  Row,
  Col,
} from 'reactstrap';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official'
import exporting from 'highcharts/modules/exporting';
import exportData from 'highcharts/modules/export-data';
import accessibility from 'highcharts/modules/accessibility';
import highchartsGantt from "highcharts/modules/gantt";
import Widget from '../../../components/Widget';
import chartsData from './mock';

exporting(Highcharts);
exportData(Highcharts);
accessibility(Highcharts);
highchartsGantt(Highcharts);

class HighChartsPage extends PureComponent {

  render() {
    return (
      <div>
        <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>WBS</p>}>
            <HighchartsReact
            highcharts={Highcharts}
            constructorType={"ganttChart"}
            options={{
              title: {
                text: '프로젝트 명'
              },
                yAxis: {
                  type: 'category',
                  grid: {
                    cellHeight: 300,
                    columns: [{
                      title: {
                        text: '구분',
                      },
                      labels: {
                        format: '{point.resource}'
                      }
                    }, {
                      title: {
                        text: '작업',
                      },
                      labels: {
                        format: '{point.name}'
                      }
                    }, {
                      title: {
                        text: '담당자',
                      },
                      labels: {
                        format: '{point.담당자}'
                        // formatter: function() {
                        //   var point = this.point,
                        //     days = (1000 * 60 * 60 * 24),
                        //     number = (point.end - point.start) / days;
                        //   return Math.round(number * 100) / 100;
                        // }
                      }
                    }]
                  }
                },
                series: [{
                  // name: 'Project 1',
                  data: [ //data 들어오는곳
                  {
                    id: 'resource1', //resource
                    // start: Date.UTC(2014, 10, 18),
                    // end: Date.UTC(2014, 10, 25),
                    resource: '구분1',
                    color: '#3a5d96',
                    pointWidth: 0, //resource에 꼭 필요
                    y: 0
                  }, {
                    name: '1-1작업1', //sub
                    start: Date.UTC(2014, 1, 14),
                    end: Date.UTC(2014, 10, 20),
                    resource: '',
                    담당자: 'jin',
                    color: '#3a5d96',
                    y: 1
                  }, {
                    name: '1-1작업2',
                    start: Date.UTC(2014, 10, 27),
                    end: Date.UTC(2014, 10, 29),
                    resource: '',
                    담당자: 'jin',
                    color: '#3a5d96',
                    y: 1
                  }, {
                    start: Date.UTC(2014, 10, 20),
                    end: Date.UTC(2014, 10, 25),
                    resource: 'Richards',
                    담당자: '',
                    color: '#497248',
                    pointWidth: 0,
                    y: 2
                  }, {
                    name: 'Run acceptance tests',
                    start: Date.UTC(2014, 10, 23),
                    end: Date.UTC(2014, 10, 26),
                    resource: '',
                    담당자: 'Lorem ',
                    color: '#497248',
                    y: 3
                  }, {
                    name: 'Test prototype1',
                    start: Date.UTC(2014, 10, 27),
                    end: Date.UTC(2014, 10, 29),
                    resource: '',
                    담당자: 'Lorem ',
                    parent: 'Start prototype',
                    color: '#497248',
                    y: 4
                  }
                ]
              }],
              navigator: {
                enabled: true,
                liveRedraw: true,
                series: {
                  type: 'gantt',
                  pointPlacement: 0.5,
                  pointPadding: 0.25
                },
              },
              rangeSelector: { //1개월, 3개월, 6개월, 1년 바꿀수 있음
                enabled: true,
                selected: 0,
                inputDateFormat: '%Y-%m-%d',
                buttons: [{
                  type: 'month',
                  count: 1,
                  text: '1m',
                  title: '1개월 보기'
                }, {
                  type: 'month',
                  count: 3,
                  text: '3m',
                  title: '3개월 보기'
                }, {
                  type: 'month',
                  count: 6,
                  text: '6m',
                  title: '6개월 보기'
                }, {
                  type: 'ytd',
                  text: 'YTD',
                  title: '연초부터 현재까지'
                }, {
                  type: 'year',
                  count: 1,
                  text: '1y',
                  title: '1년 보기'
                }, {
                  type: 'all',
                  text: 'All',
                  title: '전체보기'
                }]
              },
            }} />
          </Widget>
      </div>
    );
  }
}

export default HighChartsPage;
