import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Switch, Route, withRouter, Redirect } from "react-router";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import Hammer from "rc-hammerjs";
import { toast } from "react-toastify";

import Header from "../Header";
import Sidebar from "../Sidebar";
import {
  openSidebar,
  closeSidebar,
  toggleSidebar,
} from "../../actions/navigation";
import s from "./Layout.module.scss";

// pages
import Dashboard from "../../pages/dashboard";
import Typography from "../../pages/typography";
import Tables from "../../pages/tables";
import Notifications from "../../pages/notifications/Notifications";
import Maps from "../../pages/maps";
import Icons from "../../pages/icons";
import Charts from "../../pages/charts";

// 마이페이지, 관리자페이지
import MyPage from "../../pages/myPage/MyPage";
import Manages from "../../pages/management";
// 프로젝트 - 목록
import ProjectList from "../../pages/projectList/ProjectList"
import NewProject from "../../pages/newProject/NewProject";
import DetailProject from "../../pages/detailProject/DetailProject";
// 프로젝트 - WBS
import WBSP from "../../pages/project/wbsP";
import wbs from "../../pages/project/wbs";
// 프로젝트 - 주간보고
import ReportList from "../../pages/reportList/ReportList";
import NewReport from "../../pages/newReport/NewReport";
import DetailReport from "../../pages/detailReport/DetailReport";
// 프로젝트 - 회의록
import meeting from "../../pages/meeting/meeting";
import NMeeting from "../../pages/meeting/Nmeeting";
import DMeeting from "../../pages/meeting/Dmeeting";
// 수주매출
import orderssales from "../../pages/orderssales";
// 교육관리
import Education from "../../pages/education/Education";
import EducationDetail from "../Education/EducationDetail.js"
// 자산관리
import Equips from "../../pages/equipment";
// 관리자스케줄
import ManagerSchedule from "../../pages/managerSchedule/ManagerSchedule";
import NewSchedule from "../../pages/newSchedule/NewSchedule";
import NewScheduleWeek from "../../pages/newScheduleWeek/NewScheduleWeek";
import DetailSchedule from "../../pages/detailSchedule/DetailSchedule";
// 조직도
import organizationCar from "../../pages/organizationCar/organizationCar"
import organizationA from "../../pages/OrganizationA/organizationA"
import organizationB from "../../pages/organizationB/organizationB"
import organizationD from "../../pages/organizationD/organizationD"
import organizationE from "../../pages/organizationE/organizationE"
import organizationF from "../../pages/organizationF/organizationF"
import organizationG from "../../pages/organizationG/organizationG"
import organizationH from "../../pages/organizationH/organizationH"
import organizationI from "../../pages/organizationI/organizationI"
import organizationJ from "../../pages/organizationJ/organizationJ"
import Organization_chart from "../../pages/organization_chart/organization_chart"

import Organization from "../../pages/organization/Organization";

class Layout extends React.Component {
  static propTypes = {
    sidebarStatic: PropTypes.bool,
    sidebarOpened: PropTypes.bool,
    dispatch: PropTypes.func.isRequired,
  };

  static defaultProps = {
    sidebarStatic: true,
    sidebarOpened: true,
  };

  constructor(props) {
    super(props);

    this.handleSwipe = this.handleSwipe.bind(this);
    this.handleCloseSidebar = this.handleCloseSidebar.bind(this);
  }

  componentDidMount() {
    this.handleResize();
    window.addEventListener("resize", this.handleResize.bind(this));
    toast.warn(<div style={{fontSize: 0.8+'rem'}}>세션 만료로 로그아웃이라는 알림이 뜨거나,<br/>오류사항이 있으면<br/>라인 아이디: shchoi_ 로 연락주세요.</div>, {
      position: "top-right",
      autoClose: false,
      closeOnClick: true,
      pauseOnHover: false,
      draggable: true
    }); 
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.handleResize.bind(this));
  }

  handleResize() {
    if (window.innerWidth <= 768) {
      this.props.dispatch(toggleSidebar());
    } else if (window.innerWidth >= 768) {
      this.props.dispatch(openSidebar());
    }
  }

  handleCloseSidebar(e) {
    if (e.target.closest("#sidebar-drawer") == null && this.props.sidebarOpened && window.innerWidth <= 768) {
      this.props.dispatch(toggleSidebar());
    }
  }

  handleSwipe(e) {
    if ("ontouchstart" in window) {
      if (e.direction === 4) {
        this.props.dispatch(openSidebar());
        return;
      }

      if (e.direction === 2 && this.props.sidebarOpened) {
        this.props.dispatch(closeSidebar());
        return;
      }
    }
  }

  render() {
    return (
      <div
        className={[
          s.root,
          !this.props.sidebarOpened ? s.sidebarClose : "",
          "flatlogic-one",
          "dashboard-light",
        ].join(" ")}
        onClick={e => this.handleCloseSidebar(e)}
      >
        <Sidebar />
        <div className={s.wrap}>
          <Header />

          <Hammer onSwipe={this.handleSwipe}>
            <main className={s.content}>
              <TransitionGroup>
                <CSSTransition
                  key={this.props.location.key}
                  classNames="fade"
                  timeout={200}
                >
                  <Switch>
                    {/* 메인 페이지 -> 대시보드로 리다이렉트 */}
                    {/* <Route
                      path="/app/main"
                      exact
                      render={() => <Redirect to="/app/schedule" />}
                    /> */}

                    {/* -------샘플 페이지들------- */}
                    {/* 대시보드 페이지 -> 메인 */}
                    <Route
                      path="/app/main/dashboard"
                      exact
                      component={Dashboard}
                    />
                    <Route path={"/app/typography"} component={Typography} />
                    <Route path={"/app/tables"} component={Tables} />
                    <Route path={"/app/ui/notifications"} component={Notifications}/>
                    <Route path={"/app/ui/maps"} component={Maps} />
                    <Route path={"/app/ui/icons"} component={Icons} />
                    <Route path={"/app/ui/charts"} component={Charts} />

                    {/* 마이페이지, 관리자페이지 */}
                    <Route path={"/app/myPage"} component={MyPage} />
                    <Route path={"/app/manages"} component={Manages} />

                    {/* 프로젝트 - 목록 */}
                    <Route path={"/app/projects/list"} exact component={ProjectList} />
                    <Route path={"/app/projects/list/new"} component={NewProject}/>
                    <Route path={"/app/projects/list/:id/:year"} component={DetailProject} />

                    {/* 프로젝트 - WBS */}
                    <Route path={"/app/projects/wbs"} exact component={WBSP}/>
                    <Route path={"/app/projects/wbs/:id"} component={wbs} />

                    {/* 프로젝트 - 주간보고 */}
                    <Route path={"/app/projects/reports"} exact component={ReportList}/>
                    <Route path={"/app/projects/reports/new"} component={NewReport}/>
                    <Route path={"/app/projects/reports/:pid/:id/:year"} component={DetailReport}/>

                    {/* 프로젝트 - 회의록 */}
                    <Route path={"/app/projects/meetings"} exact component={meeting}/>
                    <Route path={"/app/projects/meetings/new"} component={NMeeting}/>
                    <Route path={"/app/projects/meetings/detail/:id"} component={DMeeting}/>

                    {/* 수주매출 */}
                    <Route path={"/app/orderssales"} component={orderssales} />

                    {/* 교육관리 */}
                    <Route exact path={"/app/education"} component={Education} />
                    <Route path={"/app/education/:team_name+"} component={EducationDetail} />

                    {/* 자산관리 */}
                    <Route path={"/app/equipments"} component={Equips} />

                    {/* 관리자스케줄 */}
                    <Route path={"/app/schedule"} exact component={ManagerSchedule} />
                    <Route path={"/app/schedule/add"} exact component={NewSchedule} />
                    <Route path={"/app/schedule/add/week"} component={NewScheduleWeek} />
                    <Route path={"/app/schedule/:id"} component={DetailSchedule} />

                    {/* 조직도 */}
                    <Route path={"/app/organizationCar"} component={organizationCar} />
                    {/* <Route path={"/app/organization"} component={Organization_chart} /> */}
                    <Route path={"/app/organizationA"} component={organizationA} />
                    <Route path={"/app/organizationB"} component={organizationB} />
                    <Route path={"/app/organizationD"} component={organizationD} />
                    <Route path={"/app/organizationE"} component={organizationE} />
                    <Route path={"/app/organizationF"} component={organizationF} />
                    <Route path={"/app/organizationG"} component={organizationG} />
                    <Route path={"/app/organizationH"} component={organizationH} />
                    <Route path={"/app/organizationI"} component={organizationI} />
                    <Route path={"/app/organizationJ"} component={organizationJ} />
                    <Route path={"/app/organization"} component={Organization} />

                  </Switch>
                </CSSTransition>
              </TransitionGroup>
            </main>
          </Hammer>
        </div>
      </div>
    );
  }
}

function mapStateToProps(store) {
  return {
    sidebarOpened: store.navigation.sidebarOpened,
    sidebarStatic: store.navigation.sidebarStatic,
  };
}

export default withRouter(connect(mapStateToProps)(Layout));
