import React, {createContext, useState, useEffect, useMemo } from 'react';
import axios from 'axios';

const UserContext = createContext();
const UserContextProvider = ({children}) => {
  const [userInfo,setUserInfo] = useState({
    num: "",
    id: "",
    division : "",
    team : "",
    name : "",
    ranks : "",
    position : "",
    authority : "",
  });
  const value = useMemo(() => ({
    userInfo,
    setUserInfo,
  }), [userInfo]);

  useEffect(() => {
    if (localStorage.getItem('id') && localStorage.getItem('authenticated')) {
      alert("또?");
      axios.post(`http://${process.env.REACT_APP_API_URL}/login`,
      {id: localStorage.getItem('id')}).then((response)=>{
        setUserInfo({
          num: response.data[0].num,
          id: response.data[0].id,
          division: response.data[0].division,
          team: response.data[0].team,
          name: response.data[0].name,
          ranks: response.data[0].ranks,
          position: response.data[0].position,
          authority: response.data[0].authority,
        });
      })
      .catch(err => {
        console.log(err);
      });
    }
  }, [userInfo]);

  return <UserContext.Provider
          value={{
              userInfo,
              setUserInfo
          }}
  >
      {children}
  </UserContext.Provider>
}

export {UserContext, UserContextProvider};
