import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Row, Table } from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import { Link } from 'react-router-dom';
import './wbsP.scss';
import { useCookies } from 'react-cookie';

const WBSP = () => {
  const authority = JSON.parse(localStorage.getItem("userInfo")) ? JSON.parse(localStorage.getItem("userInfo")).authority : "";
  const [cookies, removeCookie ] = useCookies(['token']);
  const [projects , setProjects] = useState([]);
  const [members , setMembers] = useState([]);

  const sortList = (e) => {
    let arrayCopy = [...projects];
    switch (e.target.name) {
      case "ascSu":
        arrayCopy.sort((a, b) => a.팀수주.localeCompare(b.팀수주));
        break;
      case "descSu":
        arrayCopy.sort((a, b) => b.팀수주.localeCompare(a.팀수주));
        break;
      case "ascMa":
        arrayCopy.sort((a, b) => a.팀매출.localeCompare(b.팀매출));
        break;
      case "descMa":
        arrayCopy.sort((a, b) => b.팀매출.localeCompare(a.팀매출));
        break;
      default:
        return;
    }
    setProjects(arrayCopy);
  };

  useEffect(()=> findAllProjects(), []);

  const findAllProjects = () => {
    if(authority == 0 || authority == 1){
      axios.defaults.headers.common['Authorization'] = cookies.token;
      axios
      .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/projects`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/members`)])
      .then(axios.spread((res1,res2)=>{
        setProjects(res1.data);
        setMembers(res2.data);
      }))
      .catch(err => {
        if (cookies.token) {
          removeCookie('token');
        }
        alert("세션이 만료되었습니다. 다시 로그인 해주세요");
        window.location.href = "/";
      });
    }
 }

  return (
    <>
      <Row>
        <Col>
          {authority == 0 || authority == 1 ?
            <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>WBS</p>}>
              <Table className="meeting__table table-md">
                <thead>
                  <tr>
                    <th>
                      <div>
                        <div>
                          팀(수주)
                        </div>
                        <div>
                          <button className="orderBtn glyphicon glyphicon-collapse" name="ascSu" onClick={sortList}/>
                          <button className="orderBtn glyphicon glyphicon-collapse-top" name="descSu" onClick={sortList}/>
                        </div>
                      </div>
                    </th>
                    <th>
                      <div>
                        <div>
                          팀(매출)
                        </div>
                        <div>
                          <button className="orderBtn glyphicon glyphicon-collapse" name="ascMa" onClick={sortList}/>
                          <button className="orderBtn glyphicon glyphicon-collapse-top" name="descMa" onClick={sortList}/>
                        </div>
                      </div>
                    </th>
                    <th>프로젝트 명</th>
                    <th>고객사</th>
                    <th>PM</th>
                  </tr>
                </thead>
                <tbody>
                  {projects.map((project) => {
                    const member = members.find(({id}) => id === project.PM);
                    return(
                    <tr key={project.id}>
                      <td>{project.팀수주}</td>
                      <td>{project.팀매출}</td>
                      <td>
                        <Link to={{pathname: `/app/projects/wbs/${project.id}`}}>
                          {project.프로젝트명}
                        </Link>
                      </td>
                      <td>{project.고객사}</td>
                      <td>{member ? member.name : project.PM}</td>
                    </tr>
                  )})}
                </tbody>
              </Table>
            </Widget>
          :
            <Widget title={
              <p style={{ fontSize:30, fontWeight: 700 }}>권한이 없습니다</p>
            }>
            </Widget>
          }
        </Col>
      </Row>
    </>
  );
}

export default WBSP;
