import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Row, Table } from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import moment from 'moment';
import { useCookies } from 'react-cookie';

const ProjectList = () => {
  const authority = JSON.parse(localStorage.getItem("userInfo")) ? JSON.parse(localStorage.getItem("userInfo")).authority : "";
  const [cookies, removeCookie ] = useCookies(['token']);
  const [saveDate , setSaveDate] = useState(moment(new Date()).format("YYYY"));
  const [projects , setProjects] = useState([]);
  const [teams , setTeams] = useState([]);
  let sumgfs=0; let sumglo=0; let sumgls=0; //상하반기 목표수주 목표매출 
  // let sumgfo = 0; let sumgfs=0; let sumglo=0; let sumgls=0; //상하반기 목표수주 목표매출 
  let exfo = 0; let sumexfo = 0; // 상반기예상수주
  let fo=0; let sumfo = 0; // 상반기수주
  let exfs = 0; let sumexfs = 0; // 상반기예상매출
  let fs=0; let sumfs = 0; // 상반기매출
  let exlo = 0; let sumexlo = 0; // 하반기예상수주
  let lo=0; let sumlo = 0; // 하반기수주
  let exls = 0; let sumexls = 0; // 하반기예상매출
  let ls=0; let sumls = 0; // 하반기매출

  useEffect(()=> findAllorders(), []);

  const findAllorders = () => {
    if(authority == 1){
      axios.defaults.headers.common['Authorization'] = cookies.token;
      axios
      .all([axios.get(`http://${process.env.REACT_APP_API_URL}/projects`),axios.get(`http://${process.env.REACT_APP_API_URL}/teams`) ])
      .then(axios.spread((res1,res2)=>{
        setProjects(res1.data);
        res2.data.sort((a,b)=>a.num - b.num);
        setTeams(res2.data);
        })
      )
      .catch(err => {
        if (cookies.token) {
          removeCookie('token');
        }
        alert("세션이 만료되었습니다. 다시 로그인 해주세요");
        window.location.href = "/";
      });
    }
  }

  const getGoals = (term, type) => {
    const _teams = teams.filter(team => team.year == saveDate)
    let data = [];
    let sum = 0;
    if(term){
      _teams.map((team, idx) => {
        sum += team["first" + type];
        data.push(<td key={idx}>{team["first" + type].toFixed(2)}</td>);
      })
    }else{
      _teams.map((team, idx) => {
        sum = sum + team["first" + type] + team["last" + type]
        data.push(<td key={idx}>{(team["first" + type] + team["last" + type]).toFixed(2)}</td>);
      })
    }
    data.unshift(<td>{sum.toFixed(2)}</td>)
    return data;
  };

  const getResult = (term, expect, type) => {
    const _teams = teams.filter(team => team.year == saveDate)
    let data = [];
    let totalsum = 0;
    if(term){
      _teams.map((team, idx) => {
        const teamsum = projects.filter(project => team.teamname == project["팀"+type] && saveDate==project.year)
        .reduce((acc, cur) => acc + cur["상반기"+expect+type], 0)
        totalsum += teamsum;
        data.push(<td key={idx}>{teamsum.toFixed(2)}</td>);
      })
    }else{
      _teams.map((team, idx) => {
        const teamsum = projects.filter(project => team.teamname == project["팀"+type] && saveDate==project.year)
        .reduce((acc, cur) => acc + cur["상반기"+expect+type] + cur["하반기"+expect+type], 0)
        totalsum += teamsum;
        data.push(<td key={idx}>{teamsum.toFixed(2)}</td>);
      })
    }
    data.unshift(<td>{totalsum.toFixed(2)}</td>)
    return data;
  };

