            <br />
              <Table striped hover>
                <thead>
                  <tr>
                    <th colSpan={teams.filter(team => team.year == saveDate).length+4}>상세내역단위 (백만)</th>
                  </tr>
                  <tr>
                    <th>구분</th>
                    <th>항목</th>
                    <th>TOTAL</th>
                    {teams.map((team, idx) => {
                      if(team.year == saveDate){
                        return(
                          <th key={idx}>{team.teamname}</th> 
                    )}})}
                  </tr>
                </thead>
                <tbody>
                  {/* 상반기 부분 */}
                  <tr>
                    <td rowSpan='6' style={{ color: "orange" , fontWeight:"bold", textAlign: "center" }} > 상반기 </td>
