                      )
                    }})}
                    <td>{sumexlo.toFixed(2)}</td>
                  </tr>
                  <tr>
                    <td>수주달성</td>
                    {teams.map((team, idx) => {
                      if(team.year == saveDate){
                      {projects.map((project) => {
                        if(team.teamname == project.팀수주 && saveDate==project.year){
                          lo+=project.하반기수주 + project.상반기수주;
                        }  
                      })}
                      const result = lo + fo;
                      sumlo += lo;
                      lo=0;
                      sumfo = 0;
                      return(
                        <td key={idx}>{result.toFixed(2)}</td>
                      )
                    }})}
                    <td>{sumlo.toFixed(2)}</td>
                  </tr>
                  <tr>
                    <td>목표매출</td>
                    {getGoals(false, "sale")}
                  </tr>
                  <tr>
                    <td>예상매출</td>
                    {teams.map((team, idx) => {
                      if(team.year == saveDate){
                      {projects.map((project) => {
                        if(team.teamname == project.팀매출 && saveDate==project.year){
                          exls+=project.하반기예상매출 + project.상반기예상매출;
                        }  
                      })}
                      const result= exls + exfs;
                      sumexls += exls;
                      exls=0;
                      sumexfs = 0;
                      return(
                        <td key={idx}>{result.toFixed(2)}</td>
                      )
                    }})}
                    <td>{sumexls.toFixed(2)}</td>
                  </tr>
                  <tr>
                    <td style={{ color: "red" , fontWeight:"bold" }}>매출달성</td>
                    {teams.map((team, idx) => {
                      if(team.year == saveDate){
                      {projects.map((project) => {
                        if(team.teamname == project.팀매출 && saveDate==project.year){
                          ls+=project.하반기매출;
                          ls+=project.상반기매출;
                        }  
                      })}
                      const result = ls;
                      sumls += ls;
                      ls=0;
                      return(
                        <td key={idx} style={{ color: "red" , fontWeight:"bold" }}>{result.toFixed(2)}</td>
                      )
                    }})}
                    <td style={{ color: "red" , fontWeight:"bold" }}>{sumls.toFixed(2)}</td>
                  </tr>
                </tbody>
              </Table>
            </Widget>
          :
            <Widget title={
              <p style={{ fontSize:30, fontWeight: 700 }}>권한이 없습니다</p>
            }>
            </Widget>
          }
        </Col>
      </Row>
    </>
  );
}

