  },
  {
    id: 3,
    name: "미국",
  },
  {
    id: 4,
    name: "기타 해외",
  }
];

const Detail = (props) => {
  const columns = [ "팀(수주)", "팀(매출)", "프로젝트코드", "프로젝트 명", "상태", "실", "고객사","고객부서","ManMonth","프로젝트계약금액_백만","상반기예상수주","상반기수주","상반기예상매출"
  ,"상반기매출","연간예상수주","연간수주","연간 예상매출", "연간 매출","비용","착수","종료","고객담당자","근무지","업무","도구","과제성격","사업지역"
   ,"PM","투입명단"];
  const [project, setProject] = useState([]);
  const [tools , setTools] = useState([]);
  const [teams , setTeams] = useState([]);

  const onChangeInput = (e) => {
    setProject({
      ...project,
      [e.target.id]: e.target.value
    });
  };

  const onClickUpdate = () => {
    const result = window.confirm("수정하시겠습니까?");
    if(result){
      axios.post(`http://${process.env.REACT_APP_API_URL}/projects/update`, {project: project})
      .then((response)=>{
        console.log(response);
      });
      window.location.reload();
    }
  };

  const onClickDelete = () => {
    const result = window.confirm("삭제하시겠습니까?");
    if (result) {
      axios.post(`http://${process.env.REACT_APP_API_URL}/projects/delete`, {id: project.id})
      .then((response)=>{
        console.log(response);
      }
      );
      window.location.href = "#/app/projects/list";
    }
  };

  useEffect(()=> findAllProjects(), []);

  const findAllProjects = () => {
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/projects/search?id=${props.match.params.id}&year=${props.match.params.year}`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/teams`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/tools`) ])
    .then(axios.spread((res1,res2,res3)=>{
           setProject(res1.data[0]);
           const _teams = res2.data.filter(d => d.year == moment(new Date()).format("YYYY"));
           setTeams(_teams);
           setTools(res3.data);
        })
    )
 }

  return (
    <div>
      <Row>
        <Col>
          <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>프로젝트 상세</p>}>
            <Table striped bordered responsive>
              <tbody>
                <tr style={{fontWeight: 'bold'}}>
                  <td>{columns[0]}</td>
                  <td>{columns[1]}</td>
                </tr>
                <tr>
                  <td>
                    <select className="form-control" id="팀수주" value={project.팀수주} onChange={onChangeInput}>
                      <option value="" disabled hidden></option>
                      {teams.map(team => <option key={team.num} value={team.teamname}>{team.teamname}</option>)}
                    </select>
                  </td>
                  <td>
                    <select className="form-control" id="팀매출" value={project.팀매출} onChange={onChangeInput}>
                      <option value="" disabled hidden></option>
                      {teams.map(team => <option key={team.num} value={team.teamname}>{team.teamname}</option>)}
                    </select>  
                  </td>
                </tr>
                <tr style={{fontWeight: 'bold'}}>
                  <td colSpan='2'>{columns[2]}</td>
                </tr>
                <tr>
                  <td colSpan='2'>
                    <input className="form-control" id="프로젝트코드" value={project.프로젝트코드} onChange={onChangeInput}/>  
                  </td>
                </tr>
                <tr style={{fontWeight: 'bold'}}>
                  <td colSpan='2'>{columns[3]}</td>
                </tr>
                <tr>
                  <td colSpan='2'>
                    <input type="text" className="form-control" id="프로젝트명" value={project.프로젝트명} onChange={onChangeInput}/>
                  </td>
                </tr>
                <tr>
                  <td>{columns[4]}</td>
                  <td>{columns[5]}</td>
                </tr>
                <tr>
                  <td>
                    <select className="form-control" id="상태" value={project.상태} onChange={onChangeInput}>
                      {statusData.map(status => <option key={status.id} value={status.name}>{status.name}</option>)}
                    </select>
                  </td>
                  <td>
                    <input className="form-control" id="실" value={project.실} onChange={onChangeInput}/>
                  </td>
                </tr>
                <tr>
                  <td>{columns[6]}</td>
                  <td>{columns[7]}</td>
                </tr>
                <tr>
                  <td><input className="form-control" id="고객사" value={project.고객사} onChange={onChangeInput}/></td>
                  <td><input className="form-control" id="고객부서" value={project.고객부서} onChange={onChangeInput}/></td>
                </tr>
                <tr>
                  <td colSpan='2'>{columns[8]}</td>
                </tr>
                <tr>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
                  <td colSpan='2'><input type="number" className="form-control" id="ManMonth" value={project.ManMonth} onChange={onChangeInput}/></td>
                </tr>
                <tr>
                  <td colSpan='2'>{columns[9]}</td>
                </tr>
                <tr>
                  <td colSpan='2'><input type="number" className="form-control" id="프로젝트계약금액_백만" value={project.프로젝트계약금액_백만} onChange={onChangeInput}/></td>
                </tr>
                <tr>
                  <td>{columns[10]}</td>
                  <td>{columns[11]}</td>
                </tr>
                <tr>
                  <td><input type="number" className="form-control" id="상반기예상수주" value={project.상반기예상수주} onChange={onChangeInput}/></td>
                  <td><input type="number" className="form-control" id="상반기수주" value={project.상반기수주} onChange={onChangeInput}/></td>
                </tr>
                <tr>
                  <td>{columns[12]}</td>
                  <td>{columns[13]}</td>
                </tr>
                <tr>
                  <td><input type="number" className="form-control" id="상반기예상매출" value={project.상반기예상매출} onChange={onChangeInput}/></td>
                  <td><input type="number" className="form-control" id="상반기매출" value={project.상반기매출} onChange={onChangeInput}/></td>
                </tr>
                <tr>
                  <td>{columns[14]}</td>
                  <td>{columns[15]}</td>
                </tr>
                <tr>
                  <td><input type="number" className="form-control" id="연간예상수주" value={project.연간예상수주} onChange={onChangeInput}/></td>
                  <td><input type="number" className="form-control" id="연간수주" value={project.연간수주} onChange={onChangeInput}/></td>
                </tr>
                <tr>
                  <td>{columns[16]}</td>
                  <td>{columns[17]}</td>
                </tr>
                <tr>
                  <td><input type="number" className="form-control" id="연간예상매출" value={project.연간예상매출} onChange={onChangeInput}/></td>
                  <td><input type="number" className="form-control" id="연간매출" value={project.연간매출} onChange={onChangeInput}/></td>
                </tr>
                <tr>
                  <td colSpan='2'>{columns[18]}</td>
                </tr>
                <tr>
                  <td colSpan='2'>
                    <div  style={{display: 'flex', alignItems:'center'}}>
                      장비비 : <input className="form-control" style={{ marginLeft: 7, marginRight: 15, width: 100 }} type="number"  id="장비비" value={project.장비비} onChange={onChangeInput}/>
                      컨설팅비 : <input className="form-control" style={{ marginLeft: 7, marginRight: 15, width: 100 }} type="number"  id="컨설팅비" value={project.컨설팅비} onChange={onChangeInput}/>
                      도구비 : <input className="form-control" style={{ marginLeft: 7, marginRight: 15, width: 100 }} type="number"  id="도구비" value={project.도구비} onChange={onChangeInput}/>
                    </div>
                  </td>
                </tr>
                <tr>
                  <td>{columns[19]}</td>
                  <td>{columns[20]}</td>
                </tr>
                <tr>
                  <td>
                  <Flatpickr
                    className="form-control"
                    value={project.착수}
                    options={
                      {dateFormat: "Y-m-d"}
                    }
                    onChange={착수 => {
                      setProject({...project, 착수: moment(new Date(착수[0])).format("YYYY-MM-DD")});
                    }}
                  />
                  </td>
                  <td>
                  <Flatpickr
                    className="form-control"
                    value={project.종료}
                    options={
                      {dateFormat: "Y-m-d"}
                    }
                    onChange={종료 => {
                      setProject({...project, 종료: moment(new Date(종료[0])).format("YYYY-MM-DD")});
                    }}
                  />
                  </td>
                </tr>
                <tr>
                  <td>{columns[21]}</td>
                  <td>{columns[22]}</td>
                </tr>
                <tr>
                  <td><input className="form-control" id="고객담당자" value={project.고객담당자} onChange={onChangeInput}/></td>
                  <td><input className="form-control" id="근무지" value={project.근무지} onChange={onChangeInput}/></td>
                </tr>
                <tr>
                  <td>{columns[23]}</td>
                  <td>{columns[24]}</td>
                </tr>
                <tr>
                  <td><input className="form-control" id="업무" value={project.업무} onChange={onChangeInput}/></td>
                  <td>
                    <select className="form-control" id="도구" value={project.도구} onChange={onChangeInput}>
                      <option value="" disabled hidden></option>
                      {tools.map(tool => <option key={tool.num} value={tool.toolname}>{tool.toolname}</option>)}
                    </select>
                  </td>
                </tr>
                <tr>
                  <td>{columns[25]}</td>
                  <td>{columns[26]}</td>
                </tr>
                <tr>
                  <td>
                    <select className="form-control" id="과제성격" value={project.과제성격} onChange={onChangeInput}>
                      <option value="" disabled hidden></option>
                      {characterData.map(character => <option key={character.id} value={character.name}>{character.name}</option>)}
                    </select>
                  </td>
                  <td>
                    <select className="form-control" id="사업지역" value={project.사업지역} onChange={onChangeInput}>
                      <option value="" disabled hidden></option>
                      {countryData.map(country => <option key={country.id} value={country.name}>{country.name}</option>)}
                    </select>
                  </td>
                </tr>
                <tr>
                  <td>{columns[27]}</td>
                  <td>{columns[28]}</td>
                </tr>
                <tr>
                  <td><input className="form-control" id="PM" value={project.PM} onChange={onChangeInput}/></td>{/* 조직도 DB 연동 */}
                  <td><input className="form-control" id="투입명단" value={project.투입명단} onChange={onChangeInput}/></td> {/* 조직도 DB 연동 */}
                </tr>
                <tr>
                  <td>주간보고서</td>
                  <td>실적보고</td>
                </tr>
                <tr>
                  <td>
                    <label style={{marginRight: 10}}>
                      <input type="radio" id="주간보고서" value="1" checked={project.주간보고서 == "1"} onChange={onChangeInput}/>
                      사용
                    </label>
                    <label>
                      <input type="radio" id="주간보고서" value="0" checked={project.주간보고서 == "0"} onChange={onChangeInput}/>
                      미사용
                    </label>
                  </td>
                  <td>
                    <label style={{marginRight: 10}}>
                      <input type="radio" id="실적보고" value="1" checked={project.실적보고 == "1"} onChange={onChangeInput}/>
                      사용
                    </label>
                    <label>
                      <input type="radio" id="실적보고" value="0" checked={project.실적보고 == "0"} onChange={onChangeInput}/>
                      미사용
                    </label>
                  </td>
                </tr>
              </tbody>
            </Table>
            <Button color="primary" style={{marginRight: 10}} onClick={onClickUpdate}>수정</Button>
            <Button color="primary" style={{marginLeft: 10}} onClick={onClickDelete}>삭제</Button>
          </Widget>
        </Col>
      </Row>
    </div>
  );
}

export default Detail;
