import axios from 'axios';
import moment from 'moment';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Row, Col, Table, UncontrolledButtonDropdown, DropdownMenu, DropdownToggle, DropdownItem} from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import './ReportList.scss';

const weekNumInfo = [
  {
    year: 2020,
    date: new Date(2020, 0, 1),
    weekNum: 52,
  },
  {
    year: 2021,
    date: new Date(2020, 11, 30),
    weekNum: 52,
  },
  {
    year: 2022,
    date: new Date(2021, 11, 29),
    weekNum: 53,
  },
  {
    year: 2023,
    date: new Date(2023, 0, 4),
    weekNum: 52,
  }
]

// weeknum 구하는 메서드 정의
Date.prototype.getWeek = function (dowOffset) {
  /*getWeek() was developed by Nick Baicoianu at MeanFreePath: http://www.meanfreepath.com */

  dowOffset = typeof(dowOffset) == 'number' ? dowOffset : 0; // dowOffset이 숫자면 넣고 아니면 0
  const newYear = new Date(this.getFullYear(),0,1);
  let day = newYear.getDay() - dowOffset; //the day of week the year begins on
  day = (day >= 0 ? day : day + 7);
  const daynum = Math.floor((this.getTime() - newYear.getTime() -
    (this.getTimezoneOffset()-newYear.getTimezoneOffset())*60000)/86400000) + 1;
  let weeknum;
  //if the year starts before the middle of a week
  if(day < 4) {
    weeknum = Math.floor((daynum+day-1)/7) + 1;
    if(weeknum > 52) {
      let nYear = new Date(this.getFullYear() + 1,0,1);
      let nday = nYear.getDay() - dowOffset;
      nday = nday >= 0 ? nday : nday + 7;
      /*if the next year starts before the middle of
        the week, it is week #1 of that year*/
      weeknum = nday < 4 ? 1 : 53;
    }
  }
  else {
    weeknum = Math.floor((daynum+day-1)/7);
  }
  if (weeknum === 0) {
    let tempWeeknum = new Date(this.setDate(new Date(this).getDate() - 7)).getWeek(3);
    weeknum = tempWeeknum + 1;
  }
  return weeknum;
};

const ReportList = () => {
  const [termYear, setTermYear] = useState(new Date().getFullYear());
  const [termWeek, setTermWeek] = useState(new Date().getWeek(3));
  const yearOptions = weekNumInfo.map(info => info.year).filter((year) => year < new Date().getFullYear() + 1).sort((a, b) => b - a);
  const [projects, setProjects] = useState([]);
  const [reports, setReports] = useState([]);
  const [thisReport, setThisReport] = useState([]);
  const [thisProjects, setThisProjects] = useState([]);


  const onChangeTerm = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "termYear":
        return (setTermYear(value));
      case "termWeek":
        return (setTermWeek(value));
    }
  };

  const weekNumOptions = () => { // 주차 옵션
    const weekNum = [];
    const targetYear = weekNumInfo.find(info => info.year == termYear);
    const maxWeekNum = targetYear.weekNum;
    let startDate = new Date(targetYear.date);
    let endDate = new Date(targetYear.date);
    endDate.setDate(endDate.getDate() + 6);
    for (let i = 1; i <= maxWeekNum; i++) {
      weekNum.push(<option key={i} value={i}>{i}주차 ({startDate.toLocaleDateString()}~{endDate.toLocaleDateString()})</option>);
      startDate.setDate(startDate.getDate() + 7);
      endDate.setDate(endDate.getDate() + 7);
    }
    return weekNum;
  }

  useEffect(()=> findAllInfos(), []);

  const findAllInfos = () => {
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/projects`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/reports`)])
    .then(axios.spread((res1,res2)=>{
      const _projects = res1.data.filter((project) => project.주간보고서 == "1")
      setProjects(_projects);
      setThisProjects(_projects.filter((project) => project.year == termYear && project.상태 == "6.진행중"));
      setReports(res2.data);
      const thisReport = res2.data.filter(report =>
        (new Date(report.최종수정시간).getFullYear() == termYear && new Date(report.최종수정시간).getWeek(3) == termWeek)
        || (new Date(report.최종수정시간.substring(0,10)).getFullYear() == termYear && new Date(report.최종수정시간.substring(0,10)).getWeek(3) == termWeek));
      setThisReport(thisReport);
    })
    )
  }


  return (
    <>
      <Row>
        <Col>
          <Widget title={`${termYear}년 ${termWeek}주차`}>
            <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
              <div style={{display: 'flex'}}>
                <select className="form-control" value={termYear} name="termYear" onChange={onChangeTerm} style={{width: 'auto', marginRight: 1 + 'rem'}}>
                  {yearOptions.map(year => <option key={year} value={year}>{year}</option>)}
                </select>
                <select className="form-control" value={termWeek} name="termWeek" onChange={onChangeTerm} style={{width: 'auto'}}>
                  {weekNumOptions()}
                </select>
              </div>
              <UncontrolledButtonDropdown>
                <DropdownToggle color={"info"} style={{borderRadius: 0}} >
                미등록 프로젝트{' '}
                {thisProjects.length - thisReport.length}/{thisProjects.length}
                </DropdownToggle>
                <DropdownMenu>
                  {thisProjects
                  .filter(project => !thisReport.find(report => report.project_id == project.id))
                  .map((data, index) => {
                    return (
                      <DropdownItem key={index}>
                        {data.프로젝트명}
                      </DropdownItem>
                    );
                  })}
                </DropdownMenu>
              </UncontrolledButtonDropdown>
            </div>
            <Link to="/app/projects/reports/new" style={{float: 'right', marginTop: 1+'rem', marginBottom: 1+'rem'}}>
              <button className="btn btn-primary">주간 보고서 작성</button>
            </Link>
            <Table>
              <thead>
                <tr className="report-list__tr">
                  <th className="col-md-4 report-list__th">프로젝트</th>
                  <th className="col-md-2 report-list__th">고객사</th>
                  <th className="col-md-2 report-list__th">PM</th>
                  <th className="col-md-2 report-list__th">최종수정시간</th>
                  <th className="col-md-1 report-list__th">최종확인</th>
                </tr>
              </thead>
              <tbody>
                {reports
                .filter(report => (new Date(report.최종수정시간).getFullYear() == termYear && new Date(report.최종수정시간).getWeek(3) == termWeek) 
                || (new Date(report.최종수정시간.substring(0,10)).getFullYear() == termYear && new Date(report.최종수정시간.substring(0,10)).getWeek(3) == termWeek))
                .map(report => {
                  const _project = projects.find(project => project.id === report.project_id && project.year == termYear)
                  return (
                    <tr key={report.id}>
                      <td className="col-md-4">
                        {console.log("project: ", _project, "\nreport: ", report)}
                        <Link to={{pathname: `reports/${_project.id}/${report.id}/${_project.year}`}}>
                          {_project.프로젝트명}
                        </Link>
                      </td>
                      <td className="col-md-2 report-list__td">{_project ? _project.고객사: ""}</td>
                      <td className="col-md-2 report-list__td">{_project ? _project.PM : ""}</td>
                      <td className="col-md-2 report-list__td">{new Date(report.최종수정시간) == 'Invalid Date' ? report.최종수정시간 : new Date(report.최종수정시간).toLocaleString()}</td>
                      <td className="col-md-1 report-list__td" style={report.최종확인 == "1" ? {backgroundColor: 'orange'}:{}}></td>
                    </tr>
                )}
                )}
              </tbody>
            </Table>
          </Widget>
        </Col>
      </Row>
    </>
  );
}

export default ReportList;
