import React, { useState } from "react";
import  {useHistory} from 'react-router'; 
import PropTypes from "prop-types";
import { withRouter, Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Alert, Button, Label, Input, FormGroup } from "reactstrap";
import Widget from "../../components/Widget";
import { loginUser } from "../../actions/user";
import signinImg from "../../images/signinImg.svg";
import axios from "axios";
import bcrypt from 'bcryptjs';

const Login = (props) => {
  isAuthenticated = (token) => {
    if (token) return true;
  }

  const [userInfo, setUserInfo] = useState({
    id: "",
    password: ""
  });
  const [loginStatus, setLoginStatus] = useState("");
  const [checked, setChecked] = useState(false);

  const changeId = (event) => {
    setUserInfo({
      ...userInfo,
      id: event.target.value
    });
  }

  const changePassword = (event) => {
    setUserInfo({
      ...userInfo,
      password: event.target.value
    });
  }

  const doLogin = (e) => {
    e.preventDefault();// 새로고침 안됨

    axios.post(`http://${process.env.REACT_APP_API_URL}/login`, //DB연동
    {id: userInfo.id}).then((response)=>{
      if(response.data.length>0){
        bcrypt.compare(userInfo.password, response.data[0].password, (err, res) => {
          if(res){
            if (response.data[0].authority != 1 && response.data[0].authority != 2) {
              alert("접근 권한이 없습니다.");
              return;
            }
            localStorage.setItem("id", userInfo.id);
            localStorage.setItem('authenticated', true);
            if(response.data[0].authority == 1){
              alert("관리자 로그인 성공!! 오늘 하루도 파이팅 하세요!");
            }else{
              alert("로그인 성공!! 오늘 하루도 파이팅 하세요!");
            }
            props.history.push("/");
          }else{
            alert("비밀번호가 틀렸습니다.");
          }
        });
      }else{
        alert("아이디가 없습니다.");
      }
    });
  }
  
  const signUp = () => {
    props.history.push("/Dashboard");
  }

    const { from } = props.location.state || { from: { pathname: '/app' } }; // eslint-disable-line

    // cant access login page while logged in
    if (isAuthenticated(JSON.parse(localStorage.getItem('authenticated')))) {
      return (
          <Redirect to={from} />
      );
    }

    return (
     
      <div className="auth-page">
        <Widget
          className="widget-auth my-auto"
          title={
            <h3 className="mt-0 mb-2" style={{ fontSize: 40 }}>
              로그인
            </h3>
          }
        >
          <p className="widget-auth-info">
            Welcome Back!
          </p>
          <form className="mt" onSubmit={doLogin}>
            {props.errorMessage && (
              <Alert className="alert-sm" color="danger">
                {props.errorMessage}
              </Alert>
            )}
            <div className="form-group">
              <Label for="search-input1">ID</Label>
              <input
                className="form-control"
                onChange={changeId}
                required
                name="id"
                placeholder="ID를 입력하세요"
              />
            </div>
            <div className="form-group mb-2">
              <Label for="search-input1">비밀번호</Label>
              <input
                className="form-control"
                onChange={changePassword}
                type="password"
                required
                name="password"
                placeholder="비밀번호를 입력하세요"
              />
            </div>
            <Button
              type="submit"
              color="warning"
              className="auth-btn mb-3"
              size="sm"
              // onChange={changeAuthority}
              onClick={(e) => doLogin(e)}
            >
              {props.isFetching ? "Loading..." : "Login"}
            </Button>
            <div className={"d-flex align-items-center"}>
              계정이 없으신가요?{" "}
              <Link to="register" className={"ml-1"}>
                회원가입
              </Link>
              
            </div>
              
          

          </form>
        </Widget>
        <img src={signinImg} alt="signin" className={"backImg"} />
      </div>
    );
}

function mapStateToProps(state) {
  return {
    isFetching: state.auth.isFetching,
    isAuthenticated: state.auth.isAuthenticated,
    errorMessage: state.auth.errorMessage,
  };
}

export default withRouter(connect(mapStateToProps)(Login));
