import React, { PureComponent } from 'react';
import {
  Row,
  Col,
} from 'reactstrap';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official'
import variablePie from 'highcharts/modules/variable-pie';
import exporting from 'highcharts/modules/exporting';
import exportData from 'highcharts/modules/export-data';
import accessibility from 'highcharts/modules/accessibility';
import highcharts3d from 'highcharts/highcharts-3d';
import sunburst from 'highcharts/modules/sunburst';
import vector from 'highcharts/modules/vector';
import wordcloud from 'highcharts/modules/wordcloud';
// import gantt from 'highcharts/modules/gantt';
import highchartsGantt from "highcharts/modules/gantt";
import Widget from '../../../components/Widget';
import chartsData from './mock';

variablePie(Highcharts);
exporting(Highcharts);
exportData(Highcharts);
accessibility(Highcharts);
highcharts3d(Highcharts);
sunburst(Highcharts);
vector(Highcharts);
wordcloud(Highcharts);
// gantt(Highcharts);
highchartsGantt(Highcharts);

class HighChartsPage extends PureComponent {

  state = {
    cd: chartsData,
  }

  render() {

    const { cd } = this.state;

    return (
      <div>
        <h1 className="page-title">Visual - <span className="fw-semi-bold">Highcharts</span></h1>
        <p>For more information please read full <a href="https://github.com/highcharts/highcharts-vue">documentation</a></p>
        <Row>
          <Col lg={12} xs={12}>
            <Widget
              title={<h5>Highcharts <span className="fw-semi-bold">Line Chart</span></h5>}
              close collapse
            >
              <HighchartsReact
              highcharts={Highcharts}
              constructorType={"ganttChart"}
              options={{
                title: {
                  text: 'Gantt Chart with Navigation'
                },
                // yAxis: {
                //   uniqueNames: true
                // },
                navigator: {
                  enabled: true,
                  liveRedraw: true,
                  series: {
                    type: 'gantt',
                    pointPlacement: 0.5,
                    pointPadding: 0.25
                  },
                  yAxis: {
                    min: 0,
                    max: 3,
                    reversed: true,
                    categories: []
                  }
                },
                scrollbar: {
                  enabled: true
                },
                rangeSelector: {
                  enabled: true,
                  selected: 0
                },
                series: [{
                  name: 'Project 1',
                  data: [{
                      id: 's',
                      name: 'Start prototype',
                      start: Date.toLocaleString(2021, 10, 18),
                      // start: Date.UTC(2021, 10, 18),
                      end: Date.toLocaleString(2021, 10, 20)
                  }, {
                      id: 'b',
                      name: 'Develop',
                      start: Date.UTC(2021, 10, 20),
                      end: Date.UTC(2021, 10, 25),
                      dependency: 's'
                  }, {
                      id: 'a',
                      name: 'Run acceptance tests',
                      start: Date.UTC(2021, 10, 23),
                      end: Date.UTC(2021, 10, 26)
                  }, {
                      name: 'Test prototype',
                      start: Date.UTC(2021, 10, 27),
                      end: Date.UTC(2021, 10, 29),
                      dependency: ['a', 'b']
                  }]
                }, {
                  name: 'Project 2',
                  data: [{
                    id: 'ss',
                    name: '졸려',
                    // start: new Date(2021, 10, 18).getTime(),
                    // end: new Date(2021, 10, 20).getTime()
                  }, {
                    name: 'Requirements',
                    id: 'requirements',
                    parent: 'ss',
                    start: new Date(2021, 10, 19).getTime(),
                    end: new Date(2021, 10, 30).getTime()
                  }, {
                    name: 'playing',
                    id: 'playing',
                    parent: 'ss',
                    start: new Date(2021, 11, 1).getTime(),
                    end: new Date(2021, 11, 10).getTime()
                  }, {
                    name: '레이어',
                    start: new Date(2021, 10, 20),
                    end: new Date(2021, 10, 25),
                  }, {
                    name: '아이콘',
                    start: new Date(2021, 10, 23),
                    end: new Date(2021, 10, 26)
                  }, {
                    name: '애니메이션',
                    start: new Date(2021, 10, 27),
                    end: new Date(2021, 10, 29),
                  }]
                }]
              }} />
            </Widget>
          </Col>
          <Col lg={6} xs={12}>
            <Widget
              title={<h5>Highcharts <span className="fw-semi-bold">Pie Chart</span></h5>}
              close collapse
            >
              <HighchartsReact highcharts={Highcharts} options={cd.pie} />
            </Widget>
          </Col>
          <Col lg={6} xs={12}>
            <Widget
              title={<h5>Highcharts <span className="fw-semi-bold">Column 3D Chart</span></h5>}
              close collapse
            >
              <HighchartsReact highcharts={Highcharts} options={cd.column3D} />
            </Widget>
          </Col>
          <Col lg={5} xs={12}>
            <Row>
              <Col lg={12} xs={12}>
                <Widget
                  title={<h5>Highcharts <span className="fw-semi-bold">Vector Chart</span></h5>}
                  close collapse
                  >
                    <HighchartsReact highcharts={Highcharts} options={cd.vector} />
                  </Widget>
              </Col>
              <Col lg={12} xs={12}>
                <Widget
                  title={<h5>Highcharts <span className="fw-semi-bold">Sunburst Chart</span></h5>}
                  close collapse
                  >
                    <HighchartsReact highcharts={Highcharts} options={cd.wordCloud} />
                </Widget>
              </Col>
            </Row>
          </Col>
          <Col lg={7} xs={12}>
            <Widget
              title={<h5>Highcharts <span className="fw-semi-bold">Sunburst Chart</span></h5>}
              close collapse
              >
                <HighchartsReact highcharts={Highcharts} options={cd.sunburst} />
            </Widget>
          </Col>
        </Row>
      </div>
    );
  }
}

export default HighChartsPage;
