import React, { useState, useEffect } from 'react';
import {
    Table,
    Button,
    Input,
} from "reactstrap";
import moment from 'moment';
import axios from "axios";
// import { Cookies } from "react-cookie";
import { useCookies } from 'react-cookie';

// const cookies = new Cookies();

const Team = () => {
  const [cookies, setCookie] = useCookies(['token']);
  const [teams, setTeams] = useState([]);
  const [team, setTeam] = useState({
    teamname: '',
    firstorder: '',
    firstsale: '',
    lastorder: '',
    lastsale: '',
    num: '',
    year: '',
  });
  const [year, setYear] = useState(moment(new Date()).format('YYYY'));

  const onChangeInput = (e) => {
    setTeam({
      ...team,
      [e.target.id]: e.target.value,
    });
  };

  return (
    <>
      <div className="team">
        <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between', marginBottom: 0.5+'rem'}}>
          <p style={{ fontWeight: 700, fontSize: 1.5+'rem' }}>팀관리</p>
          <select className="form-control" style={{width: 8+'rem'}} id="year" value={year} onChange={(e) => {setYear(e.target.value); setTeam({...team, year: e.target.value})}}>
            {[...Array(moment(new Date()).format('YYYY') - 2018).keys()].map((i) => (
              <option key={i} value={moment(new Date()).format('YYYY') - i + 1}>{moment(new Date()).format('YYYY') - i + 1}</option>
            ))}
          </select>
        </div>
        <Table className="manage__table table-sm">
          <thead>
            <tr>
              <th>{" "}</th>
              <th>팀명<br /><br /><Input type="text" id="teamname" value={team.teamname} onChange={onChangeInput}/></th>
              <th>상반기 목표수주<br /><br /><Input type="text" id="firstorder" value={team.firstorder} onChange={onChangeInput}/></th>
              <th>상반기 목표매출<br /><br /><Input type="text" id="firstsale" value={team.firstsale} onChange={onChangeInput}/></th>
              <th>하반기 목표수주<br /><br /><Input type="text" id="lastorder" value={team.lastorder} onChange={onChangeInput}/></th>
              <th>하반기 목표매출<br /><br /><Input type="text" id="lastsale" value={team.lastsale} onChange={onChangeInput}/></th>
              {/* <th>Action<br /><br /><Button size="sm" onClick={()=>{this.state.onSave(this.state.num)}}>{this.state.num ? "변경" : "추가"}</Button></th> */}
            </tr>
          </thead>
          {/* <tbody>  
            {this.state.teams ? this.state.teams.map(c => {
              if(c.year == this.state.year){
                return( 
                <tr key={c.num}>
                  <td style={{ textAlign: "center" }}>{c.teamname}</td> 
                  <td style={{ textAlign: "center" }}>{c.firstorder}</td> 
                  <td style={{ textAlign: "center" }}>{c.firstsale}</td>
                  <td style={{ textAlign: "center" }}>{c.lastorder}</td>
                  <td style={{ textAlign: "center" }}>{c.lastsale}</td>
                  <td><Button color={"warning"} className="UpdateBtn" size="sm" variant="primary" onClick={()=>{this.state.onEdit(c.num)}}>수정</Button>&nbsp;&nbsp;
                    <Button color={"default"} className="UpdateBtn" size="sm" variant="danger"onClick={()=>{this.state.onDelete(c.num)}}>삭제</Button></td>
                </tr> 
                );
            }})  :<tr><td colSpan="7">데이터가 없습니다.</td></tr>}
          </tbody> */}
        </Table>
      </div>
    </>
  );
}


