import React, { useEffect, useState } from 'react';
import {
    Row,
    Col,
    Table,
    Progress,
    Button,
    UncontrolledButtonDropdown,
    DropdownMenu,
    DropdownToggle,
    DropdownItem,
    Alert,
    Input,
    Label,
    Badge
  } from "reactstrap";
import Widget from "../../components/Widget";
import axios from "axios";
import moment from 'moment';

const Member = () => {
  const [teams, setTeams] = useState([]);
  const [members, setMembers] = useState([]);
  const [member, setMember] = useState({
    num: "",
    id: "",
    password: "",
    name: "",
    team: "",
    division: "",
    ranks: "",
    position: "",
    authority: "",
  });

  const onChangeInput = (e) => {
    console.log(e.target)
    setMember({
      ...member,
      [e.target.id]: e.target.value,
    });
  };

  useEffect(() => findAllInfo(), []);

  const findAllInfo = () => {
    axios
      .all([
        axios.get(`http://${process.env.REACT_APP_API_URL}/members`),
        axios.get(`http://${process.env.REACT_APP_API_URL}/teams`),
      ])
      .then(axios.spread((res1, res2) => {
        setMembers(res1.data);
        setTeams(res2.data);
      }));
  };

  const onSave = () => {
    if (member.id && member.password && member.name && member.team && member.division && member.ranks && member.authority) {
      if (member.num) {
        axios
          .post(`http://${process.env.REACT_APP_API_URL}/members/update`, {
            member: member
          })
          .then((response) => {
            console.log(response);
          });
      } else {
        axios
          .post(`http://${process.env.REACT_APP_API_URL}/members/add`, {
            member: member
          })
          .then((response) => {
            console.log(response);
          });
        }
        window.location.reload();
    }
  };

  const onDelete = (num) => {
    axios
      .post(`http://${process.env.REACT_APP_API_URL}/members/delete`, {num})
      .then((response) => {
        console.log(response);
      });
    window.location.reload();
  };

  const onEdit = (data) => {
    setMember(data);
  };

  return (
    <>
      <div className="member">
        <p style={{ fontWeight: 700, fontSize: 1.5+'rem' }}>미래차검증전략실 직원 목록</p>
        <div style={{width:100+'%'}}>
          <Table className="manage__table table-sm">
            <thead>
              <tr>
                <th>ID<br /><br /><Input type="text" id="id" value={member.id} onChange={onChangeInput}/></th>
                {/* <th>Password<br /><br /><Input type="text" id="password" value={member.password} onChange={onChangeInput}/></th> */}
                <th>이름<br /><br /><Input type="text" id="name" value={member.name} onChange={onChangeInput}/></th>
                <th>실<br /><br /><Input type="text" id="division" value={member.division} onChange={onChangeInput}/></th>
                <th>팀<br /><br />
                  <Input type="select" id="team" value={member.team} onChange={onChangeInput}>
                    <option value=""> </option>
                    {teams ? 
                    teams
                    .filter(({year}) => year == moment(new Date()).format("YYYY"))
                    .map(team => {
                    return( 
                      <option value={team.teamname}>{team.teamname}</option>     
                        );
                    })  :""}
                  </Input>
                </th>
                <th>직급<br /><br /><Input type="text" id="ranks" value={member.ranks} onChange={onChangeInput}/></th>
                <th>직책<br /><br /><Input type="text" id="position" value={member.position} onChange={onChangeInput}/></th>
                <th>권한<br /><br /><Input type="text" id="authority" value={member.authority} onChange={onChangeInput}/></th>
                <th>Action<br /><br /><Button size="sm" color="info" onClick={onSave}>{member.num ? "변경" : "추가"}</Button></th>
              </tr>
            </thead>
            <tbody>
              {members ? members.map(c => {
                  return( <tr key={c.num}>
                    <td style={{ textAlign: "center" }}>{c.id}</td> 
                    {/* <td style={{ textAlign: "center" }}>{c.password}</td>  */}
                    <td style={{ textAlign: "center" }}>{c.name}</td>
                    <td style={{ textAlign: "center" }}>{c.division}</td>
                    <td style={{ textAlign: "center" }}>{c.team}</td>
                    <td style={{ textAlign: "center" }}>{c.ranks}</td>
                    <td style={{ textAlign: "center" }}>{c.position}</td>
                    <td style={{ textAlign: "center" }}>{c.authority}</td>
                    <td>
                    <Button color={"warning"} className="UpdateBtn" size="sm" variant="primary" onClick={()=>onEdit(c)}>수정</Button>
                    &nbsp;
                    <Button color={"default"} className="UpdateBtn" size="sm" variant="danger"onClick={() => onDelete(c.num)}>삭제</Button></td>
                  </tr>
                    );
                })  :""}
            </tbody>
          </Table>
        </div>
      </div>
    </>
  );
};

export default Member;
