import React from "react";
import Member from "../../components/Manage/Member";
import Team from "../../components/Manage/Team";
import Tool from "../../components/Manage/Tool";
import {
  Row,
  Col,
  Table,
  Head,
  Body,
  Progress,
  Button,
  UncontrolledButtonDropdown,
  DropdownMenu,
  DropdownToggle,
  DropdownItem,
  Input,
  Label,
  Badge
} from "reactstrap";
import { Sparklines, SparklinesBars } from "react-sparklines";

import Widget from "../../components/Widget/Widget";
import s from "./Manages.modules.scss";
import Axios from 'axios'
import axios from "axios";



class Manages extends React.Component {
  

  state = {
    members: "",
    teams: "",
    checkboxes1: [false, true, false, false],
    checkboxes2: [false, false, false, false, false, false],
    checkboxes3: [false, false, false, false, false, false],
  };

  constructor(props) {
    super(props);
    
    this.checkAll = this.checkAll.bind(this);

   

  }

  parseDate(date) {
    this.dateSet = date.toDateString().split(" ");

    return `${date.toLocaleString("en-us", { month: "long" })} ${
      this.dateSet[2]
    }, ${this.dateSet[3]}`;
  }

  checkAll(ev, checkbox) {
    const checkboxArr = new Array(this.state[checkbox].length).fill(
      ev.target.checked
    );
    this.setState({
      [checkbox]: checkboxArr
    });
  }

  changeCheck(ev, checkbox, id) {
    //eslint-disable-next-line
    this.state[checkbox][id] = ev.target.checked;
    if (!ev.target.checked) {
      //eslint-disable-next-line
      this.state[checkbox][0] = false;
    }
    this.setState({
      [checkbox]: this.state[checkbox]
    });
  }




  render() {
    const {classes} = this.props;

    

    return (
      <div className={s.root}>
        {/* 첫번째 테이블 */}      
        <Member/>
        {/* 두번째 테이블 */}
        <Team/>    
        {/* 세번째 테이블 */}
        <Tool/>
        
      </div>
    );
  }
}

export default Manages;
