import React, { useEffect, useState } from 'react';
import {
    Table,
    Button,
    Input,
    Modal,
    ModalHeader,
    ModalBody,
} from "reactstrap";
import axios from "axios";
import moment from 'moment';
import { useCookies } from 'react-cookie';

const Place = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [places, setPlaces] = useState([]);
  const [place, setPlace] = useState({
    num: '',
    name: '',
    color: '',
    year: '',
  });
  const [year, setYear] = useState(moment(new Date()).format('YYYY'));

  const onChangeInput = (e) => {
    setPlace({
      ...place,
      [e.target.id]: e.target.value,
    });
  };

  useEffect(() => findAllInfo(), []);

  const findAllInfo = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios.get(`http://${process.env.REACT_APP_API_URL}/places`)
    .then(res => setPlaces(res.data))
    .catch(err => {
      console.log(err);
    });
    //   if (cookies.token) {
    //     removeCookie('token');
    //   }
    //   alert("세션이 만료되었습니다. 다시 로그인 해주세요");
    //   window.location.href = "/";
    // });
  };

  // const onSave = () => {
  //   if (place.id && place.name && place.place && place.소속 && place.직급 && place.authority) {
  //     if (place.num) {
  //       axios
  //         .post(`http://${process.env.REACT_APP_API_URL}/members/update`, {
  //           place: place
  //         })
  //         .then((response) => {
  //           console.log(response);
  //         })
  //         .catch(err => {
  //           if (cookies.token) {
  //             removeCookie('token');
  //           }
  //           alert("세션이 만료되었습니다. 다시 로그인 해주세요");
  //           window.location.href = "/";
  //         });
  //     } else {
  //       axios
  //         .post(`http://${process.env.REACT_APP_API_URL}/members/add`, {
  //           place: place
  //         })
  //         .then((response) => {
  //           console.log(response);
  //         })
  //         .catch(err => {
  //           if (cookies.token) {
  //             removeCookie('token');
  //           }
  //           alert("세션이 만료되었습니다. 다시 로그인 해주세요");
  //           window.location.href = "/";
  //         });
  //       }
  //     window.location.reload();
  //   }
  //   else {
  //     alert("모든 항목을 입력해주세요.");
  //   }
  // };

  // const onDelete = (num) => {
  //   const confirm = window.confirm("삭제하시겠습니까?");
  //   if (confirm) {
  //     axios
  //       .post(`http://${process.env.REACT_APP_API_URL}/members/delete`, {num})
  //       .then((response) => {
  //         console.log(response);
  //       })
  //       .catch(err => {
  //         if (cookies.token) {
  //           removeCookie('token');
  //         }
  //         alert("세션이 만료되었습니다. 다시 로그인 해주세요");
  //         window.location.href = "/";
  //       });
  //     window.location.reload();
  //   }
  // };

  return (
    <>
      <div className="place">
        <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between', marginBottom: 0.5+'rem'}}>
          <p style={{ fontWeight: 700, fontSize: 1.5+'rem' }}>근무지 관리</p>
          <select className="form-control" style={{width: 8+'rem'}} id="year" value={year} onChange={(e) => {setYear(e.target.value); setPlace({...place, year: e.target.value})}}>
            {[...Array(moment(new Date()).format('YYYY') - 2018).keys()].map((i) => (
              <option key={i} value={moment(new Date()).format('YYYY') - i + 1}>{moment(new Date()).format('YYYY') - i + 1}</option>
            ))}
          </select>
        </div>
        <Table className="manage__table table-sm">
          <thead>
            <tr>
              <th className="col-md-1">순서</th>
              <th className="col-md-3">근무지<br /><br /><Input type="text" id="name" value={place.name} onChange={onChangeInput}/></th>
              <th className="col-md-2">색상<br /><br /><Input type="text" id="firstorder" value={place.color} onChange={onChangeInput}/></th>
              <th className="col-md-2">Action<br /><br /><Button size="sm" color="info" onClick={onSave}>{place.num ? "변경" : "추가"}</Button></th>
            </tr>
          </thead>
          <tbody>  
            {places ? places
            .filter((place) => place.year == year)
            .map((place, index) => {
              return( 
              <tr key={index}>
                <td style={{ textAlign: "center" }}>{place.num}</td> 
                <td style={{ textAlign: "center" }}>{place.name}</td>
                <td style={{ textAlign: "center" }}>{place.color}</td>
                <td>
                  <Button color={"warning"} className="UpdateBtn" size="sm" variant="primary" onClick={()=>setPlace(place)}>수정</Button>
                  &nbsp;
                  <Button color={"default"} className="UpdateBtn" size="sm" variant="danger"onClick={() => onDelete(place.name, place.year)}>삭제</Button></td>
              </tr> 
              );
            })  :<tr><td colSpan="7">데이터가 없습니다.</td></tr>}
          </tbody>
        </Table>
      </div>
    </>
  );
};

export default Place;
