import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
// import { formatDate } from "@fullcalendar/common";
import Widget from "../../components/Widget/Widget";
import interactionPlugin from '@fullcalendar/interaction';
import timeGridPlugin from '@fullcalendar/timegrid';
import {
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Input,
} from "reactstrap";
import moment from 'moment';
import axios from "axios";
import { useCookies } from 'react-cookie';

const Reservation = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [members, setMembers] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [modalMenu, setModalMenu] = useState("");
  const [modalData, setModalData] = useState({
    id: "",
    title: "",
    start: "",
    end: "",
    예약자: "",
    특이사항: "",
  });
  const [events, setEvents] = useState([
    // { title: '프로젝트1', start: '2021-12-01T15:30', end: '2021-12-01T18:30', id: '19', 예약자: '예약자1', 특이사항: '특이사항1' },
    // { title: '프로젝트2', start: '2021-12-02T15:30', end: '2021-12-02T18:30', id: '20', 예약자: '예약자2', 특이사항: '특이사항2' },
    // { title: '프로젝트3', start: '2021-12-03T15:30', end: '2021-12-03T18:30', id: '30', 예약자: '예약자3', 특이사항: '특이사항3' },
  ]);

  useEffect(() => findAllInfo(), []);

  // 전체 데이터 로드
  const findAllInfo = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/reservations`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/members`)
    ])
    .then(axios.spread((res1, res2) => {
      setEvents(res1.data);
      setMembers(res2.data);
      console.log(res1.data);
      console.log(res2.data);
    }))
    .catch(err => {
      console.log(err);
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
  }

  const onClickCreate = () => {
    if (modalData.title === "" || modalData.start === "" || modalData.end === "")
      return alert("모든 항목을 입력해주세요");
    axios.post(`http://${process.env.REACT_APP_API_URL}/reservations/add`, {modalData})
    .then(res => {
      console.log(res);
    })
    .catch(err => {
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
    setIsOpen(false);
  }

  return (
    <>
      <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>프로젝트룸</p>}>
        <FullCalendar plugins={[ dayGridPlugin, timeGridPlugin, interactionPlugin ]} initialView="timeGridWeek"
          timeZone="Asia/Seoul"
          locale="ko"
          nowIndicator={true}
          weekends={false}
          headerToolbar= {{
            left: 'prev,next today',
            center: 'title',
            right: 'dayGridMonth,timeGridWeek,timeGridDay'
          }}
          slotMinTime="06:00:00"
          slotMaxTime="22:00:00"
          events={events}
          allDaySlot={false}
          eventClick={(info) => {
            console.log(info.event._def);
            setModalMenu("updateEvent");
            setModalData({
              id: info.event._def.publicId,
              title:info.event._def.title,
              start: info.event._instance.range.start.toISOString().slice(0,-1),
              end: info.event._instance.range.end.toISOString().slice(0,-1),
              예약자: info.event._def.extendedProps.예약자,
              특이사항: info.event._def.extendedProps.특이사항,
            });
            setIsOpen(true);
          }}
          selectable={true}
          selectMirror={true}
          selectOverlap={false}
          select={(selectInfo) => {
            console.log(selectInfo);
            setModalMenu("createEvent");
            setModalData({
              title: "",
              start: selectInfo.startStr,
              end: selectInfo.endStr,
              예약자: localStorage.getItem("userInfo") ? JSON.parse(localStorage.getItem("userInfo")).id : "",
              특이사항: "",
            });
            setIsOpen(true);
          }}
          scrollTime="08:00:00"
        />
      </Widget>
      <Modal isOpen={isOpen} toggle={() => setIsOpen(!isOpen)}>
        <ModalHeader toggle={() => setIsOpen(!isOpen)}>
          {modalMenu === "createEvent" ? "새로운 일정 추가" : "일정 수정"}
        </ModalHeader>
        <ModalBody>
          {modalMenu === "updateEvent" && (
            <>
              <div>내용</div>
              <div>{modalData.title}</div>
              <div>시작시간 : {moment(new Date(modalData.start)).format("YYYY-MM-DD HH:mm")}</div>
              <div>종료시간 : {moment(new Date(modalData.end)).format("YYYY-MM-DD HH:mm")}</div>
              <div>예약자 : {modalData.예약자}</div>
              <div>특이사항</div>
              <div>{modalData.특이사항}</div>
              <div>ID : {modalData.id}</div>
            </>
          )}
          {modalMenu === "createEvent" && (
            <>
              <div>내용 : <Input type="text" value={modalData.title} onChange={(e) => setModalData({...modalData, title: e.target.value})} /></div>
              <div>시작시간 : {moment(new Date(modalData.start)).format("YYYY-MM-DD HH:mm")}</div>
              <div>종료시간 : {moment(new Date(modalData.end)).format("YYYY-MM-DD HH:mm")}</div>
              <div>특이사항 : <Input type="text" value={modalData.특이사항} onChange={(e) => setModalData({...modalData, 특이사항: e.target.value})} /></div>
            </>
          )}
        </ModalBody>
        <ModalFooter>
          {modalMenu === "createEvent" && (
            <>
              <button
                className="btn btn-warning"
                onClick={() => {
                  onClickCreate();
                  // setEvents([
                  //   ...events,
                  //   {
                  //     id: Math.random().toString(36).substr(2, 9),
                  //     title: modalData.title,
                  //     start: modalData.start,
                  //     end: modalData.end,
                  //     예약자: modalData.예약자,
                  //     특이사항: modalData.특이사항,
                  //   },
                  // ]);
                  // setIsOpen(false);
                }}
              >
                예약하기
              </button>
            </>
          )}
          <button className="btn btn-secondary" onClick={() => setIsOpen(!isOpen)}>닫기</button>
        </ModalFooter>
      </Modal>
    </>
  );
};

export default Reservation;
