import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Row, Table } from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import { Link } from 'react-router-dom';
import './ProjectList.scss';
import Button from 'reactstrap/lib/Button';
import moment from 'moment';

const {GoogleSpreadsheet} = require("google-spreadsheet");
const gs_creds = require("./vom-shchoi-1f999190023a.json"); // 키 생성 후 다운된 json파일을 지정합니다.
const doc = new GoogleSpreadsheet("12Y0F34Kt6MXPIjy_phTwgXO1_uD5ZqDU_UzucNBdSNI");

async function authGoogleSheet(){
	try{
		await doc.useServiceAccountAuth(gs_creds);
		await doc.loadInfo()
	}catch(err){
		console.log( "AUTH ERROR ", err)
	}
}

// id, 팀수주, 팀매출, 프로젝트코드, 프로젝트명, 상태, 실, 고객사, 고객부서, ManMonth, 프로젝트계약금액_백만, 상반기예상수주, 상반기수주, 상반기예상매출, 상반기매출, 연간예상수주, 연간수주, 연간예상매출, 연간매출, 장비비, 컨설팅비, 도구비, 착수, 종료, 고객담당자, 근무지, 업무, 도구, 과제성격, 사업지역, PM, 투입명단, year, 주간보고서, 실적보고
async function readFirstSheetRow(data){
  authGoogleSheet(); // 처음 시작할 때 문서 접속에 대한 인증을 처리하고 해당 문서를 로드합니다.
  try{
    const date = moment(new Date()).format('YYYY-MM-DD');
    await doc.loadInfo();
    const sheet = doc.sheetsByTitle[date];
    if (sheet){
      await sheet.delete();
    }
    const new_sheet = await doc.addSheet({title: date});
    await new_sheet.resize({ rowCount: 50, columnCount: 35 });
    await new_sheet.setHeaderRow(['id', '팀수주', '팀매출', '프로젝트코드', '프로젝트명', '상태', '실', '고객사', '고객부서', 'ManMonth', '프로젝트계약금액_백만', '상반기예상수주', '상반기수주', '상반기예상매출', '상반기매출', '연간예상수주', '연간수주', '연간예상매출', '연간매출', '장비비', '컨설팅비', '도구비', '착수', '종료', '고객담당자', '근무지', '업무', '도구', '과제성격', '사업지역', 'PM', '투입명단', 'year', '주간보고서', '실적보고']);
    await new_sheet.addRows(data);
  }catch(err){
    console.log( "READ ERROR ", err)
  }
}

const ProjectList = () => {
  const [projects , setProjects] = useState([]);
  const [year, setYear] = useState(moment(new Date()).format('YYYY'));
  const columns = [ "팀(수주)", "팀(매출)", "프로젝트 명", "연간 예상수주", "연간 수주", "연간 예상매출", "연간 매출", "컨설팅비", "PM"];
  
  const compareAscSu = (a, b) => a.팀수주 < b.팀수주 ? -1 : a.팀수주 > b.팀수주 ? 1 : 0;
  const compareDescSu = (a, b) => b.팀수주 < a.팀수주 ? -1 : b.팀수주 > a.팀수주 ? 1 : 0;
  const compareAscMa = (a, b) => a.팀매출 < b.팀매출 ? -1 : a.팀매출 > b.팀매출 ? 1 : 0;
  const compareDescMa = (a, b) => b.팀매출 < a.팀매출 ? -1 : b.팀매출 > a.팀매출 ? 1 : 0;

  const sortList = (e) => {
    let arrayCopy = [...projects];
    switch (e.target.name) {
      case "ascSu":
        arrayCopy.sort(compareAscSu);
        break;
      case "descSu":
        arrayCopy.sort(compareDescSu);
        break;
      case "ascMa":
        arrayCopy.sort(compareAscMa);
        break;
      case "descMa":
        arrayCopy.sort(compareDescMa);
        break;
      default:
        return;
    }
    setProjects(arrayCopy);
  };

  useEffect(()=> findAllProjects(), []);

  const findAllProjects = () => {
    axios.get(`http://${process.env.REACT_APP_API_URL}/projects`)
    .then((res)=>{
           setProjects(res.data);
        })
 }

  return (
    <>
      <Row>
        <Col>
          <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>프로젝트목록</p>}>
            <select name="year" value={year} onChange={(e)=>setYear(e.target.value)}>
              {[...Array(moment(new Date()).format('YYYY') - 2019).keys()]
              .map((i)=>{
                return <option key={i} value={moment(new Date()).format('YYYY') - i}>{moment(new Date()).format('YYYY') - i}</option>
              })}
            </select>
            <Button color="warning" onClick={() => readFirstSheetRow(projects)}>스프레드시트 동기화</Button>
            <Link to="/app/projects/new"><button className="btn btn-primary">새로운 프로젝트</button></Link>
            <Table>
              <thead>
                <tr>
                  <th className="col-md-1 project-list__th">{columns[0]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascSu" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descSu" onClick={sortList}/></th>
                  <th className="col-md-1 project-list__th">{columns[1]}<button className="orderBtn glyphicon glyphicon-collapse" name="ascMa" onClick={sortList}/><button className="orderBtn glyphicon glyphicon-collapse-top" name="descMa" onClick={sortList}/></th>
                  <th className="col-md-3 project-list__th">{columns[2]}</th>
                  <th className="col-md-1 project-list__th">{columns[3]}</th>
                  <th className="col-md-1 project-list__th">{columns[4]}</th>
                  <th className="col-md-1 project-list__th">{columns[5]}</th>
                  <th className="col-md-1 project-list__th">{columns[6]}</th>
                  <th className="col-md-1 project-list__th">{columns[7]}</th>
                  <th className="col-md-1 project-list__th">{columns[8]}</th>
                </tr>
              </thead>
              <tbody>
                {projects
                .filter((project)=> project.year == year)
                .map((project, index) => {
                  return(
                  <tr key={index}>
                    <td className="col-md-1 project-list__td">{project.팀수주}</td>
                    <td className="col-md-1 project-list__td">{project.팀매출}</td>
                    <td className="col-md-3 project-list__td" style={{whiteSpace: 'break-spaces'}}>
                      <Link to={{pathname: `/app/projects/detail/${project.id}`}}>
                        {project.프로젝트명}
                      </Link>
                    </td>
                    <td className="col-md-1 project-list__td">{project.연간예상수주}</td>
                    <td className="col-md-1 project-list__td">{project.연간수주}</td>
                    <td className="col-md-1 project-list__td">{project.연간예상매출}</td>
                    <td className="col-md-1 project-list__td">{project.연간매출}</td>
                    <td className="col-md-1 project-list__td">{project.컨설팅비}</td>
                    <td className="col-md-1 project-list__td">{project.PM}</td>
                  </tr>
                )})}
              </tbody>
            </Table>
          </Widget>
        </Col>
      </Row>
    </>
  );
}

export default ProjectList;
