import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Switch, Route, withRouter, Redirect } from "react-router";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import Hammer from "rc-hammerjs";
import { UserContext } from '../../context/user';
import axios from "axios";

import Dashboard from "../../pages/dashboard";
import Header from "../Header";
import Sidebar from "../Sidebar";
import {
  openSidebar,
  closeSidebar,
  toggleSidebar,
} from "../../actions/navigation";
import s from "./Layout.module.scss";

// pages
import Typography from "../../pages/typography";
import Maps from "../../pages/maps";
import Notifications from "../../pages/notifications/Notifications";
import Icons from "../../pages/icons";
import Tables from "../../pages/tables";
import Manages from "../../pages/management";
import Charts from "../../pages/charts";
import Equips from "../../pages/equipment";
import orderssales from "../../pages/orderssales";
import wbs from "../../pages/project/wbs";

import Detail from "../../pages/detail/Detail"
import ProjectList from "../../pages/projectList/ProjectList"
import NewProject from "../../pages/newProject/NewProject";
import WBSP from "../../pages/project/wbsP";
import meeting from "../../pages/meeting/meeting";
import DMeeting from "../../pages/meeting/Dmeeting";
import NMeeting from "../../pages/meeting/Nmeeting";
import ReportList from "../../pages/reportList/ReportList";
import NewReport from "../../pages/newReport/NewReport";
import DetailReport from "../../pages/detailReport/DetailReport";
import ManagerSchedule from "../../pages/managerSchedule/ManagerSchedule";
import NewScheduleWeek from "../../pages/newScheduleWeek/NewScheduleWeek";
import DetailSchedule from "../../pages/detailSchedule/DetailSchedule";
import NewSchedule from "../../pages/newSchedule/NewSchedule";

// 교육관리
import Education from "../../pages/education/Education";
import EducationDetail from "../Education/EducationDetail.js"

// 조직도
import organizationCar from "../../pages/organizationCar"
import organizationA from "../../pages/OrganizationA"
import organizationB from "../../pages/organizationB"
import organizationD from "../../pages/organizationD"
import organizationE from "../../pages/organizationE"
import organizationF from "../../pages/organizationF"
import organizationG from "../../pages/organizationG"
import organizationH from "../../pages/organizationH"
import organizationI from "../../pages/organizationI"
import organizationJ from "../../pages/organizationJ"
import Organization_chart from "../../pages/organization_chart"

class Layout extends React.Component {
  static contextType = UserContext;
  static propTypes = {
    sidebarStatic: PropTypes.bool,
    sidebarOpened: PropTypes.bool,
    dispatch: PropTypes.func.isRequired,
  };

  static defaultProps = {
    sidebarStatic: true,
    sidebarOpened: true,
  };

  constructor(props) {
    super(props);

    this.handleSwipe = this.handleSwipe.bind(this);
    this.handleCloseSidebar = this.handleCloseSidebar.bind(this);
  }

  componentDidMount() {
    if (localStorage.getItem('id') 
    && localStorage.getItem('authenticated')
    && localStorage.getItem('id') != this.context.userInfo.id
    ) {
      alert("불러옵니다");
      axios.post(`http://${process.env.REACT_APP_API_URL}/login`,
      {id: localStorage.getItem('id')}).then((response)=>{
        this.context.setUserInfo({
          num: response.data[0].num,
          id: response.data[0].id,
          division: response.data[0].division,
          team: response.data[0].team,
          name: response.data[0].name,
          ranks: response.data[0].ranks,
          position: response.data[0].position,
          authority: response.data[0].authority,
        });
      })
      .catch(err => {
        console.log(err);
      });
    }
    this.handleResize();
    window.addEventListener("resize", this.handleResize.bind(this));
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.handleResize.bind(this));
  }

  handleResize() {
    if (window.innerWidth <= 768) {
      this.props.dispatch(toggleSidebar());
    } else if (window.innerWidth >= 768) {
      this.props.dispatch(openSidebar());
    }
  }

  handleCloseSidebar(e) {
    if (e.target.closest("#sidebar-drawer") == null && this.props.sidebarOpened && window.innerWidth <= 768) {
      this.props.dispatch(toggleSidebar());
    }
  }

  handleSwipe(e) {
    if ("ontouchstart" in window) {
      if (e.direction === 4) {
        this.props.dispatch(openSidebar());
        return;
      }

      if (e.direction === 2 && this.props.sidebarOpened) {
        this.props.dispatch(closeSidebar());
        return;
      }
    }
  }

  render() {
    return (
      <div
        className={[
          s.root,
          !this.props.sidebarOpened ? s.sidebarClose : "",
          "flatlogic-one",
          "dashboard-light",
        ].join(" ")}
        onClick={e => this.handleCloseSidebar(e)}
      >
        <Sidebar />
        <div className={s.wrap}>
          <Header />

          <Hammer onSwipe={this.handleSwipe}>
            <main className={s.content}>
              <TransitionGroup>
                <CSSTransition
                  key={this.props.location.key}
                  classNames="fade"
                  timeout={200}
                >
                  <Switch>
                    {/* 메인 페이지 -> 대시보드로 리다이렉트 */}
                    <Route
                      path="/app/main"
                      exact
                      render={() => <Redirect to="/app/schedule" />}
                    />
                    {/* 대시보드 페이지 -> 메인 */}
                    <Route
                      path="/app/main/dashboard"
                      exact
                      component={Dashboard}
                    />

                    <Route path={"/app/projects/wbsP"} component={WBSP}/>
