import React, {useState, useEffect} from "react";
import axios from "axios";
import { useCookies } from "react-cookie";
import './Organization.scss'
import Widget from "../../components/Widget/Widget";

const ranks = ['수석(부장)', '책임', '선임', '전임']

const Organization = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [teams, setTeams] = useState([]);
  const [members, setMembers] = useState([]);

  useEffect(() => findAllInfo(), []);

  const findAllInfo = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios
    .all([
      axios.get(`http://${process.env.REACT_APP_API_URL}/members`),
      axios.get(`http://${process.env.REACT_APP_API_URL}/teams`),
    ])
    .then(axios.spread((res1, res2) => {
      setMembers(res1.data);
      setTeams(res2.data.filter(team => team.year === new Date().getFullYear()).sort((a, b) => a.num - b.num));
    }))
    .catch(err => {
      console.log(err);
      // if (cookies.token) {
      //   removeCookie('token');
      // }
      // alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      // window.location.href = "/";
    });
  };

  const getHead = () => {
    if (members.length > 0) {
      const head = members.find(member => member.직책 == '실장');
      return (head.name + " " + head.직책);
    }
  };

  const getTeamMembers = (team) => {
    const _members = members.filter(member => member.team === team && member.직급 !== '인턴')
    .sort((a, b) => a.name.localeCompare(b.name))
    .sort((a, b) => ranks.indexOf(a.직급) - ranks.indexOf(b.직급));
    let data = [];
    _members.forEach(member => {
      if (member.직책 == '팀장') {
        data.unshift(<div className="organization__team_head">{member.name} {member.직책}</div>);
      }
      else {
        data.push(
          <div key={member.id}>
            <div className={"organization__team_"+ranks.indexOf(member.직급)}>{member.name} {member.직급}</div>
          </div>
        );
      }
    });
    data.push(<div className="organization__team_name">{_members.length}</div>);
    return data;
  };

  return (
    <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>조직도</p>}>
      <div className="organization__wrapper">
        <div className="organization__title">
          <div className="organization__title_main">미래차검증전략실</div>
          <div className="organization__title_sub">{getHead()}</div>
        </div>
        <div className="organization__calculate">
          {"숫자 계산 예정"}
        </div>
        <table className="organization__teams">
          <tbody>
            {teams
            .filter(({teamname}) => teamname.charAt(teamname.length - 1) == '팀')
            .map(team => (
              <td key={team.num} className="organization__team">
                <div className="organization__team_name">{team.teamname}</div>
                {getTeamMembers(team.teamname)}
              </td>
            ))}
          </tbody>
        </table>
        <div className="organization__interns">
          <div className="organization__interns_title">인턴</div>
          <div className="organization__interns_list">
          {members.filter(member => member.직급 === '인턴').map(member => (
            <div key={member.id}>
              <div className="organization__interns_0">{member.name}</div>
            </div>
          ))}
          </div>
        </div>
      </div>
    </Widget>
  );
}

export default Organization;
