import React, { useState, useEffect } from 'react';
import { Table, Button } from 'reactstrap';
import Flatpickr from "react-flatpickr";
import Widget from '../../components/Widget/Widget';
import moment from 'moment';
import axios from 'axios';
import {place} from '../newScheduleWeek/NewScheduleWeek';

const DetailSchedule = (props) => {
  const id = props.match.params.id;
  const [schedule, setSchedule] = useState({
    이름: '',
    날짜: '',
    오전: '',
    오후: '',
  });

  const onClickUpdate = () => {
    axios
    .post('/schedule/update', {오전: schedule.오전, 오후: schedule.오후, id})
    .then(res => {
    })
    window.location.reload();
  }

  const onClickDelete = () => {
    axios
    .post('/schedule/delete', {id})
    .then(res => {
    })
    window.location.href = "#/app/schedule";
    window.location.reload();
  }

  useEffect(()=> findAllInfos(), []);

  const findAllInfos = () => {
    axios
    .get(`http://${process.env.REACT_APP_API_URL}/schedule/search?id=${props.match.params.id}`)
    .then(res=> {
      if (res.data.length > 0) {
        setSchedule(res.data[0]);
      }
    })
  }

  return (
    <>
      <Widget>
        <Table>
          <tr>
            <td>날짜</td>
            <td>
              <Flatpickr
                className="form-control"
                value={schedule.날짜}
                onChange={(date) => setSchedule({...schedule, 날짜: moment(new Date(date)).format("YYYY-MM-DD")})}
                options={
                  {dateFormat: "Y-m-d"}
                }
              />
            </td>
          </tr>
          <tr>
            <td>오전</td>
            <td>
              <select className="form-control" value={schedule.오전} onChange={(e) => setSchedule({...schedule, 오전: e.target.value})}>
                {place.map((place, index) => (
                  <option key={index} value={place}>{place}</option>
                ))}
              </select>
            </td>
          </tr>
          <tr>
            <td>오후</td>
            <td>
              <select className="form-control" value={schedule.오후} onChange={(e) => setSchedule({...schedule, 오후: e.target.value})}>
                {place.map((place, index) => (
                  <option key={index} value={place}>{place}</option>
                ))}
              </select>
            </td>
          </tr>
        </Table>
        <Button color="primary" onClick={onClickUpdate}>수정</Button>
        <Button color="primary" onClick={onClickDelete}>삭제</Button>
      </Widget>
    </>
  );
}

export default DetailSchedule;
