import React from "react";
import  {useHistory} from 'react-router'; 
import PropTypes from "prop-types";
import { withRouter, Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Alert, Button, Label, Input, FormGroup } from "reactstrap";
import Widget from "../../components/Widget";
import { loginUser } from "../../actions/user";
import signinImg from "../../images/signinImg.svg";
import axios from "axios";
import bcrypt from 'bcryptjs';

class Login extends React.Component {
  static propTypes = {
    dispatch: PropTypes.func.isRequired,
  };


  static isAuthenticated(token) {
    if (token) return true;
  }

  constructor(props) {
    super(props);

    this.state = {
      id: "",
      password: "",
      authority: "",
      author: "",
      loginStatus: "",
      checked: false,
    };

    this.doLogin = this.doLogin.bind(this);
    this.changeId = this.changeId.bind(this);
    this.changePassword = this.changePassword.bind(this);
    this.signUp = this.signUp.bind(this);
  }


  changeId(event) {
    this.setState({ id: event.target.value });
  }

  changePassword(event) {
    this.setState({ password: event.target.value });
  }

 



  doLogin(e) {
    e.preventDefault();// 새로고침 안됨

    axios.post(`http://${process.env.REACT_APP_API_URL}/login`, //DB연동
    {
      id: this.state.id, password: this.state.password 
    }).then((response)=>{
      if(response.data.length>0){
        bcrypt.compare(this.state.password, response.data[0].password, (err, res) => {
          if(res){
            if (response.data[0].authority != 1 && response.data[0].authority != 2) {
              alert("접근 권한이 없습니다.");
              return;
            }
            localStorage.setItem("id", this.state.id);
            localStorage.setItem('authenticated', true);
            if(response.data[0].authority == 1){
              alert("관리자 로그인 성공!! 오늘 하루도 파이팅 하세요!");
            }else{
              alert("로그인 성공!! 오늘 하루도 파이팅 하세요!");
            }
            this.props.history.push("/");
          }else{
            alert("비밀번호가 틀렸습니다.");
          }
        });
      }else{
        alert("아이디가 없습니다.");
      }
    });
  }
  
  signUp() {
    this.props.history.push("/Dashboard");
  }

  render() {
    const { from } = this.props.location.state || { from: { pathname: '/app' } }; // eslint-disable-line

    // cant access login page while logged in
    if (Login.isAuthenticated(JSON.parse(localStorage.getItem('authenticated')))) {
      return (
          <Redirect to={from} />
      );
    }

    return (
     
      <div className="auth-page">
        <Widget
          className="widget-auth my-auto"
          title={
            <h3 className="mt-0 mb-2" style={{ fontSize: 40 }}>
              로그인
            </h3>
          }
        >
          <p className="widget-auth-info">
            Welcome Back! Please login to your account
          </p>
          <form className="mt" onSubmit={this.doLogin}>
            {this.props.errorMessage && (
              <Alert className="alert-sm" color="danger">
                {this.props.errorMessage}
              </Alert>
            )}
            <div className="form-group">
              <Label for="search-input1">ID</Label>
              <input
                className="form-control"
                onChange={this.changeId}
                required
                name="id"
                placeholder="Enter your username"
              />
            </div>
            <div className="form-group mb-2">
              <Label for="search-input1">Password</Label>
              <input
                className="form-control"
                onChange={this.changePassword}
                type="password"
                required
                name="password"
                placeholder="Enter your password"
              />
            </div>
           
            

            <Button
              type="submit"
              color="warning"
              className="auth-btn mb-3"
              size="sm"
              onChange={this.changeAuthority}
              onClick={(e) => this.doLogin(e)}
            >
              {this.props.isFetching ? "Loading..." : "Login"}
            </Button>
            

     

            <p className="widget-auth-info text-center">Or</p>
              
            

            <div className={"d-flex align-items-center"}>
              회원이 없으신가요?{" "}
              <Link to="register" className={"ml-1"}>
                회원가입
              </Link>
              
            </div>
              
          

          </form>
        </Widget>
        <img src={signinImg} alt="signin" className={"backImg"} />
        
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    isFetching: state.auth.isFetching,
    isAuthenticated: state.auth.isAuthenticated,
    errorMessage: state.auth.errorMessage,
  };
}

export default withRouter(connect(mapStateToProps)(Login));
