import React, {useState, useContext, useEffect} from "react";
import Member from "../../components/Manage/Member";
import Team from "../../components/Manage/Team";
import Tool from "../../components/Manage/Tool";
import {
  Row,
  Col,
  Table,
  Head,
  Body,
  Progress,
  Button,
  UncontrolledButtonDropdown,
  DropdownMenu,
  DropdownToggle,
  DropdownItem,
  Input,
  Label,
  Badge
} from "reactstrap";
import s from "./Manages.modules.scss";
import Widget from "../../components/Widget/Widget";
import { UserContext } from '../../context/user';
import axios from "axios";

const Manages = () => {
  const [menu, setMenu] = useState("member");
  const { userInfo, setUserInfo } = useContext(UserContext);
  

  // useEffect(() => {
  //   console.log(userInfo);
  //   // if (localStorage.getItem('id') && localStorage.getItem('authenticated')) {
  //   //   alert("또?");
  //   //   axios.post(`http://${process.env.REACT_APP_API_URL}/login`,
  //   //   {id: localStorage.getItem('id')}).then((response)=>{
  //   //     setUserInfo({
  //   //       num: response.data[0].num,
  //   //       id: response.data[0].id,
  //   //       division: response.data[0].division,
  //   //       team: response.data[0].team,
  //   //       name: response.data[0].name,
  //   //       ranks: response.data[0].ranks,
  //   //       position: response.data[0].position,
  //   //       authority: response.data[0].authority,
  //   //     });
  //   //   })
  //   //   .catch(err => {
  //   //     console.log(err);
  //   //   });
  //   // }
  // }, []);

  return (
    <>
      <Widget title={
        <div style={{display: 'flex', flexDirection:'row', width: 100+'%', justifyContent: 'space-between'}}>
          <p style={{ fontSize:30, fontWeight: 700 }}>관리자페이지</p>
          <div>
            <Button color="danger" size="sm" className={menu === "member" ? s.active : ""} onClick={() => {setMenu("member"); console.log(userInfo)}}>
              회원관리
            </Button>
            <Button color="danger" size="sm" className={menu === "team" ? s.active : ""} onClick={() => setMenu("team")}>
              팀관리
            </Button>
            <Button color="danger" size="sm" className={menu === "tool" ? s.active : ""} onClick={() => setMenu("tool")}>
              도구관리
            </Button>
          </div>
        </div>
      }>
        <Row>
          <Col md="12">
            {menu === "member" && <Member />}
            {menu === "team" && <Team />}
            {menu === "tool" && <Tool />}
          </Col>
        </Row>
      </Widget>
    </>
  );
}

export default Manages;
