import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Col, Row, Table } from 'reactstrap';
import Widget from '../../components/Widget/Widget';
import { Link } from 'react-router-dom';
import moment from 'moment';
import { useCookies } from 'react-cookie';

const ProjectList = () => {
  const [cookies, removeCookie ] = useCookies(['token']);
  const [projects , setProjects] = useState([]);
  const columns = [ "구분" , "항목" , "TOTAL" ];
  const [saveDate , setSaveDate] = useState();
  const [teams , setTeams] = useState([]);
  let sumgfo = 0; let sumgfs=0; let sumglo=0; let sumgls=0; //상하반기 목표수주 목표매출 
  let exfo = 0; let sumexfo = 0; // 상반기예상수주
  let fo=0; let sumfo = 0; // 상반기수주
  let exfs = 0; let sumexfs = 0; // 상반기예상매출
  let fs=0; let sumfs = 0; // 상반기매출
  let exlo = 0; let sumexlo = 0; // 하반기예상수주
  let lo=0; let sumlo = 0; // 하반기수주
  let exls = 0; let sumexls = 0; // 하반기예상매출
  let ls=0; let sumls = 0; // 하반기매출

  useEffect(()=> findAllorders(), []);

  const findAllorders = () => {
    axios.defaults.headers.common['Authorization'] = cookies.token;
    axios
    .all([axios.get(`http://${process.env.REACT_APP_API_URL}/projects`),axios.get(`http://${process.env.REACT_APP_API_URL}/teams`) ])
    .then(axios.spread((res1,res2)=>{
      setProjects(res1.data);
      setTeams(res2.data);
      })
    )
    .catch(err => {
      if (cookies.token) {
        removeCookie('token');
      }
      alert("세션이 만료되었습니다. 다시 로그인 해주세요");
      window.location.href = "/";
    });
    setSaveDate(moment(new Date()).format("YYYY"));
  }

  const onChangeYear = (e) => {
    setSaveDate(e.target.value);
  };

  return (
    <>
      <Row>
        <Col>
          <Widget title={<p style={{ fontSize:30, fontWeight: 700 }}>수주매출</p>}>
          <select className="form-control"  id="Year" value={saveDate} onChange={onChangeYear}>
            <option key={2020} value="2020" >2020</option>
            <option key={2021} value="2021" >2021</option>
            <option key={2022} value="2022">2022</option>
            <option key={2023} value="2023">2023</option>
          </select>
          <br />
            <Table striped hover>
              <thead>
                <tr>
                  <th colSpan='8'>상세내역단위 (백만)</th>
                </tr>
                <tr>
                  <th>{columns[0]}</th>
                  <th>{columns[1]}</th>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                      return(
                        <th key={idx}>{team.teamname}</th> 
                  )}})}
                  <th>{columns[2]}</th>
                </tr>
              </thead>
              <tbody>
                {/* 상반기 부분 */}
                <tr>
                  <td rowSpan='6' style={{ color: "orange" , fontWeight:"bold", textAlign: "center" }} > 상반기 </td>
                  <td>목표수주</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                    sumgfo+= team.firstorder;
                  
                    return(
                      <td key={idx}>{team.firstorder}</td> 
                  )}})}
                  <th>{sumgfo.toFixed(2)}</th>
                </tr>
                <tr>
                  <td>예상수주</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                      {projects.map((project) => {
                        if(team.teamname == project.팀수주 && saveDate==moment(project.착수).format("YYYY")){
                          exfo+=project.상반기예상수주;
                      }  
                    })}
                    const result= exfo;
                    sumexfo += exfo;
                    exfo=0;
                    return(
                      <td key={idx}>{result.toFixed(2)}</td>
                    )
                  }})}
                  <th>{sumexfo.toFixed(2)}</th>
                </tr>
                <tr>
                  <td>수주달성</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                      {projects.map((project) => {
                        if(team.teamname == project.팀수주 && saveDate==moment(project.착수).format("YYYY")){
                          fo+=project.상반기수주;
                        }  
                      }
                    )}
                    const result = fo;
                    sumfo += fo;
                    fo=0;
                    return(
                      <td key={idx}>{result.toFixed(2)}</td>
                    )
                  }})}
                  <th>{sumfo.toFixed(2)}</th>
                </tr>
                <tr>
                  <td>목표매출</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                      sumgfs+=team.firstsale;
                  
                    return(
                      <td key={idx}>{team.firstsale}</td> 
                    )}})}
                  <th>{sumgfs.toFixed(2)}</th>
                </tr>
                <tr>
                  <td>예상매출</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                    {projects.map((project) => {
                      if(team.teamname == project.팀매출 && saveDate==project.year){
                        exfs+=project.상반기예상매출;
                      }  
                    })}
                    const result= exfs;
                    sumexfs += exfs;
                    exfs=0;
                    return(
                      <td key={idx}>{result.toFixed(2)}</td>
                    )
                  }})}
                  <th>{sumexfs.toFixed(2)}</th>
                </tr>
                <tr>
                  <td style={{ color: "red" , fontWeight:"bold" }}>매출달성</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                    {projects.map((project) => {
                      if(team.teamname == project.팀매출 && saveDate==project.year){
                        fs+=project.상반기매출;
                      }  
                    })}
                    const result = fs;
                    sumfs += fs;
                    fs=0;
                    return(
                      <td key={idx} style={{ color: "red" , fontWeight:"bold" }}>{result.toFixed(2)}</td>
                    )
                  }})}
                  <th style={{ color: "red" , fontWeight:"bold" }}>{sumfs.toFixed(2)}</th>
                </tr>

                {/* 하반기 부분 */}
                <tr>
                  <td rowSpan='6' style={{ color: "orange" , fontWeight:"bold", textAlign: "center" }}> 하반기 </td>
                  <td>목표수주</td>
                  {teams.map((team, idx) => {
                  if(team.year == saveDate){
                  sumglo += team.lastorder;
                  sumglo += sumgfo;
                  sumgfo =0;
                  
                  return(
                    
                   <td key={idx}>{(team.lastorder + team.firstorder).toFixed(2)}</td> 
                )}})}
                  <th>{sumglo.toFixed(2)}</th>
                </tr>
                <tr>
                  <td>예상수주</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                    {projects.map((project) => {
                      if(team.teamname == project.팀수주 && saveDate==project.year){
                        exlo+=project.하반기예상수주;
                      }  
                    })}
                    const result= exlo + exfo;
                    sumexlo += exlo;
                    exlo=0;
                    sumexlo += sumexfo;
                    sumexfo =0;
                    return(
                      <td key={idx}>{result.toFixed(2)}</td>
                    )
                  }})}
                  <th>{sumexlo.toFixed(2)}</th>
                </tr>
                <tr>
                  <td>수주달성</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                    {projects.map((project) => {
                      if(team.teamname == project.팀수주 && saveDate==project.year){
                        lo+=project.하반기수주;
                      }  
                    })}
                    const result = lo + fo;
                    sumlo += lo;
                    lo=0;
                    sumlo += sumfo;
                    sumfo = 0;
                    return(
                      <td key={idx}>{result.toFixed(2)}</td>
                    )
                  }})}
                  <th>{sumlo.toFixed(2)}</th>
                </tr>
                <tr>
                  <td>목표매출</td>
                  {teams.map((team, idx) => {
                  if(team.year == saveDate){
                  sumgls+=team.lastsale;
                  sumgls+=team.firstsale;
                  
                  return(
                   <td key={idx}>{(team.lastsale + team.firstsale).toFixed(2)}</td> 
                )}})}
                  <th>{sumgls.toFixed(2)}</th>
                </tr>
                <tr>
                  <td>예상매출</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                    {projects.map((project) => {
                      if(team.teamname == project.팀매출 && saveDate==project.year){
                        exls+=project.하반기예상매출;
                      }  
                    })}
                    const result= exls + exfs;
                    sumexls += exls;
                    exls=0;
                    sumexls += sumexfs;
                    sumexfs = 0;
                    return(
                      <td key={idx}>{result.toFixed(2)}</td>
                    )
                  }})}
                  <th>{sumexls.toFixed(2)}</th>
                </tr>
                <tr>
                  <td style={{ color: "red" , fontWeight:"bold" }}>매출달성</td>
                  {teams.map((team, idx) => {
                    if(team.year == saveDate){
                    {projects.map((project) => {
                      if(team.teamname == project.팀매출 && saveDate==project.year){
                        ls+=project.하반기매출;
                        ls+=project.상반기매출;
                      }  
                    })}
                    const result = ls;
                    sumls += ls;
                    ls=0;
                    return(
                      <td key={idx} style={{ color: "red" , fontWeight:"bold" }}>{result.toFixed(2)}</td>
                    )
                  }})}
                  <th style={{ color: "red" , fontWeight:"bold" }}>{sumls.toFixed(2)}</th>
                </tr>
              </tbody>
            </Table>
          </Widget>
        </Col>
      </Row>
    </>
  );
}

export default ProjectList;
